mapper.select:
SELECT MTIM.COMPANY_CD , MTIM.DIVISION_CD , MTIM.PUR_SAL_TYPE , MTIM.CLOSE_YEAR_MONTH , MP.PARTNER_CD , MP.PARTNER_NM , TO_CHAR(MTIM.INVOICE_DATE ,'YYYY-MM-DD') AS INVOICE_DATE , MTIM.TAX_TYPE , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MTIM.COMPANY_CD, MTIM.DIVISION_CD, 'SM0029', MTIM.TAX_TYPE, #{langCd, jdbcType=VARCHAR}) FROM DUAL) AS TAX_TYPE_NM , MTIM.TAX_RATE , MTIM.TAX_REMARK , MTIM.DESCRIPTION , (SELECT MOM_CLOSED_PKG.FN_GET_CLOSED_LOCALAMOUNT(MTIM.COMPANY_CD, MTIM.DIVISION_CD, MTIM.PUR_SAL_TYPE, MTIM.CLOSE_YEAR_MONTH, MTIM.VENDOR_CD, MTIM.INVOICE_DATE, MTIM.TAX_TYPE) FROM DUAL) AS CLOSED_AMOUNT --"마감금액" , (SELECT MOM_CLOSED_PKG.FN_GET_CLOSED_TAXAMOUNT(MTIM.COMPANY_CD, MTIM.DIVISION_CD, MTIM.PUR_SAL_TYPE, MTIM.CLOSE_YEAR_MONTH, MTIM.VENDOR_CD, MTIM.INVOICE_DATE, MTIM.TAX_TYPE) FROM DUAL) AS CLOSED_TAX_AMOUNT --"마감세액" , (SELECT MOM_CLOSED_PKG.FN_GET_UNCLOSED_LOCALAMOUNT(MTIM.COMPANY_CD, MTIM.DIVISION_CD,MTIM.PUR_SAL_TYPE, MTIM.VENDOR_CD, MTIM.INVOICE_DATE, MTIM.TAX_TYPE) FROM DUAL) AS UNCLOSED_AMOUNT --"미마감금액" , ROWIDTOCHAR(MTIM.ROWID) AS KEY_ID FROM MOM_MASTER.MOM_TAX_INVOICE_MST MTIM , MOM_PARTNER MP WHERE MTIM.COMPANY_CD = MP.COMPANY_CD AND MTIM.DIVISION_CD = MP.DIVISION_CD AND MTIM.VENDOR_CD = MP.PARTNER_CD AND MTIM.COMPANY_CD = #{companyCd, jdbcType=VARCHAR} AND MTIM.DIVISION_CD = #{divisionCd, jdbcType=VARCHAR} AND MTIM.CLOSE_YEAR_MONTH = #{closeYearMonth, jdbcType=VARCHAR}
AND (UPPER(MTIM.VENDOR_CD) LIKE '%' || UPPER(#{partnerCdNm, jdbcType=VARCHAR}) || '%' OR UPPER(MP.PARTNER_NM) LIKE '%' || UPPER(#{partnerCdNm, jdbcType=VARCHAR}) || '%')
AND MTIM.TAX_TYPE = #{taxType, jdbcType=VARCHAR}
AND MTIM.PUR_SAL_TYPE = 'S'

mapper.delete:
DELETE FROM MOM_PROC_TAX_INVOICE_MST


(       COMPANY_CD   = #{item.companyCd, jdbcType=VARCHAR}
   AND  DIVISION_CD  = #{item.divisionCd, jdbcType=VARCHAR}
   AND  PUR_SAL_TYPE = 'S'
   AND  CREATE_BY    = #{item.userId, jdbcType=VARCHAR}
)

insert.foreach:

	    INTO MOM_PROC_TAX_INVOICE_MST( COMPANY_CD
									 , DIVISION_CD
									 , PUR_SAL_TYPE
									 , VENDOR_CD
									 , INVOICE_DATE
									 , TAX_TYPE
									 , TAX_REMARK
									 , DESCRIPTION
									 , ACTION_TYPE
									 , VAL_MSG
									 , MULTI_MSG
									 , CREATE_BY
									 , CREATE_DATE)
        VALUES                       ( #{item.companyCd, jdbcType=VARCHAR}
					                 , #{item.divisionCd, jdbcType=VARCHAR}
					                 , 'S'
					                 , #{item.partnerCd, jdbcType=VARCHAR}
					                 , TO_DATE(#{item.invoiceDate, jdbcType=VARCHAR},'YYYY-MM-DD')
					                 , #{item.taxType, jdbcType=VARCHAR}
					                 , #{item.taxRemark, jdbcType=VARCHAR}
					                 , #{item.description, jdbcType=VARCHAR}
					                 , #{item.actionType, jdbcType=VARCHAR}
					                 , #{item.valMsg, jdbcType=VARCHAR}
					                 , #{item.multiMsg, jdbcType=VARCHAR}
					                 , #{item.userId, jdbcType=VARCHAR}
					                 , MOM_COMMON_PKG.FN_GET_LOCAL_TIME(#{item.companyCd, jdbcType=VARCHAR},#{item.divisionCd, jdbcType=VARCHAR})
					                 )

mapper.select:

        { CALL MOM_CLOSED_PKG.SP_UPSERT_TAX_INVOICE_MST( #{p_err_code,  jdbcType=VARCHAR, mode=OUT}
			                                           , #{p_err_msg,   jdbcType=VARCHAR, mode=OUT}
			                                           , #{companyCd,   jdbcType=VARCHAR, mode=IN}
			                                           , #{divisionCd,  jdbcType=VARCHAR, mode=IN}
			                                           , 'S'											  
			                                           , #{userId,      jdbcType=VARCHAR, mode=IN} )}

mapper.delete:
DELETE FROM MOM_PROC_TAX_INVOICE_DTL 


    (       COMPANY_CD     = #{item.companyCd, jdbcType=VARCHAR}
       AND  DIVISION_CD    = #{item.divisionCd, jdbcType=VARCHAR}
       AND  PUR_SAL_TYPE   = 'S'
       AND  CREATE_BY      = #{item.userId, jdbcType=VARCHAR}
    )

insert.foreach:

        INTO MOM_PROC_TAX_INVOICE_DTL( COMPANY_CD
									 , DIVISION_CD
									 , PUR_SAL_TYPE
									 , VENDOR_CD
									 , INVOICE_DATE
									 , TAX_TYPE
									 , REFERENCE_NO
									 , REFERENCE_DTL_NO
									 , ITEM_ID
									 , IO_DATE
									 , QTY
									 , LOCAL_AMOUNT
									 , TAX_RATE
									 , TAX_AMOUNT
									 , DESCRIPTION
									 , ETC_YN
									 , ACTION_TYPE
									 , VAL_MSG
									 , MULTI_MSG
									 , CREATE_BY
									 , CREATE_DATE
	                                 )
        VALUES                       ( #{item.companyCd, jdbcType=VARCHAR}
					                 , #{item.divisionCd, jdbcType=VARCHAR}
					                 , 'S'
					                 , #{item.partnerCd, jdbcType=VARCHAR}
					                 , TO_DATE(#{item.invoiceDate, jdbcType=VARCHAR},'YYYY-MM-DD')
					                 , #{item.taxType, jdbcType=VARCHAR}
					                 , #{item.referenceNo, jdbcType=VARCHAR}
					                 , #{item.referenceDtlNo, jdbcType=VARCHAR}
					                 , #{item.itemId, jdbcType=VARCHAR}
					                 , TO_DATE(#{item.invoiceDate, jdbcType=VARCHAR},'YYYY-MM-DD')
					                 , #{item.qty, jdbcType=NUMERIC}
					                 , #{item.amount, jdbcType=NUMERIC}
					                 , #{item.taxRate, jdbcType=NUMERIC}
					                 , #{item.taxAmount, jdbcType=NUMERIC}
					                 , #{item.description, jdbcType=VARCHAR}
					                 , 'Y'
					                 , #{item.actionType, jdbcType=VARCHAR}
					                 , #{item.valMsg, jdbcType=VARCHAR}
					                 , #{item.multiMsg, jdbcType=VARCHAR}
					                 , #{item.userId, jdbcType=VARCHAR}
					                 , MOM_COMMON_PKG.FN_GET_LOCAL_TIME(#{item.companyCd, jdbcType=VARCHAR},#{item.divisionCd, jdbcType=VARCHAR})
					                 )

mapper.select:

        { CALL MOM_CLOSED_PKG.SP_UPSERT_TAX_INVOICE_DTL( #{p_err_code,  jdbcType=VARCHAR, mode=OUT}
			                                           , #{p_err_msg,   jdbcType=VARCHAR, mode=OUT}
			                                           , #{companyCd,   jdbcType=VARCHAR, mode=IN}
			                                           , #{divisionCd,  jdbcType=VARCHAR, mode=IN}
			                                           , 'S'											  
			                                           , #{userId,      jdbcType=VARCHAR, mode=IN} )}
