mapper.select:
SELECT MIDS.COMPANY_CD , MIDS.DIVISION_CD , MIDS.ITEM_ID , MI.ITEM_NM , MI.ITEM_SPEC , MI.ITEM_TYPE , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MI.COMPANY_CD, MI.DIVISION_CD, 'MD0002', MI.ITEM_TYPE, #{langCd, jdbcType=VARCHAR}) FROM DUAL) AS ITEM_TYPE_NM , MIDS.UOM , (SELECT MOM_COMMON_PKG.FN_GET_UNIT_NAME(MIDS.COMPANY_CD, MIDS.DIVISION_CD, MIDS.UOM) FROM DUAL) AS UOM_NM , TO_CHAR(MIDS.IO_DATE ,'YYYY-MM-DD') AS CLOSED_DATE , MIDS.WAREHOUSE_CD , MWH.WAREHOUSE_NM , MWH.PLANT_CD , (SELECT MOM_COMMON_PKG.FN_GET_PLANT_NAME(MWH.COMPANY_CD, MWH.DIVISION_CD, MWH.PLANT_CD) FROM DUAL) AS PLANT_NM , MIDS.BEGIN_QTY , MIDS.IN_QTY , MIDS.OUT_QTY , MIDS.END_QTY , MIDS.DESCRIPTION , ROWIDTOCHAR(MIDS.ROWID) AS KEY_ID FROM MOM_ITEM_INOUT_DAILY_SUMMARY MIDS , MOM_ITEM MI , MOM_WAREHOUSE MWH WHERE MIDS.COMPANY_CD = MI.COMPANY_CD AND MIDS.DIVISION_CD = MI.DIVISION_CD AND MIDS.ITEM_ID = MI.ITEM_ID AND MIDS.COMPANY_CD = MWH.COMPANY_CD AND MIDS.DIVISION_CD = MWH.DIVISION_CD AND MIDS.WAREHOUSE_CD = MWH.WAREHOUSE_CD AND MIDS.COMPANY_CD = #{companyCd, jdbcType=VARCHAR} AND MIDS.DIVISION_CD = #{divisionCd, jdbcType=VARCHAR} AND MIDS.IO_DATE = #{closedDate, jdbcType=VARCHAR}
AND MI.ITEM_TYPE IN (${itemType})
AND MWH.PLANT_CD = #{plantCd, jdbcType=VARCHAR}
AND (UPPER(MI.ITEM_ID) LIKE '%' || UPPER(#{itemIdNm, jdbcType=VARCHAR}) || '%' OR UPPER(MI.ITEM_NM) LIKE '%' || UPPER(#{itemIdNm, jdbcType=VARCHAR}) || '%')


    { CALL MOM_CLOSED_PKG.SP_CREATE_ITEM_INOUT_DAILY_SUMMARY( #{p_err_code, jdbcType=VARCHAR, mode=OUT}
					                                        , #{p_err_msg,  jdbcType=VARCHAR, mode=OUT}
					                                        , #{companyCd,  jdbcType=VARCHAR, mode=IN}
					                                        , #{divisionCd, jdbcType=VARCHAR, mode=IN}
					                                        , #{closedDate, jdbcType=DATE,    mode=IN}													  
					                                        , #{userId,     jdbcType=VARCHAR, mode=IN} )}
