mapper.select:
SELECT MIMS.COMPANY_CD , MIMS.DIVISION_CD , MIMS.ITEM_ID , MI.ITEM_NM , MI.ITEM_SPEC , MI.ITEM_TYPE , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MI.COMPANY_CD, MI.DIVISION_CD, 'MD0002', MI.ITEM_TYPE, #{langCd, jdbcType=VARCHAR}) FROM DUAL) AS ITEM_TYPE_NM , MIMS.UNIT AS UOM , (SELECT MOM_COMMON_PKG.FN_GET_UNIT_NAME(MIMS.COMPANY_CD, MIMS.DIVISION_CD, MIMS.UNIT) FROM DUAL) AS UOM_NM , MIMS.CLOSE_YEAR_MONTH , TO_CHAR(MIMS.IO_DATE ,'YYYY-MM-DD') AS CLOSED_DATE , MIMS.LOCATION_CD , ML.LOCATION_NM , ML.PLANT_CD , (SELECT MOM_COMMON_PKG.fn_get_plant_name(ML.COMPANY_CD, ML.DIVISION_CD, ML.PLANT_CD) FROM DUAL) AS PLANT_NM , MIMS.BEGIN_QTY , MIMS.IN_QTY , MIMS.OUT_QTY , MIMS.END_QTY , MIMS.DESCRIPTION , ROWIDTOCHAR(MIMS.ROWID) AS KEY_ID FROM MOM_ITEM_INOUT_MONTHLY_SUMMARY MIMS , MOM_ITEM MI , MOM_LOCATION ML WHERE MIMS.COMPANY_CD = MI.COMPANY_CD AND MIMS.DIVISION_CD = MI.DIVISION_CD AND MIMS.ITEM_ID = MI.ITEM_ID AND MIMS.COMPANY_CD = ML.COMPANY_CD AND MIMS.DIVISION_CD = ML.DIVISION_CD AND MIMS.LOCATION_CD = ML.LOCATION_CD AND MIMS.COMPANY_CD = #{companyCd, jdbcType=VARCHAR} AND MIMS.DIVISION_CD = #{divisionCd, jdbcType=VARCHAR} AND MIMS.CLOSE_YEAR_MONTH = #{closeYearMonth, jdbcType=VARCHAR}
AND MI.ITEM_TYPE IN (${itemType})
AND ML.PLANT_CD = #{plantCd, jdbcType=VARCHAR}
AND (UPPER(MI.ITEM_ID) LIKE '%' || UPPER(#{itemIdNm, jdbcType=VARCHAR}) || '%' OR UPPER(MI.ITEM_NM) LIKE '%' || UPPER(#{itemIdNm, jdbcType=VARCHAR}) || '%')


    { CALL MOM_CLOSED_PKG.SP_CREATE_ITEM_INOUT_MOMTHLY_SUMMARY( #{p_err_code,     jdbcType=VARCHAR, mode=OUT}
					                                          , #{p_err_msg,      jdbcType=VARCHAR, mode=OUT}
					                                          , #{companyCd,      jdbcType=VARCHAR, mode=IN}
					                                          , #{divisionCd,     jdbcType=VARCHAR, mode=IN}
					                                          , #{closeYearMonth, jdbcType=VARCHAR, mode=IN}													  
					                                          , #{userId,         jdbcType=VARCHAR, mode=IN} )}
