mapper.select:
WITH C_YYMM AS (SELECT TO_CHAR(TRUNC(MC.DATE_ID, 'MONTH'), 'YYYYMM') AS YYYYMM FROM MOM_CALENDAR MC WHERE 1 =1 AND MC.DATE_ID <= MOM_COMMON_PKG.FN_GET_LOCAL_TIME(#{companyCd, jdbcType=VARCHAR},#{divisionCd, jdbcType=VARCHAR}) GROUP BY TRUNC(MC.DATE_ID, 'MONTH') ORDER BY 1 DESC ) SELECT MCC.COMPANY_CD , MCC.DIVISION_CD , C.YYYYMM AS CLOSE_YEAR_MONTH , NVL(MCC.CLOSING_YN, 'N') AS CLOSING_YN , TO_CHAR(MCC.CLOSING_DATE ,'YYYY-MM-DD') AS CLOSING_DATE , MCC.CLOSING_USER_NO , (SELECT MOM_COMMON_PKG.FN_GET_USER_NAME(MCC.COMPANY_CD, MCC.DIVISION_CD, MCC.CLOSING_USER_NO) FROM DUAL ) AS CLOSING_USER_NM , MCC.DESCRIPTION , C.YYYYMM AS KEY_ID FROM C_YYMM C , MOM_MONTHLY_CLOSING_CTL MCC WHERE C.YYYYMM = MCC.CLOSE_YEAR_MONTH(+) AND MCC.COMPANY_CD(+) = #{companyCd, jdbcType=VARCHAR} AND MCC.DIVISION_CD(+) = #{divisionCd, jdbcType=VARCHAR}
AND MCC.CLOSE_YEAR_MONTH = #{closeYearMonth, jdbcType=VARCHAR}
AND MCC.CLOSING_TYPE(+) = 'I' ORDER BY C.YYYYMM DESC

mapper.delete:
DELETE FROM MOM_PROC_MONTHLY_CLOSING


(       COMPANY_CD   = #{item.companyCd, jdbcType=VARCHAR}
   AND  DIVISION_CD  = #{item.divisionCd, jdbcType=VARCHAR}
   AND  CLOSING_TYPE = 'I'
   AND  CREATE_BY    = #{item.userId, jdbcType=VARCHAR}
)

insert.foreach:

	    INTO MOM_PROC_MONTHLY_CLOSING( COMPANY_CD
									 , DIVISION_CD
									 , CLOSING_TYPE
									 , CLOSE_YEAR_MONTH
									 , CLOSING_YN
									 , DESCRIPTION
									 , ACTION_TYPE
									 , VAL_MSG
									 , MULTI_MSG
									 , CREATE_BY
									 , CREATE_DATE)
        VALUES                       ( #{item.companyCd, jdbcType=VARCHAR}
					                 , #{item.divisionCd, jdbcType=VARCHAR}
					                 , 'I'
					                 , #{item.closeYearMonth, jdbcType=VARCHAR}
					                 , #{item.closingYn, jdbcType=VARCHAR}
					                 , #{item.description, jdbcType=VARCHAR}
					                 , #{item.actionType, jdbcType=VARCHAR}
					                 , #{item.valMsg, jdbcType=VARCHAR}
					                 , #{item.multiMsg, jdbcType=VARCHAR}
					                 , #{item.userId, jdbcType=VARCHAR}
					                 , MOM_COMMON_PKG.FN_GET_LOCAL_TIME(#{item.companyCd, jdbcType=VARCHAR},#{item.divisionCd, jdbcType=VARCHAR})
					                 )

mapper.select:

        { CALL MOM_CLOSED_PKG.SP_UPSERT_MONTHLY_CLOSING( #{p_err_code,  jdbcType=VARCHAR, mode=OUT}
			                                           , #{p_err_msg,   jdbcType=VARCHAR, mode=OUT}
			                                           , #{companyCd,   jdbcType=VARCHAR, mode=IN}
			                                           , #{divisionCd,  jdbcType=VARCHAR, mode=IN}
			                                           , 'I'											  
			                                           , #{userId,      jdbcType=VARCHAR, mode=IN} )}
