mapper.select:
SELECT A.* FROM (
SELECT MIVUP.COMPANY_CD , MIVUP.DIVISION_CD , MIVUP.ITEM_ID , MI.ITEM_NM , MI.ITEM_SPEC , MI.ITEM_TYPE , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MI.COMPANY_CD, MI.DIVISION_CD, 'MD0002', MI.ITEM_TYPE, #{langCd, jdbcType=VARCHAR}) FROM DUAL) AS ITEM_TYPE_NM , MIVUP.UNIT AS UOM , (SELECT MOM_COMMON_PKG.FN_GET_UNIT_NAME(MIVUP.COMPANY_CD, MIVUP.DIVISION_CD, MIVUP.UNIT) FROM DUAL) AS UOM_NM , MIVUP.CURRENCY_CD , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MIVUP.COMPANY_CD, MIVUP.DIVISION_CD, 'SM0017', MIVUP.CURRENCY_CD, #{langCd, jdbcType=VARCHAR}) FROM DUAL) AS CURRENCY_NM , MIVUP.PREV_UNIT_PRICE , MIVUP.UNIT_PRICE , MIVUP.START_DATE , MIVUP.END_DATE , MIVUP.QTY , MIVUP.AMOUNT , MIVUP.DESCRIPTION , ROWIDTOCHAR(MIVUP.ROWID) AS KEY_ID , COUNT(*) OVER() AS TOTAL_COUNT , ROW_NUMBER() OVER(PARTITION BY MIVUP.COMPANY_CD,MIVUP.DIVISION_CD ORDER BY MIVUP.ITEM_ID,MIVUP.START_DATE, MIVUP.END_DATE ) AS ROW_CNT FROM MOM_ITEM_VALUATION_UNIT_PRICE MIVUP , MOM_ITEM MI WHERE MIVUP.COMPANY_CD = MI.COMPANY_CD AND MIVUP.DIVISION_CD = MI.DIVISION_CD AND MIVUP.ITEM_ID = MI.ITEM_ID AND MIVUP.COMPANY_CD = #{companyCd, jdbcType=VARCHAR} AND MIVUP.DIVISION_CD = #{divisionCd, jdbcType=VARCHAR} AND TO_DATE(#{basicDate, jdbcType=VARCHAR} , 'YYYY-MM-DD') BETWEEN MIVUP.START_DATE AND MIVUP.END_DATE
AND (UPPER(MI.ITEM_ID) LIKE '%' || UPPER(#{itemIdNm, jdbcType=VARCHAR}) || '%' OR UPPER(MI.ITEM_NM) LIKE '%' || UPPER(#{itemIdNm, jdbcType=VARCHAR}) || '%')
AND MI.ITEM_TYPE = #{itemType, jdbcType=VARCHAR}
) A WHERE ROW_CNT BETWEEN #{startPage, jdbcType=INTEGER} AND #{endPage, jdbcType=INTEGER}


    { CALL MOM_CLOSED_PKG.SP_CREATE_MONTHLY_AGV_UNIT_PRICE( #{p_err_code, jdbcType=VARCHAR, mode=OUT}
					                                      , #{p_err_msg,  jdbcType=VARCHAR, mode=OUT}
					                                      , #{companyCd,  jdbcType=VARCHAR, mode=IN}
					                                      , #{divisionCd, jdbcType=VARCHAR, mode=IN}
					                                      , #{basicDate,  jdbcType=DATE,    mode=IN}													  
					                                      , #{userId,     jdbcType=VARCHAR, mode=IN} )}
