mapper.select:
SELECT MDM.COMPANY_CD , MDM.DIVISION_CD , MDM.DR_NO , MDD.SEQ , MDM.INOUT_TYPE , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MDM.COMPANY_CD, MDM.DIVISION_CD, 'MM0012', MDM.INOUT_TYPE, #{langCd, jdbcType=VARCHAR}) FROM DUAL) AS INOUT_TYPE_NM , MSO.IO_TYPE , (SELECT MOM_COMMON_PKG.FN_GET_IO_TYPE_NAME(MSO.COMPANY_CD, MSO.DIVISION_CD, MSO.IO_TYPE) FROM DUAL) AS IO_TYPE_NM , MDM.CUSTOMER_CD , (SELECT MOM_COMMON_PKG.FN_GET_PARTNER_NAME(MDM.COMPANY_CD,MDM.DIVISION_CD,MDM.CUSTOMER_CD) FROM DUAL) AS CUSTOMER_NM , MDM.REQUEST_LOCATION_CD AS LOCATION_CD , (SELECT MOM_COMMON_PKG.FN_GET_LOCATION_NAME(MDM.COMPANY_CD,MDM.DIVISION_CD,MDM.REQUEST_LOCATION_CD) FROM DUAL) AS LOCATION_NM , MDD.CURRENCY_CD , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MDD.COMPANY_CD, MDD.DIVISION_CD, 'SM0017', MDD.CURRENCY_CD, #{langCd, jdbcType=VARCHAR}) FROM DUAL) AS CURRENCY_NM , MDD.ITEM_ID , MSO.ITEM_NM , MSO.ITEM_SPEC , MSO.ITEM_TYPE , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MSO.COMPANY_CD, MSO.DIVISION_CD, 'MD0002', MSO.ITEM_TYPE, #{langCd, jdbcType=VARCHAR}) FROM DUAL) AS ITEM_TYPE_NM , MSO.SO_UNIT , MSO.SO_UNIT_NM , MDD.CONFIRM_QTY AS DR_CONFIRM_QTY , MDD.CONFIRM_QTY - NVL(MDD.PROGRESS_QTY,0) AS RECEIVE_WAIT_QTY , MDD.UNIT_PRICE , MDD.CURRENCY_CD , MDD.EXCHANGE_RATE , MDD.AMOUNT , MDD.LOCAL_AMOUNT , MSO.UNIT_PRICE , MDD.DESCRIPTION , ROWIDTOCHAR(MDD.ROWID) AS KEY_ID , MSO.SO_NO , MSO.SO_SEQ , MSO.SALES_ORDER_ID , MDD.DELIVERY_REQUEST_ID AS REFERENCE_NO FROM MOM_DR_MST MDM , MOM_DR_DTL MDD , MOM_SALES_ORDER_V MSO WHERE MDM.COMPANY_CD = MDD.COMPANY_CD AND MDM.DIVISION_CD = MDD.DIVISION_CD AND MDM.DR_NO = MDD.DR_NO AND MDD.COMPANY_CD = MSO.COMPANY_CD AND MDD.DIVISION_CD = MSO.DIVISION_CD AND MDD.REFERENCE_DTL_NO = MSO.SALES_ORDER_ID AND MDM.INOUT_TYPE = 'CR' AND MDD.STATE = 'DRF' AND MDM.COMPANY_CD = #{companyCd, jdbcType=VARCHAR} AND MDM.DIVISION_CD = #{divisionCd, jdbcType=VARCHAR}
AND (UPPER(MDM.DR_NO) LIKE '%' || UPPER(#{drNo, jdbcType=VARCHAR}) || '%')
AND MDM.REQUEST_LOCATION_CD = #{locationCd, jdbcType=VARCHAR}
AND MDM.CUSTOMER_CD = #{customerCd, jdbcType=VARCHAR}
ORDER BY MDD.DR_NO , MDD.SEQ

mapper.delete:
DELETE FROM MOM_PROC_LOCATION_RECEIPT_DTL


    (       COMPANY_CD  = #{item.companyCd, jdbcType=VARCHAR}
       AND  DIVISION_CD = #{item.divisionCd, jdbcType=VARCHAR}
       AND  CREATE_BY   = #{item.userId, jdbcType=VARCHAR}
    )

insert.foreach:

INTO MOM_PROC_LOCATION_RECEIPT_DTL( COMPANY_CD
                                  , DIVISION_CD
                                  , RECEIPT_ID
                                  , SEQ
                                  , IO_TYPE
                                  , PARTNER_CD
                                  , LOCATION_CD
                                  , ITEM_ID
                                  , UNIT
                                  , RECEIPT_QTY
                                  , CURRENCY_CD
                                  , EXCHANGE_RATE
                                  , UNIT_PRICE
                                  , AMOUNT
                                  , LOCAL_AMOUNT
                                  , REFERENCE_NO
                                  , RECEIPT_DETAIL_ID
                                  , RCV_LOGISTIC_UNIT_ID
                                  , DESCRIPTION
                                  , DEL_YN
                                  , VAL_MSG
                                  , MULTI_MSG
                                  , CREATE_BY
                                  , CREATE_DATE
                                  )
VALUES                            ( #{item.companyCd, jdbcType=VARCHAR}  
                                  , #{item.divisionCd, jdbcType=VARCHAR}         
                                  , #{item.receiptId, jdbcType=VARCHAR}
                                  , #{item.seq, jdbcType=NUMERIC}
                                  , #{item.ioType, jdbcType=VARCHAR}
                                  , #{item.customerCd, jdbcType=VARCHAR}
                                  , #{item.locationCd, jdbcType=VARCHAR}
                                  , #{item.itemId, jdbcType=VARCHAR}
                                  , #{item.soUnit, jdbcType=VARCHAR}
                                  , #{item.receiveWaitQty, jdbcType=NUMERIC}
                                  , #{item.currencyCd, jdbcType=VARCHAR}
                                  , #{item.exchangeRate, jdbcType=NUMERIC}
                                  , #{item.unitPrice, jdbcType=NUMERIC}
                                  , #{item.amount, jdbcType=NUMERIC}
                                  , #{item.localAmount, jdbcType=NUMERIC}
                                  , #{item.referenceNo, jdbcType=VARCHAR}
                                  , #{item.receiptDetailId, jdbcType=VARCHAR}
                                  , #{item.rcvLogisticUnitId, jdbcType=VARCHAR}
                                  , #{item.description, jdbcType=VARCHAR}
                                  , #{item.delYn, jdbcType=VARCHAR}
                                  , #{item.valMsg, jdbcType=VARCHAR}
                                  , #{item.multiMsg, jdbcType=VARCHAR}
                                  , #{item.userId, jdbcType=VARCHAR}
                                  , MOM_COMMON_PKG.FN_GET_LOCAL_TIME(#{item.companyCd, jdbcType=VARCHAR},#{item.divisionCd, jdbcType=VARCHAR})
                                  )

mapper.select:

{ CALL MOM_WMS_PKG.SP_UPSERT_LOCATION_RECEIPT_DTL( #{p_err_code,  jdbcType=VARCHAR, mode=OUT}
                                                 , #{p_err_msg,   jdbcType=VARCHAR, mode=OUT}
                                                 , #{companyCd,   jdbcType=VARCHAR, mode=IN}
                                                 , #{divisionCd,  jdbcType=VARCHAR, mode=IN}    
                                                 , 'OBS'                   
                                                 , #{userId,      jdbcType=VARCHAR, mode=IN} )}
