mapper.select:
SELECT MDM.COMPANY_CD , MDM.DIVISION_CD , MDM.DR_NO , MDD.SEQ , MDM.INOUT_TYPE , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MDM.COMPANY_CD, MDM.DIVISION_CD, 'MM0012', MDM.INOUT_TYPE, #{langCd, jdbcType=VARCHAR}) FROM DUAL) AS INOUT_TYPE_NM , MDD.IO_TYPE , (SELECT MOM_COMMON_PKG.FN_GET_IO_TYPE_NAME(MDD.COMPANY_CD, MDD.DIVISION_CD, MDD.IO_TYPE) FROM DUAL) AS IO_TYPE_NM , MDM.CUSTOMER_CD AS PARTNER_CD , (SELECT MOM_COMMON_PKG.FN_GET_PARTNER_NAME(MDM.COMPANY_CD,MDM.DIVISION_CD,MDM.CUSTOMER_CD) FROM DUAL) AS PARTNER_NM , MDD.LOCATION_CD AS ISSUE_LOCATION_CD , (SELECT MOM_COMMON_PKG.FN_GET_LOCATION_NAME(MDM.COMPANY_CD,MDM.DIVISION_CD,MDD.LOCATION_CD) FROM DUAL) AS ISSUE_LOCATION_NM , MDM.REQUEST_LOCATION_CD , (SELECT MOM_COMMON_PKG.FN_GET_LOCATION_NAME(MDM.COMPANY_CD,MDM.DIVISION_CD,MDM.REQUEST_LOCATION_CD) FROM DUAL) AS REQUEST_LOCATION_NM , MDD.CURRENCY_CD , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MDD.COMPANY_CD, MDD.DIVISION_CD, 'SM0017', MDD.CURRENCY_CD, #{langCd, jdbcType=VARCHAR}) FROM DUAL) AS CURRENCY_NM , MDD.ITEM_ID , MI.ITEM_NM , MI.ITEM_SPEC , MI.ITEM_TYPE , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MI.COMPANY_CD, MI.DIVISION_CD, 'MD0002', MI.ITEM_TYPE, #{langCd, jdbcType=VARCHAR}) FROM DUAL) AS ITEM_TYPE_NM , MDD.UNIT , (SELECT MOM_COMMON_PKG.FN_GET_UNIT_NAME(MDD.COMPANY_CD, MDD.DIVISION_CD, MDD.UNIT ) FROM DUAL) AS UNIT_NM , MDD.REQUEST_QTY AS DR_REQUEST_QTY , MDD.REQUEST_QTY - NVL(MDD.PROGRESS_QTY,0) AS ISSUE_WAIT_QTY , MDD.UNIT_PRICE , MDD.CURRENCY_CD , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MDD.COMPANY_CD, MDD.DIVISION_CD, 'SM0017', MDD.CURRENCY_CD, #{langCd, jdbcType=VARCHAR}) FROM DUAL) AS CURRENCY_NM , MDD.EXCHANGE_RATE , MDD.AMOUNT , MDD.LOCAL_AMOUNT , MDD.DESCRIPTION , ROWIDTOCHAR(MDD.ROWID) AS KEY_ID , MDD.REFERENCE_NO , MDD.REFERENCE_DTL_NO , MDD.LOCATION_USE_YN FROM MOM_DR_MST MDM , MOM_DR_DTL MDD , MOM_ITEM MI WHERE MDM.COMPANY_CD = MDD.COMPANY_CD AND MDM.DIVISION_CD = MDD.DIVISION_CD AND MDM.DR_NO = MDD.DR_NO AND MDD.COMPANY_CD = MI.COMPANY_CD AND MDD.DIVISION_CD = MI.DIVISION_CD AND MDD.ITEM_ID = MI.ITEM_ID AND MDD.STATE = 'DRF' AND MDD.LOCATION_USE_YN = 'N' AND MDM.COMPANY_CD = #{companyCd, jdbcType=VARCHAR} AND MDM.DIVISION_CD = #{divisionCd, jdbcType=VARCHAR}
AND MDD.LOCATION_CD = #{issueLocationCd, jdbcType=VARCHAR}
AND MDM.REQUEST_LOCATION_CD = #{requestLocationCd, jdbcType=VARCHAR}
ORDER BY MDD.DR_NO , MDD.SEQ

mapper.delete:
DELETE FROM MOM_PROC_DO_DTL


    (       COMPANY_CD  = #{item.companyCd, jdbcType=VARCHAR}
       AND  DIVISION_CD = #{item.divisionCd, jdbcType=VARCHAR}
       AND  CREATE_BY   = #{item.userId, jdbcType=VARCHAR}
    )

insert.foreach:

INTO MOM_PROC_DO_DTL( COMPANY_CD
                    , DIVISION_CD
                    , DO_NO
                    , SEQ
                    , DELIVERY_ORDER_ID
                    , QTY
                    , REFERENCE_NO
                    , REFERENCE_DTL_NO
                    , DESCRIPTION
                    , DEL_YN
                    , VAL_MSG
                    , MULTI_MSG
                    , CREATE_BY
                    , CREATE_DATE
                    )
VALUES              ( #{item.companyCd, jdbcType=VARCHAR}  
                    , #{item.divisionCd, jdbcType=VARCHAR}         
                    , #{item.doNo, jdbcType=VARCHAR}
                    , #{item.seq, jdbcType=NUMERIC}
                    , #{item.deliveryOrderId, jdbcType=VARCHAR}
                    , #{item.issueWaitQty, jdbcType=NUMERIC}
                    , #{item.referenceNo, jdbcType=VARCHAR}
                    , #{item.referenceDtlNo, jdbcType=VARCHAR}
                    , #{item.description, jdbcType=VARCHAR}
                    , #{item.delYn, jdbcType=VARCHAR}
                    , #{item.valMsg, jdbcType=VARCHAR}
                    , #{item.multiMsg, jdbcType=VARCHAR}
                    , #{item.userId, jdbcType=VARCHAR}
                    , MOM_COMMON_PKG.FN_GET_LOCAL_TIME(#{item.companyCd, jdbcType=VARCHAR},#{item.divisionCd, jdbcType=VARCHAR})
                    )

mapper.select:

{ CALL MOM_STOCK_PKG.SP_UPSERT_DO_DTL( #{p_err_code,  jdbcType=VARCHAR, mode=OUT}
                                     , #{p_err_msg,   jdbcType=VARCHAR, mode=OUT}
                                     , #{companyCd,   jdbcType=VARCHAR, mode=IN}
                                     , #{divisionCd,  jdbcType=VARCHAR, mode=IN}    
                                     , #{userId,      jdbcType=VARCHAR, mode=IN} )}
