mapper.select:
SELECT MUP.ITEM_ID , MI.ITEM_NM , MUP.PARTNER_CD , (SELECT MOM_COMMON_PKG.FN_GET_PARTNER_NAME(MUP.COMPANY_CD,MUP.DIVISION_CD,MUP.PARTNER_CD) FROM DUAL) AS PARTNER_NM , MUP.CURRENCY_CD , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MUP.COMPANY_CD, MUP.DIVISION_CD, 'SM0017', MUP.CURRENCY_CD, #{langCd, jdbcType=VARCHAR}) FROM DUAL) AS CURRENCY_NM , MI.STORAGE_WAREHOUSE_CD AS RECEIPT_WAREHOUSE_CD , (SELECT MOM_COMMON_PKG.FN_GET_WAREHOUSE_NAME(MI.COMPANY_CD,MI.DIVISION_CD,MI.STORAGE_WAREHOUSE_CD) FROM DUAL) AS RECEIPT_WAREHOUSE_NM , MI.IQC_FLAG , MI.LOT_MANAGEMENT_YN , MI.PO_OVER_RECEIPT_RATE , MUP.UNIT_PRICE , '' AS PO_QTY , MI.PURCHASE_UOM , (SELECT MOM_COMMON_PKG.FN_GET_UNIT_NAME(MI.COMPANY_CD, MI.DIVISION_CD, MI.PURCHASE_UOM) FROM DUAL) AS PURCHASE_UOM_NM , ROWIDTOCHAR(MUP.ROWID) AS KEY_ID FROM MOM_UNIT_PRICE MUP , MOM_ITEM MI WHERE MUP.COMPANY_CD = MI.COMPANY_CD(+) AND MUP.DIVISION_CD = MI.DIVISION_CD(+) AND MUP.ITEM_ID = MI.ITEM_ID(+) AND MUP.COMPANY_CD = #{companyCd, jdbcType=VARCHAR} AND MUP.DIVISION_CD = #{divisionCd, jdbcType=VARCHAR} AND MUP.PRICE_CATEGORY = 'P' AND MUP.PARTNER_CD = #{partnerCd, jdbcType=VARCHAR} AND MUP.CURRENCY_CD = #{currencyCd, jdbcType=VARCHAR}
AND (UPPER(MI.ITEM_ID) LIKE '%' || UPPER(#{itemId , jdbcType=VARCHAR}) || '%' OR UPPER(MI.ITEM_NM) LIKE '%' || UPPER(#{itemId , jdbcType=VARCHAR}))
AND TRUNC(MOM_COMMON_PKG.FN_GET_LOCAL_TIME(MUP.COMPANY_CD, MUP.DIVISION_CD)) BETWEEN MUP.START_DATE AND MUP.END_DATE

mapper.delete:
DELETE FROM MOM_PROC_PO_DTL


(       COMPANY_CD         = #{item.companyCd, jdbcType=VARCHAR}
   AND  DIVISION_CD        = #{item.divisionCd, jdbcType=VARCHAR}
   AND  PO_REFERENCE_TYPE  = '20'
   AND  CREATE_BY          = #{item.userId, jdbcType=VARCHAR}
)

insert.foreach:

        INTO MOM_PROC_PO_DTL( COMPANY_CD
			                , DIVISION_CD
			                , PO_REFERENCE_TYPE
			                , PO_NO
			                , PURCHASE_ORDER_ID
			                , REFERENCE_NO
			                , REFERENCE_DTL_NO
			                , ITEM_ID
			                , PURCHASE_UOM
			                , QTY
			                , UNIT_PRICE
			                , WAREHOUSE_CD
			                , IQC_FLAG
			                , LOT_MANAGEMENT_YN
			                , PO_OVER_RECEIPT_RATE
			                , DESCRIPTION
			                , DEL_YN
			                , VAL_MSG
			                , MULTI_MSG
			                , CREATE_BY
			                , CREATE_DATE )
        VALUES               ( #{item.companyCd, jdbcType=VARCHAR}
			                , #{item.divisionCd, jdbcType=VARCHAR}
			                , '20'
			                , #{item.poNo, jdbcType=VARCHAR}
			                , #{item.purchaseOrderId, jdbcType=VARCHAR}
			                , #{item.prNo, jdbcType=VARCHAR}
			                , #{item.purchaseRequestId, jdbcType=VARCHAR}
			                , #{item.itemId, jdbcType=VARCHAR}
			                , #{item.purchaseUom, jdbcType=VARCHAR}
			                , #{item.poQty, jdbcType=NUMERIC}
			                , #{item.unitPrice, jdbcType=NUMERIC}
			                , #{item.receiptWarehouseCd, jdbcType=VARCHAR}
			                , #{item.iqcFlag, jdbcType=VARCHAR}
			                , #{item.lotManagementYn, jdbcType=VARCHAR}
			                , #{item.poOverReceiptRate, jdbcType=NUMERIC}
			                , #{item.description, jdbcType=VARCHAR}
			                ,'N'
			                , #{item.valMsg, jdbcType=VARCHAR}
			                , #{item.multiMsg, jdbcType=VARCHAR}
			                , #{item.userId, jdbcType=VARCHAR}
			                , MOM_COMMON_PKG.FN_GET_LOCAL_TIME(#{item.companyCd, jdbcType=VARCHAR},#{item.divisionCd, jdbcType=VARCHAR})
			              )

mapper.select:

        { CALL MOM_MATERIAL_PKG.SP_UPSERT_PO_DTL( #{p_err_code,       jdbcType=VARCHAR, mode=OUT}
		                                        , #{p_err_msg,        jdbcType=VARCHAR, mode=OUT}
		                                        , #{companyCd,        jdbcType=VARCHAR, mode=IN}
		                                        , #{divisionCd,       jdbcType=VARCHAR, mode=IN}	
		                                        , '20'										  
		                                        , #{userId,           jdbcType=VARCHAR, mode=IN} )}
