mapper.select:
SELECT MIS.COMPANY_CD , MIS.DIVISION_CD , MIS.LOCATION_CD , (SELECT MOM_COMMON_PKG.fn_get_location_name(MIS.COMPANY_CD, MIS.DIVISION_CD, MIS.LOCATION_CD) FROM DUAL) AS LOCATION_NM , MIS.AREA_CD , (SELECT MOM_COMMON_PKG.FN_GET_AREA_NAME(MIS.COMPANY_CD, MIS.DIVISION_CD, MIS.AREA_CD) FROM DUAL) AS AREA_NM , MIS.LOGISTICS_UNIT_CD , MIS.ITEM_ID , MI.ITEM_NM , MI.ITEM_SPEC , MI.ITEM_TYPE , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MI.COMPANY_CD, MI.DIVISION_CD, 'MD0002', MI.ITEM_TYPE, #{langCd, jdbcType=VARCHAR}) FROM DUAL) AS ITEM_TYPE_NM , MIS.STOCK_QTY , ROWIDTOCHAR(MIS.ROWID) AS KEY_ID FROM MOM_AREA_ITEM_STOCK MIS , MOM_ITEM MI WHERE MIS.COMPANY_CD = MI.COMPANY_CD AND MIS.DIVISION_CD = MI.DIVISION_CD AND MIS.ITEM_ID = MI.ITEM_ID AND MIS.COMPANY_CD = #{companyCd, jdbcType=VARCHAR} AND MIS.DIVISION_CD = #{divisionCd, jdbcType=VARCHAR} AND MIS.LOCATION_CD = #{pickingLocationCd, jdbcType=VARCHAR} AND MIS.ITEM_ID = #{itemId, jdbcType=VARCHAR}
AND MIS.LOGISTICS_UNIT_CD != #{toLogisticsUnitCd, jdbcType=VARCHAR}
AND MIS.STOCK_QTY > 0 ORDER BY MIS.STOCK_QTY

mapper.delete:
DELETE FROM MOM_PROC_EXEC_TASK_RESULT 


    (       COMPANY_CD     = #{item.companyCd, jdbcType=VARCHAR}
       AND  DIVISION_CD    = #{item.divisionCd, jdbcType=VARCHAR}
       AND  EXEC_TASK_TYPE = 'P'
       AND  CREATE_BY      = #{item.userId, jdbcType=VARCHAR}
    )

insert.foreach:

        INTO MOM_PROC_EXEC_TASK_RESULT( COMPANY_CD
	                                  , DIVISION_CD
	                                  , EXEC_TASK_TYPE 
                                      , EXEC_TASK_ID 
                                      , EXEC_TASK_DTL_ID  
	                                  , LOCATION_CD  
	                                  , FROM_AREA_CD 
	                                  , FROM_LOGISTICS_UNIT_CD
	                                  , TO_AREA_CD 
	                                  , TO_LOGISTICS_UNIT_CD
	                                  , ITEM_ID 
	                                  , UNIT
	                                  , QTY 
	                                  , DESCRIPTION
	                                  , VAL_MSG
	                                  , MULTI_MSG
	                                  , CREATE_BY
	                                  , CREATE_DATE
	                                  )
        VALUES                        ( #{item.companyCd, jdbcType=VARCHAR}
	                                  , #{item.divisionCd, jdbcType=VARCHAR}
	                                  , 'P'
	                                  , #{item.pickingId, jdbcType=VARCHAR}
	                                  , #{item.pickingDetailId, jdbcType=VARCHAR}
	                                  , #{item.locationCd, jdbcType=VARCHAR}
	                                  , #{item.areaCd, jdbcType=VARCHAR}
	                                  , #{item.logisticsUnitCd, jdbcType=VARCHAR}
	                                  , #{item.toAreaCd, jdbcType=VARCHAR}
	                                  , #{item.toLogisticsUnitCd, jdbcType=VARCHAR}
	                                  , #{item.itemId, jdbcType=VARCHAR}
	                                  , #{item.unit, jdbcType=VARCHAR}
	                                  , #{item.pickingQty, jdbcType=NUMERIC}
	                                  , #{item.description, jdbcType=VARCHAR}
	                                  , #{item.valMsg, jdbcType=VARCHAR}
	                                  , #{item.multiMsg, jdbcType=VARCHAR}
	                                  , #{item.userId, jdbcType=VARCHAR}
	                                  , MOM_COMMON_PKG.FN_GET_LOCAL_TIME(#{item.companyCd, jdbcType=VARCHAR},#{item.divisionCd, jdbcType=VARCHAR})
	                                  )

mapper.select:

{ CALL MOM_WMS_PKG.SP_LOCATION_EXEC_TASK_RESULT( #{p_err_code,  jdbcType=VARCHAR, mode=OUT}
                                               , #{p_err_msg,   jdbcType=VARCHAR, mode=OUT}
                                               , #{companyCd,   jdbcType=VARCHAR, mode=IN}
                                       		   , #{divisionCd,  jdbcType=VARCHAR, mode=IN} 
                                       		   , 'P'
                                               , #{userId,      jdbcType=VARCHAR, mode=IN} )}
