mapper.select:
SELECT MPD.PURCHASE_REQUEST_ID , MPM.PR_TYPE , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MPM.COMPANY_CD, MPM.DIVISION_CD, 'MM0001', MPM.PR_TYPE, #{langCd, jdbcType=VARCHAR}) FROM DUAL) AS PR_TYPE_NM , MPM.PR_TITLE , TO_CHAR(MPD.DUE_DATE ,'YYYY-MM-DD') AS REQUEST_DUE_DATE , MPM.REQUEST_DEPT_CD , (SELECT MOM_COMMON_PKG.FN_GET_DEPARTMENT_NAME(MPM.COMPANY_CD, MPM.DIVISION_CD, MPM.REQUEST_DEPT_CD) FROM DUAL) AS REQUEST_DEPT_NM , MPD.PO_USER_NO , (SELECT MOM_COMMON_PKG.FN_GET_USER_NAME(MPD.COMPANY_CD, MPD.DIVISION_CD, MPD.PO_USER_NO) FROM DUAL ) AS PO_USER_NM , MPD.ITEM_ID , MPD.REQUEST_QTY AS PURCHASE_REQUEST_QTY , MPD.PO_QTY AS PURCHASE_ORDERED_QTY , MPD.REQUEST_QTY - NVL(MPD.PO_QTY, 0) - NVL(MPD.CLOSE_QTY,0) AS PO_PASSIBLE_QTY , MPD.LOCAL_AMOUNT , MPD.CURRENCY_CD , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MPD.COMPANY_CD, MPD.DIVISION_CD, 'SM0017', MPD.CURRENCY_CD, #{langCd, jdbcType=VARCHAR}) FROM DUAL) AS CURRENCY_NM , MI.STORAGE_WAREHOUSE_CD AS RECEIPT_WAREHOUSE_CD , (SELECT MOM_COMMON_PKG.FN_GET_WAREHOUSE_NAME(MPD.COMPANY_CD,MPD.DIVISION_CD,MI.STORAGE_WAREHOUSE_CD) FROM DUAL) AS RECEIPT_WAREHOUSE_NM , MI.ITEM_NM , MI.ITEM_SPEC , MI.ITEM_TYPE , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MPD.COMPANY_CD, MPD.DIVISION_CD, 'MD0002', MI.ITEM_TYPE, #{langCd, jdbcType=VARCHAR}) FROM DUAL) AS ITEM_TYPE_NM , MPD.PURCHASE_UOM , (SELECT MOM_COMMON_PKG.FN_GET_UNIT_NAME(MPD.COMPANY_CD, MPD.DIVISION_CD, MPD.PURCHASE_UOM) FROM DUAL) AS PURCHASE_UOM_NM , MI.UOM , (SELECT MOM_COMMON_PKG.FN_GET_UNIT_NAME(MI.COMPANY_CD, MI.DIVISION_CD, MI.UOM) FROM DUAL) AS UOM_NM , MI.MAKER , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MPD.COMPANY_CD, MPD.DIVISION_CD, 'MD0007', MI.MAKER, #{langCd, jdbcType=VARCHAR}) FROM DUAL) AS MAKER_NM , NVL(MI.IQC_FLAG, 'N') AS IQC_FLAG , NVL(MI.LOT_MANAGEMENT_YN, 'N') AS LOT_MANAGEMENT_YN , MI.PO_OVER_RECEIPT_RATE , MPM.REFERENCE_NO , MPD.DESCRIPTION , TO_CHAR(MPM.REQUEST_DATE ,'YYYY-MM-DD') AS REQUEST_DATE , MPM.REQUEST_USER_NO , (SELECT MOM_COMMON_PKG.FN_GET_USER_NAME(MPM.COMPANY_CD, MPM.DIVISION_CD, MPM.REQUEST_USER_NO) FROM DUAL ) AS REQUEST_USER_NM , TO_CHAR(MPM.CONFIRM_DATE ,'YYYY-MM-DD') AS CONFIRM_DATE , MPM.CONFIRM_USER_NO , (SELECT MOM_COMMON_PKG.FN_GET_USER_NAME(MPM.COMPANY_CD, MPM.DIVISION_CD, MPM.CONFIRM_USER_NO) FROM DUAL) AS CONFIRM_USER_NM , TO_CHAR(MPD.RECEIPT_DATE ,'YYYY-MM-DD') AS RECEIPT_DATE , MPD.RECEIPT_USER_NO , (SELECT MOM_COMMON_PKG.FN_GET_USER_NAME(MPD.COMPANY_CD, MPD.DIVISION_CD, MPD.RECEIPT_USER_NO) FROM DUAL ) AS RECEIPT_USER_NM , MI.PROCUREMENT_TYPE , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MPD.COMPANY_CD, MPD.DIVISION_CD, 'MD0004', MI.PROCUREMENT_TYPE, #{langCd, jdbcType=VARCHAR}) FROM DUAL) AS PROCUREMENT_TYPE_NM , MPD.COMPANY_CD , MPD.DIVISION_CD , MPD.PR_NO , MPD.PR_SEQ , ROWIDTOCHAR(MPD.ROWID) AS KEY_ID FROM MOM_PR_DTL MPD , MOM_PR_MST MPM , MOM_ITEM MI , MOM_UNIT_PRICE MUP WHERE MPD.COMPANY_CD = MPM.COMPANY_CD AND MPD.DIVISION_CD = MPM.DIVISION_CD AND MPD.PR_NO = MPM.PR_NO AND MPD.COMPANY_CD = MI.COMPANY_CD AND MPD.DIVISION_CD = MI.DIVISION_CD AND MPD.ITEM_ID = MI.ITEM_ID AND MPD.COMPANY_CD = MUP.COMPANY_CD AND MPD.DIVISION_CD = MUP.DIVISION_CD AND MPD.ITEM_ID = MUP.ITEM_ID AND MPD.CURRENCY_CD = MUP.CURRENCY_CD AND MPD.COMPANY_CD = #{companyCd, jdbcType=VARCHAR} AND MPD.DIVISION_CD = #{divisionCd, jdbcType=VARCHAR} AND MPD.STATE = 'PRR' -- 접수건
AND (UPPER(MPM.PR_NO) LIKE '%' || UPPER(#{prNo, jdbcType=VARCHAR}) || '%')
AND MPM.REQUEST_DEPT_CD = #{requestDeptCd, jdbcType=VARCHAR}
AND (UPPER(MI.ITEM_ID) LIKE '%' || UPPER(#{itemIdNm, jdbcType=VARCHAR}) || '%' OR UPPER(MI.ITEM_NM) LIKE '%' || UPPER(#{itemIdNm, jdbcType=VARCHAR}) || '%')
AND MPD.PO_USER_NO = #{poUserNo, jdbcType=VARCHAR}
AND MPD.REQUEST_QTY - NVL(MPD.PO_QTY, 0) - NVL(MPD.CLOSE_QTY,0) > 0 AND MUP.PRICE_CATEGORY = 'P' AND MUP.PARTNER_CD = #{partnerCd, jdbcType=VARCHAR} AND MUP.CURRENCY_CD = #{currencyCd, jdbcType=VARCHAR} AND TRUNC(MOM_COMMON_PKG.FN_GET_LOCAL_TIME(#{companyCd, jdbcType=VARCHAR},#{divisionCd, jdbcType=VARCHAR})) BETWEEN MUP.START_DATE AND MUP.END_DATE ORDER BY MPD.PR_NO , MPD.PR_SEQ

mapper.delete:
DELETE FROM MOM_PROC_PO_DTL


(       COMPANY_CD         = #{item.companyCd, jdbcType=VARCHAR}
   AND  DIVISION_CD        = #{item.divisionCd, jdbcType=VARCHAR}
   AND  PO_REFERENCE_TYPE  = '10'
   AND  CREATE_BY          = #{item.userId, jdbcType=VARCHAR}
)

insert.foreach:

        INTO MOM_PROC_PO_DTL( COMPANY_CD
			                , DIVISION_CD
			                , PO_REFERENCE_TYPE
			                , PO_NO
			                , PURCHASE_ORDER_ID
			                , REFERENCE_NO
			                , REFERENCE_DTL_NO
			                , ITEM_ID
			                , PURCHASE_UOM
			                , QTY
			                , UNIT_PRICE
			                , WAREHOUSE_CD
			                , IQC_FLAG
			                , LOT_MANAGEMENT_YN
			                , PO_OVER_RECEIPT_RATE
			                , DESCRIPTION
			                , DEL_YN
			                , VAL_MSG
			                , MULTI_MSG
			                , CREATE_BY
			                , CREATE_DATE )
        VALUES               ( #{item.companyCd, jdbcType=VARCHAR}
			                , #{item.divisionCd, jdbcType=VARCHAR}
			                , '10'
			                , #{item.poNo, jdbcType=VARCHAR}
			                , #{item.purchaseOrderId, jdbcType=VARCHAR}
			                , #{item.prNo, jdbcType=VARCHAR}
			                , #{item.purchaseRequestId, jdbcType=VARCHAR}
			                , #{item.itemId, jdbcType=VARCHAR}
			                , #{item.purchaseUom, jdbcType=VARCHAR}
			                , #{item.poPassibleQty, jdbcType=NUMERIC}
			                , #{item.refUnitPrice, jdbcType=NUMERIC}
			                , #{item.receiptWarehouseCd, jdbcType=VARCHAR}
			                , #{item.iqcFlag, jdbcType=VARCHAR}
			                , #{item.lotManagementYn, jdbcType=VARCHAR}
			                , #{item.poOverReceiptRate, jdbcType=NUMERIC}
			                , #{item.description, jdbcType=VARCHAR}
			                ,'N'
			                , #{item.valMsg, jdbcType=VARCHAR}
			                , #{item.multiMsg, jdbcType=VARCHAR}
			                , #{item.userId, jdbcType=VARCHAR}
			                , MOM_COMMON_PKG.FN_GET_LOCAL_TIME(#{item.companyCd, jdbcType=VARCHAR},#{item.divisionCd, jdbcType=VARCHAR})
			              )

mapper.select:

        { CALL MOM_MATERIAL_PKG.SP_UPSERT_PO_DTL( #{p_err_code,       jdbcType=VARCHAR, mode=OUT}
		                                        , #{p_err_msg,        jdbcType=VARCHAR, mode=OUT}
		                                        , #{companyCd,        jdbcType=VARCHAR, mode=IN}
		                                        , #{divisionCd,       jdbcType=VARCHAR, mode=IN}	
		                                        , '10'										  
		                                        , #{userId,           jdbcType=VARCHAR, mode=IN} )}
