mapper.select:
SELECT MPM.PO_NO , MPD.PO_SEQ , MPM.PO_TITLE , MPM.PO_TYPE , (SELECT MOM_COMMON_PKG.FN_GET_PO_TYPE_NAME(MPM.COMPANY_CD, MPM.DIVISION_CD, MPM.PO_TYPE) FROM DUAL) AS PO_TYPE_NM , MPD.ITEM_ID , TO_CHAR(MPM.PO_DUE_DATE ,'YYYY-MM-DD') AS PO_DUE_DATE , TO_CHAR(MPD.DELIVER_CONFIRM_DATE ,'YYYY-MM-DD') AS DELIVER_CONFIRM_DATE , MPD.PO_QTY , NVL(MPD.PROGRESS_QTY,0) AS RECEIVE_QTY , (MPD.PO_QTY - NVL(MPD.PROGRESS_QTY,0)) AS REMAIN_QTY , MPM.CURRENCY_CD , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MPM.COMPANY_CD, MPM.DIVISION_CD, 'SM0017', MPM.CURRENCY_CD, #{langCd, jdbcType=VARCHAR}) FROM DUAL) AS CURRENCY_NM , MPD.UNIT_PRICE , MPM.EXCHANGE_RATE , MPD.AMOUNT , MPD.LOCAL_AMOUNT , MPD.PURCHASE_UOM , (SELECT MOM_COMMON_PKG.FN_GET_UNIT_NAME(MPD.COMPANY_CD, MPD.DIVISION_CD, MPD.PURCHASE_UOM) FROM DUAL) AS PURCHASE_UOM_NM , MPD.WAREHOUSE_CD AS RECEIPT_WAREHOUSE_CD , (SELECT MOM_COMMON_PKG.FN_GET_WAREHOUSE_NAME(MPD.COMPANY_CD,MPD.DIVISION_CD,MPD.WAREHOUSE_CD) FROM DUAL) AS RECEIPT_WAREHOUSE_NM , MPM.PAYMENT_METHOD , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MPM.COMPANY_CD, MPM.DIVISION_CD, 'MM0006', MPM.PAYMENT_METHOD, #{langCd, jdbcType=VARCHAR}) FROM DUAL) AS PAYMENT_METHOD_NM , MPM.PRICE_TERMS , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MPM.COMPANY_CD, MPM.DIVISION_CD, 'MM0007', MPM.PRICE_TERMS, #{langCd, jdbcType=VARCHAR}) FROM DUAL) AS PRICE_TERMS_NM , MPM.PAYMENT_TERMS , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MPM.COMPANY_CD, MPM.DIVISION_CD, 'MM0008', MPM.PAYMENT_TERMS, #{langCd, jdbcType=VARCHAR}) FROM DUAL) AS PAYMENT_TERMS_NM , MPM.SHIPPING_METHOD , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MPM.COMPANY_CD, MPM.DIVISION_CD, 'MM0009', MPM.SHIPPING_METHOD, #{langCd, jdbcType=VARCHAR}) FROM DUAL) AS SHIPPING_METHOD_NM , TO_CHAR(MPM.CREATE_DATE ,'YYYY-MM-DD') AS PO_CREATE_DATE , MPM.CREATE_BY AS PO_USER_NO , (SELECT MOM_COMMON_PKG.FN_GET_USER_NAME(MPM.COMPANY_CD, MPM.DIVISION_CD, MPM.CREATE_BY) FROM DUAL ) AS PO_USER_NM , MPM.CREATE_DEPT_CD AS PO_DEPT_CD , (SELECT MOM_COMMON_PKG.FN_GET_DEPARTMENT_NAME(MPM.COMPANY_CD, MPM.DIVISION_CD, MPM.CREATE_DEPT_CD) FROM DUAL) AS PO_DEPT_NM , TO_CHAR(MPM.CONFIRM_DATE ,'YYYY-MM-DD') AS CONFIRM_DATE , MI.ITEM_NM , MI.ITEM_SPEC , MI.ITEM_TYPE , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MI.COMPANY_CD, MI.DIVISION_CD, 'MD0002', MI.ITEM_TYPE, #{langCd, jdbcType=VARCHAR}) FROM DUAL) AS ITEM_TYPE_NM , MPD.IQC_FLAG , MI.MAKER , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MI.COMPANY_CD, MI.DIVISION_CD, 'MD0007', MI.MAKER, #{langCd, jdbcType=VARCHAR}) FROM DUAL) AS MAKER_NM , MI.PROCUREMENT_TYPE , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MI.COMPANY_CD, MI.DIVISION_CD, 'MD0004', MI.PROCUREMENT_TYPE, #{langCd, jdbcType=VARCHAR}) FROM DUAL) AS PROCUREMENT_TYPE_NM , MPM.VENDOR_CD AS PARTNER_CD , (SELECT MOM_COMMON_PKG.FN_GET_PARTNER_NAME(MPM.COMPANY_CD,MPM.DIVISION_CD,MPM.VENDOR_CD) FROM DUAL) AS PARTNER_NM , MPD.PURCHASE_ORDER_ID , MPD.PO_OVER_RECEIPT_RATE , MPD.PO_QTY + NVL(DECODE(NVL(MPD.PO_OVER_RECEIPT_RATE,0), 0, 0, (MPD.PO_QTY * ( MPD.PO_OVER_RECEIPT_RATE / 100) )),0) AS PO_OVER_RECEIPT_QTY , MI.LOT_MANAGEMENT_YN , MI.LOT_MARK , MPD.COMPANY_CD , MPD.DIVISION_CD , DECODE(MPC.MARKET_CD, 'D', 'RD', 'O', 'RO') AS PROCESS_CD , ROWIDTOCHAR(MPD.ROWID) AS KEY_ID FROM MOM_PO_MST MPM , MOM_PO_DTL MPD , MOM_ITEM MI , MOM_PO_CATEGORY MPC WHERE MPM.COMPANY_CD = MPD.COMPANY_CD AND MPM.DIVISION_CD = MPD.DIVISION_CD AND MPM.PO_NO = MPD.PO_NO AND MPM.COMPANY_CD = MI.COMPANY_CD AND MPM.DIVISION_CD = MI.DIVISION_CD AND MPD.ITEM_ID = MI.ITEM_ID AND MPM.COMPANY_CD = MPC.COMPANY_CD AND MPM.DIVISION_CD = MPC.DIVISION_CD AND MPM.PO_TYPE = MPC.PO_TYPE AND MPM.COMPANY_CD = #{companyCd, jdbcType=VARCHAR} AND MPM.DIVISION_CD = #{divisionCd, jdbcType=VARCHAR} AND MPM.DO_INVOICE_YN = 'N' AND MPC.MARKET_CD = DECODE(#{processCd, jdbcType=VARCHAR}, 'RD', 'D', 'RO', 'O') AND MPD.STATE = 'POF' --확정 AND MPM.PO_REFERENCE_TYPE IN ('10','20')
AND MPM.VENDOR_CD = #{partnerCd, jdbcType=VARCHAR}
AND MPD.WAREHOUSE_CD = #{receiptWarehouseCd, jdbcType=VARCHAR}
AND MPD.IQC_FLAG = #{iqcFlag, jdbcType=VARCHAR}
AND MPM.CURRENCY_CD = #{currencyCd, jdbcType=VARCHAR}

mapper.delete:
	DELETE FROM MOM_PROC_PI_DTL


            (    COMPANY_CD  = #{item.companyCd, jdbcType=VARCHAR}
            AND  DIVISION_CD = #{item.divisionCd, jdbcType=VARCHAR}
            AND  PROCESS_CD  = 'RO'
            AND  CREATE_BY   = #{item.userId, jdbcType=VARCHAR}
            )

insert.foreach:

        INTO MOM_PROC_PI_DTL( COMPANY_CD
		                    , DIVISION_CD
		                    , PROCESS_CD
		                    , INOUT_NO
		                    , PURCHASE_INOUT_ID
		                    , IO_TYPE
		                    , ITEM_ID
		                    , INOUT_UOM
		                    , INOUT_QTY
		                    , CURRENCY_CD
		                    , EXCHANGE_RATE
		                    , UNIT_PRICE
		                    , PO_OVER_RECEIPT_RATE
		                    , LOT_USE_YN
                            , LOT_RULE
		                    , REFERENCE_NO
		                    , REFERENCE_DTL_NO
		                    , DESCRIPTION
		                    , DEL_YN
		                    , VAL_MSG
		                    , MULTI_MSG
		                    , CREATE_BY
		                    , CREATE_DATE )
        VALUES              ( #{item.companyCd, jdbcType=VARCHAR}
			                , #{item.divisionCd, jdbcType=VARCHAR}
			                , 'RO'
			                , #{item.inoutNo, jdbcType=VARCHAR}
			                , #{item.purchaseInoutId, jdbcType=VARCHAR}
			                , #{item.ioType, jdbcType=VARCHAR}
			                , #{item.itemId, jdbcType=VARCHAR}
			                , #{item.purchaseUom, jdbcType=VARCHAR}
			                , #{item.remainQty, jdbcType=NUMERIC}
			                , #{item.currencyCd, jdbcType=VARCHAR}
                            , #{item.exchangeRate, jdbcType=NUMERIC}
                            , #{item.unitPrice, jdbcType=NUMERIC}	 
                            , #{item.poOverReceiptRate, jdbcType=NUMERIC}	
                            , #{item.lotManagementYn, jdbcType=VARCHAR}
			                , #{item.lotMake, jdbcType=VARCHAR}  
						    , #{item.poNo, jdbcType=VARCHAR}
                            , #{item.purchaseOrderId, jdbcType=VARCHAR}
			                , #{item.description, jdbcType=VARCHAR}
			                , #{item.delYn, jdbcType=VARCHAR}
			                , #{item.valMsg, jdbcType=VARCHAR}
			                , #{item.multiMsg, jdbcType=VARCHAR}
			                , #{item.userId, jdbcType=VARCHAR}
			                , MOM_COMMON_PKG.FN_GET_LOCAL_TIME(#{item.companyCd, jdbcType=VARCHAR},#{item.divisionCd, jdbcType=VARCHAR})
                            )

mapper.select:

    { CALL MOM_MATERIAL_PKG.SP_UPSERT_PI_DTL( #{p_err_code,  jdbcType=VARCHAR, mode=OUT}
	                                        , #{p_err_msg,   jdbcType=VARCHAR, mode=OUT}
                                            , #{companyCd,   jdbcType=VARCHAR , mode=IN}
                                            , #{divisionCd,  jdbcType=VARCHAR, mode=IN}			
                                            , 'RO'								  
                                            , #{userId,      jdbcType=VARCHAR, mode=IN} )}
