mapper.select:
SELECT MUP.COMPANY_CD , MUP.DIVISION_CD , MUP.PARTNER_CD , (SELECT MOM_COMMON_PKG.FN_GET_PARTNER_NAME(MUP.COMPANY_CD,MUP.DIVISION_CD,MUP.PARTNER_CD) FROM DUAL) AS PARTNER_NM , MUP.ITEM_ID , MI.ITEM_NM , MI.ITEM_SPEC , MI.ITEM_TYPE , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MI.COMPANY_CD, MI.DIVISION_CD, 'MD0002', MI.ITEM_TYPE, #{langCd, jdbcType=VARCHAR}) FROM DUAL) AS ITEM_TYPE_NM , MI.PURCHASE_UOM , (SELECT MOM_COMMON_PKG.FN_GET_UNIT_NAME(MI.COMPANY_CD, MI.DIVISION_CD, MI.PURCHASE_UOM) FROM DUAL) AS PURCHASE_UOM_NM , MI.UOM AS CONVERSION_UOM , (SELECT MOM_COMMON_PKG.FN_GET_UNIT_NAME(MI.COMPANY_CD, MI.DIVISION_CD, MI.UOM) FROM DUAL) AS CONVERSION_UOM_NM , MI.STORAGE_WAREHOUSE_CD AS RETURN_WAREHOUSE_CD , (SELECT MOM_COMMON_PKG.FN_GET_WAREHOUSE_NAME(MI.COMPANY_CD,MI.DIVISION_CD,MI.STORAGE_WAREHOUSE_CD) FROM DUAL) AS RETURN_WAREHOUSE_NM , MI.PO_OVER_RECEIPT_RATE , MUP.CURRENCY_CD , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MUP.COMPANY_CD, MUP.DIVISION_CD, 'SM0017', MUP.CURRENCY_CD, #{langCd, jdbcType=VARCHAR}) FROM DUAL) AS CURRENCY_NM , MUP.UNIT_PRICE , '' AS RECEIPT_REQUEST_QTY , '' AS EXCEPTION_REASON_CD , MUP.START_DATE , MUP.END_DATE , ROWIDTOCHAR(MUP.ROWID) AS KEY_ID FROM MOM_UNIT_PRICE MUP , MOM_ITEM MI WHERE MUP.COMPANY_CD = MI.COMPANY_CD AND MUP.DIVISION_CD = MI.DIVISION_CD AND MUP.ITEM_ID = MI.ITEM_ID AND MUP.COMPANY_CD = #{companyCd, jdbcType=VARCHAR} AND MUP.DIVISION_CD = #{divisionCd, jdbcType=VARCHAR} AND MUP.PRICE_CATEGORY = 'P'
AND MUP.PARTNER_CD = #{partnerCd, jdbcType=VARCHAR}
AND MUP.CURRENCY_CD = #{currencyCd, jdbcType=VARCHAR}
AND MOM_COMMON_PKG.FN_GET_LOCAL_TIME(#{companyCd, jdbcType=VARCHAR},#{divisionCd, jdbcType=VARCHAR}) BETWEEN MUP.START_DATE AND MUP.END_DATE AND MUP.ITEM_ID NOT IN (SELECT MPD.ITEM_ID FROM MOM_PURCHASE_INOUT_DTL MPD WHERE MPD.COMPANY_CD = MUP.COMPANY_CD AND MPD.DIVISION_CD = MUP.DIVISION_CD AND MPD.INOUT_NO = #{returnNo, jdbcType=VARCHAR})

mapper.delete:
	DELETE FROM MOM_PROC_PI_DTL


            (    COMPANY_CD  = #{item.companyCd, jdbcType=VARCHAR}
            AND  DIVISION_CD = #{item.divisionCd, jdbcType=VARCHAR}
            AND  PROCESS_CD  = 'TD' 
            AND  CREATE_BY   = #{item.userId, jdbcType=VARCHAR}
            )

insert.foreach:

        INTO MOM_PROC_PI_DTL( COMPANY_CD
			                , DIVISION_CD
			                , PROCESS_CD
			                , INOUT_NO
			                , PURCHASE_INOUT_ID
			                , IO_TYPE
			                , ITEM_ID
			                , INOUT_UOM
			                , CURRENCY_CD
			                , EXCHANGE_RATE
			                , UNIT_PRICE
			                , PO_OVER_RECEIPT_RATE
			                , DESCRIPTION
			                , DEL_YN
			                , VAL_MSG
			                , MULTI_MSG
			                , CREATE_BY
			                , CREATE_DATE )
        VALUES              ( #{item.companyCd, jdbcType=VARCHAR}
			                , #{item.divisionCd, jdbcType=VARCHAR}
			                , 'TD' 
			                , #{item.inoutNo, jdbcType=VARCHAR}
			                , #{item.purchaseInoutId, jdbcType=VARCHAR}
			                , #{item.ioType, jdbcType=VARCHAR}
			                , #{item.itemId, jdbcType=VARCHAR}
			                , #{item.purchaseUom, jdbcType=VARCHAR}
			                , #{item.currencyCd, jdbcType=VARCHAR}
			                , #{item.exchangeRate, jdbcType=NUMERIC}
			                , #{item.unitPrice, jdbcType=NUMERIC}
			                , #{item.poOverReceiptRate, jdbcType=NUMERIC}	
			                , #{item.description, jdbcType=VARCHAR}
			                , #{item.delYn, jdbcType=VARCHAR}
			                , #{item.valMsg, jdbcType=VARCHAR}
			                , #{item.multiMsg, jdbcType=VARCHAR}
			                , #{item.userId, jdbcType=VARCHAR}
			                , MOM_COMMON_PKG.FN_GET_LOCAL_TIME(#{item.companyCd, jdbcType=VARCHAR},#{item.divisionCd, jdbcType=VARCHAR})
                            )

mapper.select:

    { CALL MOM_MATERIAL_PKG.SP_UPSERT_PI_DTL( #{p_err_code,  jdbcType=VARCHAR, mode=OUT}
				                            , #{p_err_msg,   jdbcType=VARCHAR, mode=OUT}
				                            , #{companyCd,   jdbcType=VARCHAR , mode=IN}
				                            , #{divisionCd,  jdbcType=VARCHAR, mode=IN}			
				                            , 'TD'								  
				                            , #{userId,      jdbcType=VARCHAR, mode=IN} )}
