mapper.select:
SELECT A.* FROM (
SELECT MDM.COMPANY_CD , MDM.DIVISION_CD , MDM.DO_NO AS DELIVERY_REQUEST_NO , MDM.DO_NM AS DELIVERY_REQUEST_NM , MDM.PROCESS_CD , (SELECT MOM_COMMON_PKG.FN_GET_PROCESS_NAME(MDM.COMPANY_CD, MDM.DIVISION_CD, MDM.PROCESS_CD) FROM DUAL) AS PROCESS_NM , MDM.ISSUE_WAREHOUSE_CD , MWI.WAREHOUSE_NM AS ISSUE_WAREHOUSE_NM , MDM.REQUEST_WAREHOUSE_CD AS RECEIPT_WAREHOUSE_CD , MWR.WAREHOUSE_NM AS RECEIPT_WAREHOUSE_NM , TO_CHAR(MDM.ISSUE_DUE_DATE ,'YYYY-MM-DD') AS DELIVERY_DUE_DATE , MDM.DESCRIPTION , MDM.ISSUE_WH_USE_YN AS ISSUE_WAREHOUSE_USE_YN , MDM.REQUEST_WH_USE_YN AS REQUEST_WAREHOUSE_USE_YN , MDM.UPDATE_BY AS UPDATE_USER_NO , (SELECT MOM_COMMON_PKG.FN_GET_USER_NAME(MDM.COMPANY_CD, MDM.DIVISION_CD, MDM.UPDATE_BY) FROM DUAL ) AS UPDATE_USER_NM , TO_CHAR(MDM.UPDATE_DATE ,'YYYY-MM-DD HH24:MI:SS') AS UPDATE_DATE , TO_CHAR(MDM.CREATE_DATE ,'YYYY-MM-DD') AS CREATE_DATE , ROWIDTOCHAR(MDM.ROWID) AS KEY_ID , COUNT(*) OVER() AS TOTAL_COUNT , ROW_NUMBER() OVER(PARTITION BY MDM.COMPANY_CD,MDM.DIVISION_CD ORDER BY MDM.DO_NO) AS ROW_CNT FROM MOM_DO_MST MDM , MOM_WAREHOUSE MWI , MOM_WAREHOUSE MWR WHERE MDM.COMPANY_CD = MWI.COMPANY_CD AND MDM.DIVISION_CD = MWI.DIVISION_CD AND MDM.ISSUE_WAREHOUSE_CD = MWI.WAREHOUSE_CD AND MDM.COMPANY_CD = MWR.COMPANY_CD AND MDM.DIVISION_CD = MWR.DIVISION_CD AND MDM.REQUEST_WAREHOUSE_CD = MWR.WAREHOUSE_CD AND MDM.COMPANY_CD = #{companyCd, jdbcType=VARCHAR} AND MDM.DIVISION_CD = #{divisionCd, jdbcType=VARCHAR} AND MDM.STATE = 'DRF' AND MDM.WH_RECEIPT_YN = 'N' AND MDM.PROCESS_CD IN (SELECT MIM.PROCESS_CD FROM MOM_WH_IO_PROCESS_MAPPING MIM WHERE MIM.COMPANY_CD = MDM.COMPANY_CD AND MIM.DIVISION_CD = MDM.DIVISION_CD AND MIM.IO_RECEIPT_TYPE = 'WMI' AND MIM.USE_YN = 'Y') AND MDM.REQUEST_WAREHOUSE_CD = #{receiptWarehouseCd, jdbcType=VARCHAR} AND MDM.REQUEST_WH_USE_YN = 'Y'
AND UPPER(MDM.DO_NO) LIKE '%' || UPPER(#{deliveryRequestNo, jdbcType=VARCHAR}) || '%'
) A WHERE ROW_CNT BETWEEN #{startPage, jdbcType=INTEGER} AND #{endPage, jdbcType=INTEGER}

mapper.delete:
        DELETE FROM MOM_PROC_WH_RECEIPT_DTL


	        (       COMPANY_CD       = #{item.companyCd, jdbcType=VARCHAR}
	           AND  DIVISION_CD      = #{item.divisionCd, jdbcType=VARCHAR}
	           AND  IO_RECEIPT_TYPE  = 'WMI'
	           AND  CREATE_BY        = #{item.userId, jdbcType=VARCHAR}
	        )

insert.foreach:

	       INTO MOM_PROC_WH_RECEIPT_DTL( COMPANY_CD
                    		           , DIVISION_CD
                    		           , IO_RECEIPT_TYPE
			                           , RECEIPT_ID
			                           , RECEIPT_DETAIL_ID
			                           , REFERENCE_NO
	                                   , DESCRIPTION
			                           , DEL_YN
			                           , VAL_MSG
			                           , MULTI_MSG
			                           , CREATE_BY
			                           , CREATE_DATE )
           VALUES                      ( #{item.companyCd, jdbcType=VARCHAR}  
			                           , #{item.divisionCd, jdbcType=VARCHAR} 
			                           , 'WMI'     
			                   		   , #{item.receiptId, jdbcType=VARCHAR}
					                   , #{item.receiptDetailId, jdbcType=VARCHAR}
					                   , #{item.deliveryRequestNo, jdbcType=VARCHAR}
					                   , #{item.description, jdbcType=VARCHAR}
					                   , #{item.delYn, jdbcType=VARCHAR}
					                   , #{item.valMsg, jdbcType=VARCHAR}
					                   , #{item.multiMsg, jdbcType=VARCHAR}
					                   , #{item.userId, jdbcType=VARCHAR}
					                   , MOM_COMMON_PKG.FN_GET_LOCAL_TIME(#{item.companyCd, jdbcType=VARCHAR},#{item.divisionCd, jdbcType=VARCHAR})
					                   )

mapper.select:

{ CALL MOM_WMS_PKG.SP_UPSERT_WAREHOUSE_RECEIPT_DTL( #{p_err_code,  jdbcType=VARCHAR, mode=OUT}
                                                  , #{p_err_msg,   jdbcType=VARCHAR, mode=OUT}
                                                  , #{companyCd,   jdbcType=VARCHAR, mode=IN}
                                                  , #{divisionCd,  jdbcType=VARCHAR, mode=IN} 
                                                  , 'WMI'
                                                  , #{userId,      jdbcType=VARCHAR, mode=IN} )}
