mapper.select:
SELECT A.* FROM (
SELECT MPIM.COMPANY_CD , MPIM.DIVISION_CD , MPIM.INOUT_NO AS RECEIPT_NO , MPIM.TITLE AS RECEIPT_NM , MPIM.PROCESS_CD , (SELECT MOM_COMMON_PKG.FN_GET_PROCESS_NAME(MPIM.COMPANY_CD, MPIM.DIVISION_CD, MPIM.PROCESS_CD) FROM DUAL) AS PROCESS_NM , MPIM.VENDOR_CD , MP.PARTNER_NM AS CUSTOMER_NM , MPIM.WAREHOUSE_CD AS RECEIPT_WAREHOUSE_CD , MWH.WAREHOUSE_NM AS RECEIPT_WAREHOUSE_NM , TO_CHAR(MPIM.RECEIPT_DATE ,'YYYY-MM-DD') AS RECEIPT_DATE , MPIM.RECEIPT_WH_USE_YN , MPIM.UPDATE_BY AS UPDATE_USER_NO , (SELECT MOM_COMMON_PKG.FN_GET_USER_NAME(MPIM.COMPANY_CD, MPIM.DIVISION_CD, MPIM.UPDATE_BY) FROM DUAL ) AS UPDATE_USER_NM , TO_CHAR(MPIM.UPDATE_DATE ,'YYYY-MM-DD HH24:MI:SS') AS UPDATE_DATE , TO_CHAR(MPIM.CREATE_DATE ,'YYYY-MM-DD') AS CREATE_DATE , ROWIDTOCHAR(MPIM.ROWID) AS KEY_ID , COUNT(*) OVER() AS TOTAL_COUNT , ROW_NUMBER() OVER(PARTITION BY MPIM.COMPANY_CD,MPIM.DIVISION_CD ORDER BY MPIM.INOUT_NO) AS ROW_CNT FROM MOM_PURCHASE_INOUT_MST MPIM , MOM_PARTNER MP , MOM_WAREHOUSE MWH WHERE MPIM.COMPANY_CD = MP.COMPANY_CD AND MPIM.DIVISION_CD = MP.DIVISION_CD AND MPIM.VENDOR_CD = MP.PARTNER_CD AND MPIM.COMPANY_CD = MWH.COMPANY_CD AND MPIM.DIVISION_CD = MWH.DIVISION_CD AND MPIM.WAREHOUSE_CD = MWH.WAREHOUSE_CD AND MPIM.COMPANY_CD = #{companyCd, jdbcType=VARCHAR} AND MPIM.DIVISION_CD = #{divisionCd, jdbcType=VARCHAR} AND MPIM.STATE = 'PIGR' AND MPIM.WH_RECEIPT_YN = 'N' AND MPIM.PROCESS_CD IN (SELECT MIM.PROCESS_CD FROM MOM_WH_IO_PROCESS_MAPPING MIM WHERE MIM.COMPANY_CD = MPIM.COMPANY_CD AND MIM.DIVISION_CD = MPIM.DIVISION_CD AND MIM.IO_RECEIPT_TYPE = 'WIP' AND MIM.USE_YN = 'Y') AND MPIM.WAREHOUSE_CD = #{receiptWarehouseCd, jdbcType=VARCHAR} AND MDM.RECEIPT_WH_USE_YN = 'Y'
AND UPPER(MPIM.INOUT_NO ) LIKE '%' || UPPER(#{receiptNo, jdbcType=VARCHAR}) || '%'
) A WHERE ROW_CNT BETWEEN #{startPage, jdbcType=INTEGER} AND #{endPage, jdbcType=INTEGER}

mapper.delete:
        DELETE FROM MOM_PROC_WH_RECEIPT_DTL


	        (       COMPANY_CD       = #{item.companyCd, jdbcType=VARCHAR}
	           AND  DIVISION_CD      = #{item.divisionCd, jdbcType=VARCHAR}
	           AND  IO_RECEIPT_TYPE  = 'WIP'
	           AND  CREATE_BY        = #{item.userId, jdbcType=VARCHAR}
	        )

insert.foreach:

	       INTO MOM_PROC_WH_RECEIPT_DTL( COMPANY_CD
                    		           , DIVISION_CD
                    		           , IO_RECEIPT_TYPE
			                           , RECEIPT_ID
			                           , RECEIPT_DETAIL_ID
			                           , REFERENCE_NO
	                                   , DESCRIPTION
			                           , DEL_YN
			                           , VAL_MSG
			                           , MULTI_MSG
			                           , CREATE_BY
			                           , CREATE_DATE )
           VALUES                      ( #{item.companyCd, jdbcType=VARCHAR}  
			                           , #{item.divisionCd, jdbcType=VARCHAR} 
			                           , 'WIP'     
			                   		   , #{item.receiptId, jdbcType=VARCHAR}
					                   , #{item.receiptDetailId, jdbcType=VARCHAR}
					                   , #{item.deliveryRequestNo, jdbcType=VARCHAR}
					                   , #{item.description, jdbcType=VARCHAR}
					                   , #{item.delYn, jdbcType=VARCHAR}
					                   , #{item.valMsg, jdbcType=VARCHAR}
					                   , #{item.multiMsg, jdbcType=VARCHAR}
					                   , #{item.userId, jdbcType=VARCHAR}
					                   , MOM_COMMON_PKG.FN_GET_LOCAL_TIME(#{item.companyCd, jdbcType=VARCHAR},#{item.divisionCd, jdbcType=VARCHAR})
					                   )

mapper.select:

{ CALL MOM_WMS_PKG.SP_UPSERT_WAREHOUSE_RECEIPT_DTL( #{p_err_code,  jdbcType=VARCHAR, mode=OUT}
                                                  , #{p_err_msg,   jdbcType=VARCHAR, mode=OUT}
                                                  , #{companyCd,   jdbcType=VARCHAR, mode=IN}
                                                  , #{divisionCd,  jdbcType=VARCHAR, mode=IN} 
                                                  , 'WIP'
                                                  , #{userId,      jdbcType=VARCHAR, mode=IN} )}
