mapper.select:
SELECT A.* FROM (
SELECT MS.COMPANY_CD , MS.DIVISION_CD , MS.ITEM_STOCK_ID , MS.WAREHOUSE_CD , (SELECT MOM_COMMON_PKG.FN_GET_WAREHOUSE_NAME(MS.COMPANY_CD, MS.DIVISION_CD, MS.WAREHOUSE_CD) FROM DUAL ) AS WAREHOUSE_NM , MS.LOCATION_CD , (SELECT MOM_COMMON_PKG.FN_GET_LOCATION_NAME(MS.COMPANY_CD, MS.DIVISION_CD, MS.LOCATION_CD) FROM DUAL ) AS LOCATION_NM , MS.ITEM_ID , MS.LOT_ID , MS.STOCK_QTY , MS.ALLOCATE_QTY , MS.STOCK_QTY - NVL(MS.ALLOCATE_QTY,0) AS AVAILABLE_QTY , MS.STOCK_QTY - NVL(MS.ALLOCATE_QTY,0) AS DO_QTY , MI.ITEM_NM , MI.ITEM_SPEC , MI.ITEM_TYPE , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MS.COMPANY_CD, MS.DIVISION_CD, 'MD0002', MI.ITEM_TYPE, #{langCd, jdbcType=VARCHAR}) FROM DUAL) AS ITEM_TYPE_NM , MI.UOM , (SELECT MOM_COMMON_PKG.FN_GET_UNIT_NAME(MI.COMPANY_CD, MI.DIVISION_CD, MI.UOM) FROM DUAL) AS UOM_NM , MS.UPDATE_BY AS UPDATE_USER_NO , (SELECT MOM_COMMON_PKG.FN_GET_USER_NAME(MS.COMPANY_CD, MS.DIVISION_CD, MS.UPDATE_BY) FROM DUAL ) AS UPDATE_USER_NM , TO_CHAR(MS.UPDATE_DATE ,'YYYY-MM-DD HH24:MI:SS') AS UPDATE_DATE , MS.ITEM_STOCK_ID AS KEY_ID , COUNT(*) OVER() AS TOTAL_COUNT , ROW_NUMBER() OVER(PARTITION BY MS.COMPANY_CD,MS.DIVISION_CD ORDER BY MS.ITEM_STOCK_ID) AS ROW_CNT FROM MOM_STOCK MS , MOM_ITEM MI WHERE MS.COMPANY_CD = MI.COMPANY_CD AND MS.DIVISION_CD = MI.DIVISION_CD AND MS.ITEM_ID = MI.ITEM_ID AND MS.COMPANY_CD = #{companyCd, jdbcType=VARCHAR} AND MS.DIVISION_CD = #{divisionCd, jdbcType=VARCHAR} AND MS.WAREHOUSE_CD = #{warehouseCd, jdbcType=VARCHAR} AND MS.LOCATION_CD = #{locationCd, jdbcType=VARCHAR}
AND (UPPER(MI.ITEM_ID) LIKE '%' || UPPER(#{itemIdNm , jdbcType=VARCHAR}) || '%' OR UPPER(MI.ITEM_NM) LIKE '%' || UPPER(#{itemIdNm , jdbcType=VARCHAR}))
AND MS.STOCK_QTY - NVL(MS.ALLOCATE_QTY,0) > 0
) A WHERE ROW_CNT BETWEEN #{startPage, jdbcType=INTEGER} AND #{endPage, jdbcType=INTEGER}

mapper.delete:
        DELETE FROM MOM_PROC_LOCATION_DO_DTL


	        (       COMPANY_CD       = #{item.companyCd, jdbcType=VARCHAR}
	           AND  DIVISION_CD      = #{item.divisionCd, jdbcType=VARCHAR}
	           AND  CREATE_BY        = #{item.userId, jdbcType=VARCHAR}
	        )

insert.foreach:

	       INTO MOM_PROC_LOCATION_DO_DTL( COMPANY_CD
                    		            , DIVISION_CD
                    		            , LOCATION_DO_NO
                    		            , SEQ
                    		            , DELIVERY_ORDER_ID
                    		            , WAREHOUSE_CD
                    		            , LOCATION_CD
                    		            , LOT_ID
			                            , ITEM_ID
			                            , UOM
			                            , QTY
			                            , ITEM_STOCK_ID
			                            , DESCRIPTION
			                            , DEL_YN
			                            , VAL_MSG
			                            , MULTI_MSG
			                            , CREATE_BY
			                            , CREATE_DATE )
           VALUES                       ( #{item.companyCd, jdbcType=VARCHAR}  
			                            , #{item.divisionCd, jdbcType=VARCHAR} 
			                            , #{item.locationDoNo, jdbcType=VARCHAR}
			                            , #{item.seq, jdbcType=NUMERIC}
			                            , #{item.deliveryOrderId, jdbcType=VARCHAR}
			                            , #{item.warehouseCd, jdbcType=VARCHAR}
			                            , #{item.locationCd, jdbcType=VARCHAR}
			                            , #{item.lotId, jdbcType=VARCHAR}
			                   		    , #{item.itemId, jdbcType=VARCHAR}
			                   		    , #{item.uom, jdbcType=VARCHAR}
					                    , #{item.doQty, jdbcType=NUMERIC}
					                    , #{item.itemStockId, jdbcType=VARCHAR}
					                    , #{item.description, jdbcType=VARCHAR}
					                    , #{item.delYn, jdbcType=VARCHAR}
					                    , #{item.valMsg, jdbcType=VARCHAR}
					                    , #{item.multiMsg, jdbcType=VARCHAR}
					                    , #{item.userId, jdbcType=VARCHAR}
					                    , MOM_COMMON_PKG.FN_GET_LOCAL_TIME(#{item.companyCd, jdbcType=VARCHAR},#{item.divisionCd, jdbcType=VARCHAR})
					                    )

mapper.select:

{ CALL MOM_WMS_PKG.SP_UPSERT_LOCATION_DO_DTL( #{p_err_code,  jdbcType=VARCHAR, mode=OUT}
                                            , #{p_err_msg,   jdbcType=VARCHAR, mode=OUT}
                                            , #{companyCd,   jdbcType=VARCHAR, mode=IN}
                                            , #{divisionCd,  jdbcType=VARCHAR, mode=IN} 
                                            , #{userId,      jdbcType=VARCHAR, mode=IN} )}
