mapper.select:
SELECT A.* FROM (
WITH C_STOCK AS ( SELECT MLS.COMPANY_CD , MLS.DIVISION_CD , MLS.ITEM_STOCK_ID , MLS.WAREHOUSE_CD , MLS.LOCATION_CD , ML.LOCATION_NM , MLS.LOGISTICS_UNIT_CD , MLS.ITEM_ID , MLS.LOT_ID , MLS.STOCK_QTY , MLS.ALLOCATE_QTY , MLS.STOCK_QTY - NVL(MLS.ALLOCATE_QTY,0) AS AVAILABLE_QTY , MLS.UPDATE_BY AS UPDATE_USER_NO ,(SELECT MOM_COMMON_PKG.FN_GET_USER_NAME(MLS.COMPANY_CD, MLS.DIVISION_CD, MLS.UPDATE_BY) FROM DUAL ) AS UPDATE_USER_NM , TO_CHAR(MLS.UPDATE_DATE ,'YYYY-MM-DD HH24:MI:SS') AS UPDATE_DATE FROM MOM_LOCATION_ITEM_STOCK MLS , MOM_LOCATION ML WHERE MLS.COMPANY_CD = ML.COMPANY_CD AND MLS.DIVISION_CD = ML.DIVISION_CD AND MLS.LOCATION_CD = ML.LOCATION_CD AND MLS.COMPANY_CD = #{companyCd, jdbcType=VARCHAR} AND MLS.DIVISION_CD = #{divisionCd, jdbcType=VARCHAR} AND ML.LOGISTIC_USE_CD = #{zone, jdbcType=VARCHAR} ) SELECT MLS.COMPANY_CD , MLS.DIVISION_CD , MLS.ITEM_STOCK_ID , MLS.WAREHOUSE_CD , MWH.WAREHOUSE_NM , MLS.LOCATION_CD , MLS.LOCATION_NM , MLS.LOGISTICS_UNIT_CD , MLS.ITEM_ID , MLS.LOT_ID , MLS.STOCK_QTY , MLS.ALLOCATE_QTY , MLS.AVAILABLE_QTY , MI.ITEM_NM , MI.ITEM_SPEC , MI.ITEM_TYPE , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MLS.COMPANY_CD, MLS.DIVISION_CD, 'MD0002', MI.ITEM_TYPE, #{langCd, jdbcType=VARCHAR}) FROM DUAL) AS ITEM_TYPE_NM , MI.UOM , (SELECT MOM_COMMON_PKG.FN_GET_UNIT_NAME(MI.COMPANY_CD, MI.DIVISION_CD, MI.UOM) FROM DUAL) AS UOM_NM , MLS.UPDATE_USER_NO , MLS.UPDATE_USER_NM , MLS.UPDATE_DATE , MLS.ITEM_STOCK_ID AS KEY_ID , COUNT(*) OVER() AS TOTAL_COUNT , ROW_NUMBER() OVER(PARTITION BY MLS.COMPANY_CD,MLS.DIVISION_CD ORDER BY MLS.ITEM_STOCK_ID) AS ROW_CNT FROM C_STOCK MLS , MOM_WAREHOUSE MWH , MOM_ITEM MI WHERE MLS.COMPANY_CD = MWH.COMPANY_CD AND MLS.DIVISION_CD = MWH.DIVISION_CD AND MLS.WAREHOUSE_CD = MWH.WAREHOUSE_CD AND MLS.COMPANY_CD = MI.COMPANY_CD AND MLS.DIVISION_CD = MI.DIVISION_CD AND MLS.ITEM_ID = MI.ITEM_ID AND MLS.AVAILABLE_QTY > 0
) A WHERE ROW_CNT BETWEEN #{startPage, jdbcType=INTEGER} AND #{endPage, jdbcType=INTEGER}

mapper.delete:
        DELETE FROM MOM_PROC_LOCATION_DO_DTL


	        (       COMPANY_CD       = #{item.companyCd, jdbcType=VARCHAR}
	           AND  DIVISION_CD      = #{item.divisionCd, jdbcType=VARCHAR}
	           AND  CREATE_BY        = #{item.userId, jdbcType=VARCHAR}
	        )

insert.foreach:

	       INTO MOM_PROC_LOCATION_DO_DTL( COMPANY_CD
                    		            , DIVISION_CD
                    		            , LOCATION_DO_NO
                    		            , SEQ
                    		            , DELIVERY_ORDER_ID
                    		            , LOCATION_CD
                    		            , LOGISTICS_UNIT_CD
                    		            , LOT_ID
			                            , ITEM_ID
			                            , QTY
			                            , ITEM_STOCK_ID
			                            , DESCRIPTION
			                            , DEL_YN
			                            , VAL_MSG
			                            , MULTI_MSG
			                            , CREATE_BY
			                            , CREATE_DATE )
           VALUES                       ( #{item.companyCd, jdbcType=VARCHAR}  
			                            , #{item.divisionCd, jdbcType=VARCHAR} 
			                            , #{item.locationDoNo, jdbcType=VARCHAR}
			                            , #{item.seq, jdbcType=NUMERIC}
			                            , #{item.deliveryOrderId, jdbcType=VARCHAR}
			                            , #{item.locationCd, jdbcType=VARCHAR}
			                            , #{item.logisticsUnitCd, jdbcType=VARCHAR}
			                            , #{item.lotId, jdbcType=VARCHAR}
			                   		    , #{item.itemId, jdbcType=VARCHAR}
					                    , #{item.availableQty, jdbcType=NUMERIC}
					                    , #{item.itemStockId, jdbcType=VARCHAR}
					                    , #{item.description, jdbcType=VARCHAR}
					                    , #{item.delYn, jdbcType=VARCHAR}
					                    , #{item.valMsg, jdbcType=VARCHAR}
					                    , #{item.multiMsg, jdbcType=VARCHAR}
					                    , #{item.userId, jdbcType=VARCHAR}
					                    , MOM_COMMON_PKG.FN_GET_LOCAL_TIME(#{item.companyCd, jdbcType=VARCHAR},#{item.divisionCd, jdbcType=VARCHAR})
					                    )

mapper.select:

{ CALL MOM_WMS_PKG.SP_UPSERT_LOCATION_DO_DTL( #{p_err_code,  jdbcType=VARCHAR, mode=OUT}
                                            , #{p_err_msg,   jdbcType=VARCHAR, mode=OUT}
                                            , #{companyCd,   jdbcType=VARCHAR, mode=IN}
                                            , #{divisionCd,  jdbcType=VARCHAR, mode=IN} 
                                            , #{userId,      jdbcType=VARCHAR, mode=IN} )}
