mapper.select:
SELECT MDM.COMPANY_CD , MDM.DIVISION_CD , MDM.PROCESS_CD , (SELECT MOM_COMMON_PKG.FN_GET_PROCESS_NAME(MDM.COMPANY_CD, MDM.DIVISION_CD, MDM.PROCESS_CD) FROM DUAL) AS PROCESS_NM , MDM.REQUEST_WAREHOUSE_CD , (SELECT MOM_COMMON_PKG.FN_GET_WAREHOUSE_NAME(MDM.COMPANY_CD, MDM.DIVISION_CD, MDM.REQUEST_WAREHOUSE_CD) FROM DUAL) AS REQUEST_WAREHOUSE_NM , MDM.ISSUE_WAREHOUSE_CD , (SELECT MOM_COMMON_PKG.FN_GET_WAREHOUSE_NAME(MDM.COMPANY_CD, MDM.DIVISION_CD, MDM.ISSUE_WAREHOUSE_CD) FROM DUAL) AS ISSUE_WAREHOUSE_NM , MDD.ITEM_ID , MI.ITEM_NM , MI.ITEM_SPEC , MI.ITEM_TYPE , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MI.COMPANY_CD, MI.DIVISION_CD, 'MD0002', MI.ITEM_TYPE, 'KR') FROM DUAL) AS ITEM_TYPE_NM , MDD.QTY AS DO_QTY , MII.LOT_ID , TO_CHAR(MII.IO_DATE ,'YYYY-MM-DD') AS RECEIPT_DATE , MII.QTY AS RECEIPT_QTY , MII.UOM , (SELECT MOM_COMMON_PKG.FN_GET_UNIT_NAME(MII.COMPANY_CD, MII.DIVISION_CD, MII.UOM ) FROM DUAL) AS UOM_NM , MII.IO_TYPE , (SELECT MOM_COMMON_PKG.FN_GET_IO_TYPE_NAME(MII.COMPANY_CD, MII.DIVISION_CD, MII.IO_TYPE) FROM DUAL) AS IO_TYPE_NM , (SELECT MOM_STOCK_PKG.FN_GET_ITEM_WH_STOCK_LOT_QTY( MDM.COMPANY_CD, MDM.DIVISION_CD, MDD.ITEM_ID, MDM.REQUEST_WAREHOUSE_CD, MII.LOT_ID) FROM DUAL) AS AVAILABLE_QTY , (SELECT MOM_STOCK_PKG.FN_GET_ITEM_AVAILABLE_LOT_QTY( MDM.COMPANY_CD, MDM.DIVISION_CD, MDD.ITEM_ID, MDM.REQUEST_WAREHOUSE_CD, MII.LOT_ID) FROM DUAL) AS CURRENT_STOCK_QTY , MDD.REFERENCE_NO , MDD.REFERENCE_DTL_NO , TO_CHAR(MII.CREATE_DATE , 'YYYY-MM-DD HH24:MI:SS') AS CREATE_DATE , MII.CREATE_BY AS CREATE_USER_NO , (SELECT MOM_COMMON_PKG.FN_GET_USER_NAME(MII.COMPANY_CD, MII.DIVISION_CD, MII.CREATE_BY) FROM DUAL ) AS CREATE_USER_NM , MDM.DESCRIPTION , ROWIDTOCHAR(MII.ROWID) AS KEY_ID FROM MOM_DO_MST MDM , MOM_DO_DTL MDD , MOM_ITEM MI , MOM_DO_INOUT_MAPPING MIM , MOM_ITEM_INOUT MII WHERE MDM.COMPANY_CD = MDD.COMPANY_CD AND MDM.DIVISION_CD = MDD.DIVISION_CD AND MDM.DO_NO = MDD.DO_NO AND MDD.COMPANY_CD = MI.COMPANY_CD AND MDD.DIVISION_CD = MI.DIVISION_CD AND MDD.ITEM_ID = MI.ITEM_ID AND MDD.COMPANY_CD = MIM.COMPANY_CD AND MDD.DIVISION_CD = MIM.DIVISION_CD AND MDD.DELIVERY_ORDER_ID = MIM.DELIVERY_ORDER_ID AND MIM.COMPANY_CD = MII.COMPANY_CD AND MIM.DIVISION_CD = MII.DIVISION_CD AND MIM.ITEM_INOUT_ID = MII.ITEM_INOUT_ID AND MDM.COMPANY_CD = #{companyCd, jdbcType=VARCHAR} AND MDM.DIVISION_CD = #{divisionCd, jdbcType=VARCHAR} AND MDM.PROCESS_CD = 'MI' AND MDM.STATE = 'DOE'
AND (UPPER(MDM.DO_NO) LIKE '%' || UPPER(#{issueRequestNo, jdbcType=VARCHAR}) || '%')
AND MDM.REQUEST_WAREHOUSE_CD = #{requestWarehouseCd, jdbcType=VARCHAR}
AND MDM.ISSUE_WAREHOUSE_CD = #{issueWarehouseCd, jdbcType=VARCHAR}

mapper.delete:
DELETE FROM MOM_PROC_TAX_INVOICE_DTL 


    (       COMPANY_CD     = #{item.companyCd, jdbcType=VARCHAR}
       AND  DIVISION_CD    = #{item.divisionCd, jdbcType=VARCHAR}
       AND  PUR_SAL_TYPE = 'O'
       AND  CREATE_BY      = #{item.userId, jdbcType=VARCHAR}
    )

insert.foreach:

        INTO MOM_PROC_TAX_INVOICE_DTL( COMPANY_CD
									 , DIVISION_CD
									 , PUR_SAL_TYPE
									 , VENDOR_CD
									 , INVOICE_DATE
									 , TAX_TYPE
									 , REFERENCE_NO
									 , REFERENCE_DTL_NO
									 , ITEM_ID
									 , IO_DATE
									 , QTY
									 , LOCAL_AMOUNT
									 , TAX_RATE
									 , TAX_AMOUNT
									 , DESCRIPTION
									 , ETC_YN
									 , ACTION_TYPE
									 , VAL_MSG
									 , MULTI_MSG
									 , CREATE_BY
									 , CREATE_DATE
	                                 )
        VALUES                       ( #{item.companyCd, jdbcType=VARCHAR}
					                 , #{item.divisionCd, jdbcType=VARCHAR}
					                 , 'O'
					                 , #{item.partnerCd, jdbcType=VARCHAR}
					                 , TO_DATE(#{item.invoiceDate, jdbcType=VARCHAR},'YYYY-MM-DD')
					                 , #{item.taxType, jdbcType=VARCHAR}
					                 , #{item.referenceNo, jdbcType=VARCHAR}
					                 , #{item.referenceDtlNo, jdbcType=VARCHAR}
					                 , #{item.itemId, jdbcType=VARCHAR}
					                 , TO_DATE(#{item.receiptDate, jdbcType=VARCHAR},'YYYY-MM-DD') 
					                 , #{item.receiptQty, jdbcType=NUMERIC}
					                 , #{item.localAmount, jdbcType=NUMERIC}
					                 , #{item.taxRate, jdbcType=NUMERIC}
					                 , #{item.taxAmount, jdbcType=NUMERIC}
					                 , #{item.description, jdbcType=VARCHAR}
					                 , 'N'
					                 , #{item.actionType, jdbcType=VARCHAR}
					                 , #{item.valMsg, jdbcType=VARCHAR}
					                 , #{item.multiMsg, jdbcType=VARCHAR}
					                 , #{item.userId, jdbcType=VARCHAR}
					                 , MOM_COMMON_PKG.FN_GET_LOCAL_TIME(#{item.companyCd, jdbcType=VARCHAR},#{item.divisionCd, jdbcType=VARCHAR})
					                 )

mapper.select:

        { CALL MOM_CLOSED_PKG.SP_UPSERT_TAX_INVOICE_DTL( #{p_err_code,  jdbcType=VARCHAR, mode=OUT}
			                                           , #{p_err_msg,   jdbcType=VARCHAR, mode=OUT}
			                                           , #{companyCd,   jdbcType=VARCHAR, mode=IN}
			                                           , #{divisionCd,  jdbcType=VARCHAR, mode=IN}
			                                           , 'O'											  
			                                           , #{userId,      jdbcType=VARCHAR, mode=IN} )}
