mapper.select:
SELECT MDM.COMPANY_CD , MDM.DIVISION_CD , MDM.DO_NO AS ISSUE_REQUEST_NO , MDM.DO_NM AS ISSUE_REQUEST_NM , MDD.SEQ , MDD.DELIVERY_ORDER_ID , MDM.PROCESS_CD , (SELECT MOM_COMMON_PKG.FN_GET_PROCESS_NAME(MDM.COMPANY_CD, MDM.DIVISION_CD, MDM.PROCESS_CD) FROM DUAL) AS PROCESS_NM , MDM.PARTNER_CD , (SELECT MOM_COMMON_PKG.FN_GET_PARTNER_NAME(MDM.COMPANY_CD, MDM.DIVISION_CD, MDM.PARTNER_CD) FROM DUAL ) AS PARTNER_NM , MDM.REQUEST_WAREHOUSE_CD , (SELECT MOM_COMMON_PKG.FN_GET_WAREHOUSE_NAME(MDM.COMPANY_CD, MDM.DIVISION_CD, MDM.REQUEST_WAREHOUSE_CD) FROM DUAL) AS REQUEST_WAREHOUSE_NM , MDM.ISSUE_WAREHOUSE_CD , (SELECT MOM_COMMON_PKG.FN_GET_WAREHOUSE_NAME(MDM.COMPANY_CD, MDM.DIVISION_CD, MDM.ISSUE_WAREHOUSE_CD) FROM DUAL) AS ISSUE_WAREHOUSE_NM , MDD.ITEM_ID , MI.ITEM_NM , MI.ITEM_SPEC , MI.ITEM_TYPE , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MI.COMPANY_CD, MI.DIVISION_CD, 'MD0002', MI.ITEM_TYPE, 'KR') FROM DUAL) AS ITEM_TYPE_NM , MII.LOT_ID , TO_CHAR(MII.IO_DATE ,'YYYY-MM-DD') AS ISSUE_DATE , MII.QTY AS ISSUE_QTY , MII.UOM , '' AS RETURN_QTY , (SELECT MOM_COMMON_PKG.FN_GET_UNIT_NAME(MII.COMPANY_CD, MII.DIVISION_CD, MII.UOM ) FROM DUAL) AS UOM_NM , MII.IO_TYPE , (SELECT MOM_COMMON_PKG.FN_GET_IO_TYPE_NAME(MII.COMPANY_CD, MII.DIVISION_CD, MII.IO_TYPE) FROM DUAL) AS IO_TYPE_NM , (SELECT MOM_STOCK_PKG.FN_GET_ITEM_WH_STOCK_LOT_QTY( MDM.COMPANY_CD, MDM.DIVISION_CD, MDD.ITEM_ID, MDM.REQUEST_WAREHOUSE_CD, MII.LOT_ID) FROM DUAL) AS AVAILABLE_QTY , (SELECT MOM_STOCK_PKG.FN_GET_ITEM_AVAILABLE_LOT_QTY( MDM.COMPANY_CD, MDM.DIVISION_CD, MDD.ITEM_ID, MDM.REQUEST_WAREHOUSE_CD, MII.LOT_ID) FROM DUAL) AS CURRENT_STOCK_QTY , MDD.REFERENCE_NO , MDD.REFERENCE_DTL_NO , TO_CHAR(MII.CREATE_DATE , 'YYYY-MM-DD HH24:MI:SS') AS CREATE_DATE , MII.CREATE_BY AS CREATE_USER_NO , (SELECT MOM_COMMON_PKG.FN_GET_USER_NAME(MII.COMPANY_CD, MII.DIVISION_CD, MII.CREATE_BY) FROM DUAL ) AS CREATE_USER_NM , MDM.DESCRIPTION , ROWIDTOCHAR(MII.ROWID) AS KEY_ID FROM MOM_DO_MST MDM , MOM_DO_DTL MDD , MOM_ITEM MI , MOM_DO_INOUT_MAPPING MIM , MOM_ITEM_INOUT MII WHERE MDM.COMPANY_CD = MDD.COMPANY_CD AND MDM.DIVISION_CD = MDD.DIVISION_CD AND MDM.DO_NO = MDD.DO_NO AND MDD.COMPANY_CD = MI.COMPANY_CD AND MDD.DIVISION_CD = MI.DIVISION_CD AND MDD.ITEM_ID = MI.ITEM_ID AND MDD.COMPANY_CD = MIM.COMPANY_CD AND MDD.DIVISION_CD = MIM.DIVISION_CD AND MDD.DELIVERY_ORDER_ID = MIM.DELIVERY_ORDER_ID AND MIM.COMPANY_CD = MII.COMPANY_CD AND MIM.DIVISION_CD = MII.DIVISION_CD AND MIM.ITEM_INOUT_ID = MII.ITEM_INOUT_ID AND MDM.COMPANY_CD = #{companyCd, jdbcType=VARCHAR} AND MDM.DIVISION_CD = #{divisionCd, jdbcType=VARCHAR} AND MDM.PROCESS_CD = 'NI' AND MDM.STATE = 'DOE'
AND MDM.REQUEST_WAREHOUSE_CD = #{requestWarehouseCd, jdbcType=VARCHAR}
AND MDM.ISSUE_WAREHOUSE_CD = #{issueWarehouseCd, jdbcType=VARCHAR}

mapper.delete:
DELETE FROM MOM_PROC_DO_DTL


    (       COMPANY_CD  = #{item.companyCd, jdbcType=VARCHAR}
       AND  DIVISION_CD = #{item.divisionCd, jdbcType=VARCHAR}
       AND  PROCESS_CD  = 'NIR'
       AND  CREATE_BY   = #{item.userId, jdbcType=VARCHAR}
    )

insert.foreach:

        INTO MOM_PROC_DO_DTL( COMPANY_CD
                   		    , DIVISION_CD
                   		    , PROCESS_CD
		                    , DO_NO
		                    , SEQ
		                    , DELIVERY_ORDER_ID
		                    , QTY
		                    , ITEM_ID
		                    , DO_UOM
		                    , CONVERSION_UOM
		                    , IO_TYPE
		                    , REFERENCE_NO
		                    , REFERENCE_DTL_NO
		                    , LOT_ID
		                    , DESCRIPTION
		                    , DEL_YN
		                    , VAL_MSG
		                    , MULTI_MSG
		                    , CREATE_BY
		                    , CREATE_DATE
                            )
        VALUES             ( #{item.companyCd, jdbcType=VARCHAR}  
		                   , #{item.divisionCd, jdbcType=VARCHAR} 
		                   , 'NIR'        
		                   , #{item.issueRequestNo, jdbcType=VARCHAR}
		                   , #{item.seq, jdbcType=NUMERIC}
		                   , #{item.deliveryOrderId, jdbcType=VARCHAR}
		                   , #{item.returnQty, jdbcType=NUMERIC}
		                   , #{item.itemId, jdbcType=VARCHAR}
		                   , #{item.uom, jdbcType=VARCHAR}
		                   , #{item.uom, jdbcType=VARCHAR}
		                   , #{item.ioType, jdbcType=VARCHAR}
		                   , #{item.referenceNo, jdbcType=VARCHAR}
		                   , #{item.referenceDtlNo, jdbcType=VARCHAR}
		                   , #{item.lotId, jdbcType=VARCHAR}
		                   , #{item.description, jdbcType=VARCHAR}
		                   , #{item.delYn, jdbcType=VARCHAR}
		                   , #{item.valMsg, jdbcType=VARCHAR}
		                   , #{item.multiMsg, jdbcType=VARCHAR}
		                   , #{item.userId, jdbcType=VARCHAR}
		                   , MOM_COMMON_PKG.FN_GET_LOCAL_TIME(#{item.companyCd, jdbcType=VARCHAR},#{item.divisionCd, jdbcType=VARCHAR})
		                   )

mapper.select:

{ CALL MOM_STOCK_PKG.SP_UPSERT_DO_DTL( #{p_err_code,  jdbcType=VARCHAR, mode=OUT}
                                     , #{p_err_msg,   jdbcType=VARCHAR, mode=OUT}
                                     , #{companyCd,   jdbcType=VARCHAR, mode=IN}
                                     , #{divisionCd,  jdbcType=VARCHAR, mode=IN} 
                                     , 'NIR'
                                     , #{userId,      jdbcType=VARCHAR, mode=IN} )}
