mapper.delete:
	DELETE FROM MOM_PROC_PI_DTL


            (    COMPANY_CD  = #{item.companyCd, jdbcType=VARCHAR}
            AND  DIVISION_CD = #{item.divisionCd, jdbcType=VARCHAR}
            AND  PROCESS_CD  = 'OP' 
            AND  CREATE_BY   = #{item.userId, jdbcType=VARCHAR}
            )

insert.foreach:

        INTO MOM_PROC_PI_DTL( COMPANY_CD
			                , DIVISION_CD
			                , PROCESS_CD
			                , INOUT_NO
			                , PURCHASE_INOUT_ID
			                , IO_TYPE
			                , ITEM_ID
			                , INOUT_UOM
			                , INOUT_QTY
			                , GOOD_QTY
			                , DEFECT_QTY
			                , CURRENCY_CD
			                , EXCHANGE_RATE
			                , UNIT_PRICE
			                , PO_OVER_RECEIPT_RATE
			                , REFERENCE_NO
			                , REFERENCE_DTL_NO
			                , DESCRIPTION
			                , DEL_YN
			                , VAL_MSG
			                , MULTI_MSG
			                , CREATE_BY
			                , CREATE_DATE )
        VALUES              ( #{item.companyCd, jdbcType=VARCHAR}
			                , #{item.divisionCd, jdbcType=VARCHAR}
			                , 'OP' 
			                , #{item.inoutNo, jdbcType=VARCHAR}
			                , #{item.purchaseInoutId, jdbcType=VARCHAR}
			                , #{item.ioType, jdbcType=VARCHAR}
			                , #{item.itemId, jdbcType=VARCHAR}
			                , #{item.purchaseUom, jdbcType=VARCHAR}
			                , #{item.goodQty, jdbcType=NUMERIC}
			                , #{item.goodQty, jdbcType=NUMERIC}
			                , #{item.defectQty, jdbcType=NUMERIC}
			                , #{item.currencyCd, jdbcType=VARCHAR}
			                , #{item.exchangeRate, jdbcType=NUMERIC}
			                , #{item.unitPrice, jdbcType=NUMERIC}
			                , #{item.poOverReceiptRate, jdbcType=NUMERIC}	
			                , #{item.poNo, jdbcType=VARCHAR}
			                , #{item.purchaseOrderId, jdbcType=VARCHAR}
			                , #{item.description, jdbcType=VARCHAR}
			                , #{item.delYn, jdbcType=VARCHAR}
			                , #{item.valMsg, jdbcType=VARCHAR}
			                , #{item.multiMsg, jdbcType=VARCHAR}
			                , #{item.userId, jdbcType=VARCHAR}
			                , MOM_COMMON_PKG.FN_GET_LOCAL_TIME(#{item.companyCd, jdbcType=VARCHAR},#{item.divisionCd, jdbcType=VARCHAR})
                            )

mapper.select:

    { CALL MOM_MATERIAL_PKG.SP_UPSERT_PI_DTL( #{p_err_code,  jdbcType=VARCHAR, mode=OUT}
				                            , #{p_err_msg,   jdbcType=VARCHAR, mode=OUT}
				                            , #{companyCd,   jdbcType=VARCHAR , mode=IN}
				                            , #{divisionCd,  jdbcType=VARCHAR, mode=IN}			
				                            , 'OP'								  
				                            , #{userId,      jdbcType=VARCHAR, mode=IN} )}
