mapper.select:
SELECT A.* FROM (
SELECT CPM.COMPANY_CD , CPM.DIVISION_CD , CPM.ITEM_ID , MI.ITEM_NM , MI.ITEM_SPEC , MI.ITEM_TYPE , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MI.COMPANY_CD, MI.DIVISION_CD, 'MD0002', MI.ITEM_TYPE, #{langCd, jdbcType=VARCHAR}) FROM DUAL) AS ITEM_TYPE_NM , CPM.PLAN_DATE , TO_CHAR(CPM.PLAN_DATE ,'YYYY-MM-DD') AS REQUEST_DUE_DATE , CPM.QTY , MI.UOM , (SELECT MOM_COMMON_PKG.FN_GET_UNIT_NAME(MI.COMPANY_CD, MI.DIVISION_CD, MI.UOM) FROM DUAL) AS UOM_NM , ROUND((CPM.QTY/MI.PURCHASE_CONVERSION_RATE),2) AS PURCHASE_REQUEST_QTY , MI.PURCHASE_UOM , (SELECT MOM_COMMON_PKG.FN_GET_UNIT_NAME(MI.COMPANY_CD, MI.DIVISION_CD, MI.MFG_UOM) FROM DUAL) AS PURCHASE_UOM_NM , MI.UOM , MI.STORAGE_WAREHOUSE_CD AS RECEIPT_WAREHOUSE_CD , (SELECT MOM_COMMON_PKG.FN_GET_WAREHOUSE_NAME(CPM.COMPANY_CD,CPM.DIVISION_CD,MI.STORAGE_WAREHOUSE_CD) FROM DUAL) AS RECEIPT_WAREHOUSE_NM , MI.STOCK_TYPE , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MI.COMPANY_CD, MI.DIVISION_CD, 'MD0048', MI.STOCK_TYPE, #{langCd, jdbcType=VARCHAR}) FROM DUAL) AS STOCK_TYPE_NM , MI.IQC_FLAG , MI.PO_OVER_RECEIPT_RATE , MI.LOT_MANAGEMENT_YN , MI.LOT_MARK AS LOT_RULE , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MI.COMPANY_CD, MI.DIVISION_CD, 'MD0052', MI.LOT_MARK, #{langCd, jdbcType=VARCHAR}) FROM DUAL) AS LOT_RULE_NM , ROWIDTOCHAR(CPM.ROWID) AS KEY_ID , COUNT(*) OVER() AS TOTAL_COUNT , ROW_NUMBER() OVER(PARTITION BY CPM.COMPANY_CD, CPM.DIVISION_CD ORDER BY CPM.PLAN_DATE, CPM.ITEM_ID) AS ROW_CNT FROM CAL_EXP_PSI_MOM CPM , MOM_ITEM MI WHERE CPM.COMPANY_CD = MI.COMPANY_CD AND CPM.DIVISION_CD = MI.DIVISION_CD AND CPM.ITEM_ID = MI.ITEM_ID AND CPM.COMPANY_CD = #{companyCd, jdbcType=VARCHAR} AND CPM.DIVISION_CD = #{divisionCd, jdbcType=VARCHAR} AND CPM.PLAN_TYPE = 'PRE' AND CPM.SEQ = 5 AND CPM.MRP_APPLY_YN = 'N' AND CPM.PLAN_DATE BETWEEN TO_DATE(#{planDateSD, jdbcType=VARCHAR}, 'YYYY-MM-DD') AND TO_DATE(#{planDateED, jdbcType=VARCHAR}, 'YYYY-MM-DD') + 0.99999 ORDER BY CPM.PLAN_DATE , CPM.ITEM_ID , CPM.QTY
) A WHERE ROW_CNT BETWEEN #{startPage, jdbcType=INTEGER} AND #{endPage, jdbcType=INTEGER}

mapper.delete:
        DELETE FROM MOM_PROC_PR_DTL


	        (       COMPANY_CD  = #{item.companyCd, jdbcType=VARCHAR}
               AND  DIVISION_CD = #{item.divisionCd, jdbcType=VARCHAR}
               AND  PR_TYPE   = '30' 
               AND  CREATE_BY   = #{item.userId, jdbcType=VARCHAR}
	        )

insert.foreach:

	       INTO MOM_PROC_PR_DTL( COMPANY_CD
		                       , DIVISION_CD
		                       , PR_TYPE
		                       , PR_NO
		                       , PR_SEQ
		                       , PURCHASE_REQUEST_ID
		                       , ITEM_ID
		                       , DUE_DATE
		                       , REQUEST_QTY
		                       , LOCAL_AMOUNT
		                       , DESCRIPTION
		                       , DEL_YN
		                       , VAL_MSG
		                       , MULTI_MSG
		                       , CREATE_BY
		                       , CREATE_DATE )
        VALUES                 ( #{item.companyCd, jdbcType=VARCHAR}
		                       , #{item.divisionCd, jdbcType=VARCHAR}
		                       , '30'
		                       , #{item.prNo, jdbcType=VARCHAR}
		                       , #{item.prSeq, jdbcType=NUMERIC}
		                       , #{item.purchaseRequestId, jdbcType=VARCHAR}
		                       , #{item.itemId, jdbcType=VARCHAR}
		                       , TO_DATE(#{item.requestDueDate, jdbcType=VARCHAR},'YYYY-MM-DD')
		                       , #{item.purchaseRequestQty, jdbcType=NUMERIC}
		                       , #{item.localAmount, jdbcType=NUMERIC}
		                       , #{item.description, jdbcType=VARCHAR}
		                       , #{item.delYn, jdbcType=VARCHAR}
		                       , #{item.valMsg, jdbcType=VARCHAR}
		                       , #{item.multiMsg, jdbcType=VARCHAR}
		                       , #{item.userId, jdbcType=VARCHAR}
		                       , MOM_COMMON_PKG.FN_GET_LOCAL_TIME(#{item.companyCd, jdbcType=VARCHAR},#{item.divisionCd, jdbcType=VARCHAR})
		                       )

mapper.select:

{ CALL MOM_MATERIAL_PKG.SP_UPSERT_PR_DTL( #{p_err_code,  jdbcType=VARCHAR, mode=OUT}
                                        , #{p_err_msg,   jdbcType=VARCHAR, mode=OUT}
                                        , #{companyCd,   jdbcType=VARCHAR, mode=IN}
                                        , #{divisionCd,  jdbcType=VARCHAR, mode=IN}   
                                        , '30'
                                        , 'P'                    
                                        , #{userId,      jdbcType=VARCHAR, mode=IN} )}
