mapper.select:
	SELECT  CODE
         ,  NVL((SELECT MOM_COMMON_PKG.FN_GET_MULTI_LANGUAGE(#{companyCd, jdbcType=VARCHAR},#{divisionCd, jdbcType=VARCHAR},#{langCd, jdbcType=VARCHAR},'CODE',#{groupCd, jdbcType=VARCHAR}|| '_' ||CODE)
	             FROM   DUAL ),CODE ||'('|| CODE_NM ||')') AS LABEL
	FROM    MOM_CODE
	WHERE   DIVISION_CD = #{divisionCd, jdbcType=VARCHAR}
	AND     COMPANY_CD  = #{companyCd, jdbcType=VARCHAR}
	AND     GROUP_CD    = #{groupCd, jdbcType=VARCHAR}
	AND     USE_YN      = 'Y'

    AND     CODE = #{processTypeId, jdbcType=VARCHAR} 


    AND     ATTRIBUTE1 = #{attribute1, jdbcType=VARCHAR} 


    AND     ATTRIBUTE2 = #{attribute2, jdbcType=VARCHAR} 

	ORDER BY SORT_NO,LABEL


SELECT GRID_ID        AS CODE 
     , GRID_ID     ||'('|| GRID_NM      ||')' AS LABEL
FROM   MOM_GRID   
WHERE  COMPANY_CD  = #{companyCd, jdbcType=VARCHAR}
AND    DIVISION_CD = #{divisionCd, jdbcType=VARCHAR} 
AND    MENU_ID     = #{menuId, jdbcType=VARCHAR} 
ORDER BY GRID_ID

	SELECT AUTH_GROUP_CD AS CODE 
	     , AUTH_GROUP_CD ||'('|| AUTH_GROUP_NM ||')' AS LABEL
    FROM   MOM_AUTH_GROUP
    WHERE  COMPANY_CD  = #{companyCd, jdbcType=VARCHAR}
	AND    DIVISION_CD = #{divisionCd, jdbcType=VARCHAR} 
    ORDER BY LABEL

SELECT MC.COMPANY_CD AS CODE , MC.COMPANY_NM AS LABEL FROM MOM_COMPANY MC WHERE MC.USE_YN = 'Y'
AND MD.COMPANY_CD = #{companyCd, jdbcType=VARCHAR}
ORDER BY COMPANY_NM

SELECT DIVISION_CD AS CODE , DIVISION_NM AS LABEL FROM MOM_DIVISION_V WHERE 1=1
AND COMPANY_CD = #{companyCd, jdbcType=VARCHAR}
ORDER BY DIVISION_NM


SELECT WAREHOUSE_CD   AS CODE 
     , WAREHOUSE_CD ||'('|| WAREHOUSE_NM   ||')' AS LABEL
FROM   MOM_WAREHOUSE   
WHERE  COMPANY_CD  = #{companyCd, jdbcType=VARCHAR}
AND    DIVISION_CD = #{divisionCd, jdbcType=VARCHAR} 

AND    WAREHOUSE_TYPE = #{warehouseType, jdbcType=VARCHAR} 


AND    (TRIM(UPPER(WAREHOUSE_CD)) LIKE '%' || TRIM(UPPER(#{searchKey, jdbcType=VARCHAR})) || '%' OR TRIM(UPPER(WAREHOUSE_NM)) LIKE '%' || TRIM(UPPER(#{searchKey, jdbcType=VARCHAR})) || '%')


AND    WAREHOUSE_USE_YN = #{warehouseUseYn, jdbcType=VARCHAR} 


AND    PURCHASE_WAREHOUSE_YN = #{purchaseWarehouseYn, jdbcType=VARCHAR} 


AND    SHIPPING_WAREHOUSE_YN = #{shippingWarehouseYn, jdbcType=VARCHAR} 

AND    USE_YN      = 'Y'
ORDER BY LABEL


SELECT WORK_CENTER_CD     AS CODE 
     , WORK_CENTER_CD   ||'('|| WORK_CENTER_NM     ||')' AS LABEL
FROM   MOM_WORK_CENTER   
WHERE  COMPANY_CD  = #{companyCd, jdbcType=VARCHAR}
AND    DIVISION_CD = #{divisionCd, jdbcType=VARCHAR} 
AND    USE_YN      = 'Y'

AND WORK_CENTER_TYPE = #{workCenterType, jdbcType=VARCHAR} 


AND (WORK_CENTER_CD LIKE '%' || TRIM(UPPER(#{searchKey, jdbcType=VARCHAR})) || '%' OR WORK_CENTER_NM LIKE '%' || TRIM(UPPER(#{searchKey, jdbcType=VARCHAR})) || '%')

ORDER BY LABEL


SELECT WORK_STATION_CD     AS CODE 
     , WORK_STATION_CD   ||'('|| WORK_STATION_NM     ||')' AS LABEL
FROM   MOM_WORK_STATION   
WHERE  COMPANY_CD  = #{companyCd, jdbcType=VARCHAR}
AND    DIVISION_CD = #{divisionCd, jdbcType=VARCHAR} 
AND    USE_YN      = 'Y'

AND    WORK_CENTER_CD = #{workCenterCd, jdbcType=VARCHAR} 

ORDER BY LABEL


SELECT IO_TYPE       AS CODE 
     , IO_TYPE    ||'('|| IO_TYPE_NM      ||')' AS LABEL
FROM   MOM_IO_CATEGORY   
WHERE  COMPANY_CD  = #{companyCd, jdbcType=VARCHAR}
AND    DIVISION_CD = #{divisionCd, jdbcType=VARCHAR} 

AND    PUR_RETURN_YN = #{purReturnYn, jdbcType=VARCHAR} 


AND    PUR_RECEIVE_YN = #{purReceiveYn, jdbcType=VARCHAR} 


AND    SHIPPING_YN = #{shippingYn, jdbcType=VARCHAR} 


AND    EX_SHIPPING_YN = #{exShippingYn, jdbcType=VARCHAR} 

AND    USE_YN      = 'Y'
ORDER BY LABEL


SELECT LOCATION_CD     AS CODE 
     , LOCATION_CD  ||'('|| LOCATION_NM    ||')' AS LABEL
FROM   MOM_LOCATION   
WHERE  COMPANY_CD  = #{companyCd, jdbcType=VARCHAR}
AND    DIVISION_CD = #{divisionCd, jdbcType=VARCHAR}

AND    WAREHOUSE_CD = #{warehouseCd , jdbcType=VARCHAR}  

AND    USE_YN      = 'Y'
ORDER BY LOCATION_NM


SELECT RESOURCE_CD       AS CODE 
     , RESOURCE_CD  ||'('|| RESOURCE_NM      ||')' AS LABEL
FROM   MOM_LOGISTICS_RESOURCE   
WHERE  COMPANY_CD  = #{companyCd, jdbcType=VARCHAR}
AND    DIVISION_CD = #{divisionCd, jdbcType=VARCHAR} 
AND    USE_YN      = 'Y'

AND    RESOURCE_GROUP_CD = #{resourceGroupCd, jdbcType=VARCHAR} 

ORDER BY RESOURCE_NM


    SELECT MLT.RECEIPT_TYPE_CD       AS CODE 
         , MLT.RECEIPT_TYPE_CD  ||'('|| MLT.RECEIPT_TYPE_NM       ||')' AS LABEL
    FROM   MOM_WAREHOUSE_RECEIPT_TYPE MLT   
    WHERE  MLT.COMPANY_CD  = #{companyCd, jdbcType=VARCHAR}
    AND    MLT.DIVISION_CD = #{divisionCd, jdbcType=VARCHAR} 
    AND    MLT.USE_YN      = 'Y'

    AND    MLT.RECEIPT_TYPE_CD  = #{receiptTypeCd, jdbcType=VARCHAR}  


    AND    MLT.ACTION_TYPE_CD  = #{actionTypeCd, jdbcType=VARCHAR}  


	AND    MLT.ACTION_TYPE_CD = (SELECT MPT.ACTION_TYPE_CD
							     FROM   MOM_PROCESS_TYPE MPT
							     WHERE  MPT.COMPANY_CD   = MLT.COMPANY_CD
							     AND    MPT.DIVISION_CD  = MLT.DIVISION_CD 
							     AND    MPT.PROCESS_TYPE = #{processType, jdbcType=VARCHAR} 
							     AND    MPT.USE_YN = 'Y')

    ORDER BY RECEIPT_TYPE_NM

SELECT MLM.WAREHOUSE_CD AS CODE , MLM.WAREHOUSE_CD ||'('||ML.WAREHOUSE_NM ||')' AS LABEL FROM MOM_WAREHOUSE_MANAGER MLM , MOM_WAREHOUSE ML WHERE MLM.COMPANY_CD = ML.COMPANY_CD AND MLM.DIVISION_CD = ML.DIVISION_CD AND MLM.WAREHOUSE_CD = ML.WAREHOUSE_CD AND MLM.COMPANY_CD = #{companyCd, jdbcType=VARCHAR} AND MLM.DIVISION_CD = #{divisionCd, jdbcType=VARCHAR} AND MLM.USE_YN = 'Y'
AND MLM.WAREHOUSE_CD = #{warehouseCd, jdbcType=VARCHAR}
GROUP BY MLM.WAREHOUSE_CD , ML.WAREHOUSE_NM ORDER BY ML.WAREHOUSE_NM

SELECT MU.USER_NO AS CODE , MU.USER_NO ||'('||MU.USER_NM ||')' AS LABEL FROM MOM_WAREHOUSE_MANAGER MLM , MOM_USER MU WHERE MLM.COMPANY_CD = MU.COMPANY_CD AND MLM.DIVISION_CD = MU.DIVISION_CD AND MLM.USER_NO = MU.USER_NO AND MLM.COMPANY_CD = #{companyCd, jdbcType=VARCHAR} AND MLM.DIVISION_CD = #{divisionCd, jdbcType=VARCHAR} AND MLM.USE_YN = 'Y'
AND MLM.WAREHOUSE_CD = #{warehouseCd, jdbcType=VARCHAR}
GROUP BY MU.USER_NO , MU.USER_NM ORDER BY MU.USER_NM

SELECT CODE
     , NVL((SELECT MOM_COMMON_PKG.FN_GET_MULTI_LANGUAGE(#{companyCd, jdbcType=VARCHAR},#{divisionCd, jdbcType=VARCHAR},#{langCd, jdbcType=VARCHAR},'CODE',#{groupCd, jdbcType=VARCHAR}|| '_' ||CODE)
            FROM   DUAL ),CODE ||'('|| CODE_NM ||')') AS LABEL
FROM   MOM_CODE
WHERE  DIVISION_CD = #{divisionCd, jdbcType=VARCHAR}
AND    COMPANY_CD  = #{companyCd, jdbcType=VARCHAR}
AND    GROUP_CD    = 'MM0012'
AND    USE_YN      = 'Y'

AND     ATTRIBUTE1 = #{attribute1, jdbcType=VARCHAR} 


AND     ATTRIBUTE2 = #{attribute2, jdbcType=VARCHAR} 


AND     ATTRIBUTE3 = #{attribute3, jdbcType=VARCHAR} 

ORDER BY SORT_NO,LABEL

SELECT MP.PROCESS_CD AS CODE , MP.PROCESS_CD ||'('|| MP.PROCESS_NM ||')' AS LABEL FROM MOM_PROCESS MP WHERE MP.COMPANY_CD = #{companyCd, jdbcType=VARCHAR} AND MP.DIVISION_CD = #{divisionCd, jdbcType=VARCHAR}
AND MP.PROCESS_TYPE = #{processType, jdbcType=VARCHAR}
AND EXISTS ( SELECT * FROM MOM_PROCESS_TYPE MPT WHERE MPT.COMPANY_CD = MP.COMPANY_CD AND MPT.DIVISION_CD = MP.DIVISION_CD AND MPT.PROCESS_TYPE = MP.PROCESS_TYPE AND MPT.ACTION_TYPE_CD = #{actionTypeCd, jdbcType=VARCHAR} AND MPT.USE_YN = 'Y' )
AND USE_YN = 'Y' ORDER BY PROCESS_CD

SELECT MIC.IO_TYPE AS CODE , MIC.IO_TYPE ||'('|| MIC.IO_TYPE_NM ||')' AS LABEL FROM MOM_PROCESS_IO_MAPPING MPIM , MOM_IO_CATEGORY MIC WHERE MPIM.COMPANY_CD = MIC.COMPANY_CD AND MPIM.DIVISION_CD = MIC.DIVISION_CD AND MPIM.IO_TYPE = MIC.IO_TYPE AND MPIM.COMPANY_CD = #{companyCd, jdbcType=VARCHAR} AND MPIM.DIVISION_CD = #{divisionCd, jdbcType=VARCHAR}
AND MPIM.PROCESS_CD = #{processCd, jdbcType=VARCHAR}
AND MPIM.USE_YN = 'Y' ORDER BY MPIM.SORT_NO, MPIM.IO_TYPE


SELECT PROCESS_TYPE        AS CODE 
     , PROCESS_TYPE    ||'('|| PROCESS_TYPE_NM      ||')' AS LABEL
FROM   MOM_PROCESS_TYPE  
WHERE  COMPANY_CD  = #{companyCd, jdbcType=VARCHAR}
AND    DIVISION_CD = #{divisionCd, jdbcType=VARCHAR}

AND    PROCESS_TYPE = #{processType, jdbcType=VARCHAR} 


AND    BOUNDARY_TYPE_CD  = #{boundaryTypeCd, jdbcType=VARCHAR} 


AND    ACTION_TYPE_CD   = #{actionTypeCd, jdbcType=VARCHAR} 

AND    USE_YN      = 'Y'
ORDER BY PROCESS_TYPE

SELECT MWP.WAREHOUSE_CD AS CODE , MWP.WAREHOUSE_CD ||'('|| MWH.WAREHOUSE_NM ||')' AS LABEL FROM MOM_WAREHOUSE_PROCESS_TYPE MWP , MOM_WAREHOUSE MWH WHERE MWP.COMPANY_CD = MWH.COMPANY_CD AND MWP.DIVISION_CD = MWH.DIVISION_CD AND MWP.WAREHOUSE_CD = MWH.WAREHOUSE_CD AND MWP.COMPANY_CD = #{companyCd, jdbcType=VARCHAR} AND MWP.DIVISION_CD = #{divisionCd, jdbcType=VARCHAR}
AND MWP.PROCESS_TYPE = #{processType, jdbcType=VARCHAR}
AND MWP.PROCESS_TYPE IN (SELECT MPT.PROCESS_TYPE FROM MOM_PROCESS_TYPE MPT WHERE MPT.COMPANY_CD = MWP.COMPANY_CD AND MPT.DIVISION_CD = MWP.DIVISION_CD AND MPT.ACTION_TYPE_CD = #{actionTypeCd, jdbcType=VARCHAR} AND MPT.USE_YN = 'Y' )
AND MWP.USE_YN = 'Y' GROUP BY MWP.WAREHOUSE_CD , MWH.WAREHOUSE_NM ORDER BY MWH.WAREHOUSE_NM


SELECT LOGISTICS_UNIT_CD       AS CODE 
     , LOGISTICS_UNIT_CD    ||'('|| LOGISTICS_UNIT_NM      ||')' AS LABEL
FROM   MOM_LOGISTICS_UNIT  
WHERE  COMPANY_CD  = #{companyCd, jdbcType=VARCHAR}
AND    DIVISION_CD = #{divisionCd, jdbcType=VARCHAR}

AND     LOCATION_CD = #{locationCd, jdbcType=VARCHAR} 

ORDER BY LOGISTICS_UNIT_CD

