mapper.select:
SELECT A.* FROM (
SELECT MDM.COMPANY_CD , MDM.DIVISION_CD , MDM.DO_NO AS SHIPPING_REQUEST_NO , MDM.DO_NM AS SHIPPING_REQUEST_NM , MDM.PROCESS_CD , (SELECT MOM_COMMON_PKG.FN_GET_PROCESS_NAME(MDM.COMPANY_CD, MDM.DIVISION_CD, MDM.PROCESS_CD) FROM DUAL) AS PROCESS_NM , MDM.PARTNER_CD , MP.PARTNER_NM , MDM.ISSUE_WAREHOUSE_CD , MWH.WAREHOUSE_NM AS ISSUE_WAREHOUSE_NM , MDM.CURRENCY_CD , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MDM.COMPANY_CD, MDM.DIVISION_CD, 'SM0017', MDM.CURRENCY_CD, #{langCd, jdbcType=VARCHAR}) FROM DUAL) AS CURRENCY_NM , MDM.EXCHANGE_RATE , TO_CHAR(MDM.ISSUE_DUE_DATE ,'YYYY-MM-DD') AS SHIPPING_DUE_DATE , MDM.DESCRIPTION , MDM.WAREHOUSE_USE_YN , MDM.UPDATE_BY AS UPDATE_USER_NO , (SELECT MOM_COMMON_PKG.FN_GET_USER_NAME(MDM.COMPANY_CD, MDM.DIVISION_CD, MDM.UPDATE_BY) FROM DUAL ) AS UPDATE_USER_NM , TO_CHAR(MDM.UPDATE_DATE ,'YYYY-MM-DD HH24:MI:SS') AS UPDATE_DATE , TO_CHAR(MDM.CREATE_DATE ,'YYYY-MM-DD') AS CREATE_DATE , ROWIDTOCHAR(MDM.ROWID) AS KEY_ID , COUNT(*) OVER() AS TOTAL_COUNT , ROW_NUMBER() OVER(PARTITION BY MDM.COMPANY_CD,MDM.DIVISION_CD ORDER BY MDM.DO_NO) AS ROW_CNT FROM MOM_DO_MST MDM , MOM_PARTNER MP , MOM_WAREHOUSE MWH WHERE MDM.COMPANY_CD = MP.COMPANY_CD AND MDM.DIVISION_CD = MP.DIVISION_CD AND MDM.PARTNER_CD = MP.PARTNER_CD AND MDM.COMPANY_CD = MWH.COMPANY_CD AND MDM.DIVISION_CD = MWH.DIVISION_CD AND MDM.ISSUE_WAREHOUSE_CD = MWH.WAREHOUSE_CD AND MDM.COMPANY_CD = #{companyCd, jdbcType=VARCHAR} AND MDM.DIVISION_CD = #{divisionCd, jdbcType=VARCHAR} AND MDM.STATE = 'DRG' AND MDM.PROCESS_CD = 'NI' AND MDM.CREATE_DATE BETWEEN TO_DATE(#{createDateSD, jdbcType=VARCHAR}, 'YYYY-MM-DD') AND TO_DATE(#{createDateED, jdbcType=VARCHAR}, 'YYYY-MM-DD') + 0.99999
AND (UPPER(MDM.DO_NO) LIKE '%' || UPPER(#{shippingRequestNo, jdbcType=VARCHAR}) || '%')
AND (UPPER(MDM.PARTNER_CD) LIKE '%' || UPPER(#{partnerCdNm, jdbcType=VARCHAR}) || '%' OR UPPER(MP.PARTNER_NM) LIKE '%' || UPPER(#{partnerCdNm, jdbcType=VARCHAR}) || '%')
AND (UPPER(MDM.ISSUE_WAREHOUSE_CD) LIKE '%' || UPPER(#{issueWarehouseCdNm, jdbcType=VARCHAR}) || '%' OR UPPER(MWH.WAREHOUSE_NM) LIKE '%' || UPPER(#{issueWarehouseCdNm, jdbcType=VARCHAR}) || '%')
) A WHERE ROW_CNT BETWEEN #{startPage, jdbcType=INTEGER} AND #{endPage, jdbcType=INTEGER}

mapper.delete:
DELETE FROM MOM_PROC_DO_MST


    (       COMPANY_CD  = #{item.companyCd, jdbcType=VARCHAR}
       AND  DIVISION_CD = #{item.divisionCd, jdbcType=VARCHAR}
       AND  PROCESS_CD  = 'NI'
       AND  CREATE_BY   = #{item.userId, jdbcType=VARCHAR}
    )

insert.foreach:

INTO MOM_PROC_DO_MST( COMPANY_CD
                    , DIVISION_CD
                    , PROCESS_CD
                    , DO_NO
                    , DO_NM
                    , ISSUE_WAREHOUSE_CD
                    , PARTNER_CD
                    , FORWARDER_CD
                    , DERIVERY_TO_ADRESS
                    , LOGISTICS_RESOURCE_CD
                    , PARTIAL_SHIPMENT_YN
                    , SHIPPING_MARK
                    , NAME_PLATE
                    , INSPECTION_REPORT_YN
                    , TRANSIT_INSURANCE_YN
                    , ISSUE_DUE_DATE
                    , CURRENCY_CD
                    , EXCHANGE_RATE
                    , DESCRIPTION
                    , DEL_YN
                    , VAL_MSG
                    , MULTI_MSG
                    , CREATE_BY
                    , CREATE_DATE
                    )
VALUES              ( #{item.companyCd, jdbcType=VARCHAR}
                    , #{item.divisionCd, jdbcType=VARCHAR}
                    , 'NI'
                    , #{item.shippingRequestNo, jdbcType=VARCHAR}
                    , #{item.shippingRequestNm, jdbcType=VARCHAR}
                    , #{item.issueWarehouseCd, jdbcType=VARCHAR}
                    , #{item.customerCd, jdbcType=VARCHAR}
                    , #{item.forwarderCd, jdbcType=VARCHAR}
                    , #{item.deriveryToAdress, jdbcType=VARCHAR}
                    , #{item.logisticsResourceCd, jdbcType=VARCHAR}
                    , #{item.partialShipmentYn, jdbcType=VARCHAR}
                    , #{item.shippingMark, jdbcType=VARCHAR}
                    , #{item.namePlate, jdbcType=VARCHAR}
                    , #{item.inspectionReportYn, jdbcType=VARCHAR}
                    , #{item.transitInsuranceYn, jdbcType=VARCHAR}
                    , #{item.shippingDueDate, jdbcType=VARCHAR}
                    , #{item.currencyCd, jdbcType=VARCHAR}
                    , #{item.exchangeRate, jdbcType=NUMERIC}
                    , #{item.description, jdbcType=VARCHAR}
                    , #{item.delYn, jdbcType=VARCHAR}
                    , #{item.valMsg, jdbcType=VARCHAR}
                    , #{item.multiMsg, jdbcType=VARCHAR}
                    , #{item.userId, jdbcType=VARCHAR}
                    , MOM_COMMON_PKG.FN_GET_LOCAL_TIME(#{item.companyCd, jdbcType=VARCHAR},#{item.divisionCd, jdbcType=VARCHAR})
                    )

mapper.select:

{ CALL MOM_STOCK_PKG.SP_UPSERT_DO_MST( #{p_err_code,  jdbcType=VARCHAR, mode=OUT}
                                     , #{p_err_msg,   jdbcType=VARCHAR, mode=OUT}
                                     , #{companyCd,   jdbcType=VARCHAR, mode=IN}
                                     , #{divisionCd,  jdbcType=VARCHAR, mode=IN}
                                     , 'NI'
                                     , #{userId,      jdbcType=VARCHAR, mode=IN} )}

