mapper.select:
SELECT A.* FROM (
SELECT MDM.COMPANY_CD , MDM.DIVISION_CD , MDM.DO_NO AS RESTORE_REQUEST_NO , MDM.DO_NM AS RESTORE_REQUEST_NM , MDM.PROCESS_CD , (SELECT MOM_COMMON_PKG.FN_GET_PROCESS_NAME(MDM.COMPANY_CD, MDM.DIVISION_CD, MDM.PROCESS_CD) FROM DUAL) AS PROCESS_NM , MDM.REQUEST_WAREHOUSE_CD AS RESTORE_WAREHOUSE_CD , MWR.WAREHOUSE_NM AS RESTORE_WAREHOUSE_NM , MDM.ISSUE_WAREHOUSE_CD AS REQUEST_WAREHOUSE_CD , MWI.WAREHOUSE_NM AS REQUEST_WAREHOUSE_NM , TO_CHAR(MDM.ISSUE_DUE_DATE ,'YYYY-MM-DD') AS RESTORE_DUE_DATE , MDM.REASON_CD AS RESTORE_REASON_CD , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MDM.COMPANY_CD, MDM.DIVISION_CD, 'IC0008', MDM.REASON_CD, #{langCd, jdbcType=VARCHAR}) FROM DUAL) AS RESTORE_REASON_NM , MDM.DESCRIPTION , MDM.ISSUE_WH_USE_YN AS REQUEST_WAREHOUSE_USE_YN , MDM.REQUEST_WH_USE_YN AS RESTORE_WAREHOUSE_USE_YN , MDM.UPDATE_BY AS UPDATE_USER_NO , (SELECT MOM_COMMON_PKG.FN_GET_USER_NAME(MDM.COMPANY_CD, MDM.DIVISION_CD, MDM.UPDATE_BY) FROM DUAL ) AS UPDATE_USER_NM , TO_CHAR(MDM.UPDATE_DATE ,'YYYY-MM-DD HH24:MI:SS') AS UPDATE_DATE , TO_CHAR(MDM.CREATE_DATE ,'YYYY-MM-DD') AS CREATE_DATE , ROWIDTOCHAR(MDM.ROWID) AS KEY_ID , COUNT(*) OVER() AS TOTAL_COUNT , ROW_NUMBER() OVER(PARTITION BY MDM.COMPANY_CD,MDM.DIVISION_CD ORDER BY MDM.DO_NO) AS ROW_CNT FROM MOM_DO_MST MDM , MOM_WAREHOUSE MWR , MOM_WAREHOUSE MWI WHERE MDM.COMPANY_CD = MWR.COMPANY_CD AND MDM.DIVISION_CD = MWR.DIVISION_CD AND MDM.REQUEST_WAREHOUSE_CD = MWR.WAREHOUSE_CD AND MDM.COMPANY_CD = MWI.COMPANY_CD AND MDM.DIVISION_CD = MWI.DIVISION_CD AND MDM.ISSUE_WAREHOUSE_CD = MWI.WAREHOUSE_CD AND MDM.COMPANY_CD = #{companyCd, jdbcType=VARCHAR} AND MDM.DIVISION_CD = #{divisionCd, jdbcType=VARCHAR} AND MDM.STATE = 'DRG' AND MDM.PROCESS_CD = 'MIR' AND MDM.CREATE_DATE BETWEEN TO_DATE(#{createDateSD, jdbcType=VARCHAR}, 'YYYY-MM-DD') AND TO_DATE(#{createDateED, jdbcType=VARCHAR}, 'YYYY-MM-DD') + 0.99999 AND EXISTS ( SELECT '*' FROM MOM_WAREHOUSE_MANAGER MLM WHERE MLM.COMPANY_CD = MDM.COMPANY_CD AND MLM.DIVISION_CD = MDM.DIVISION_CD AND MLM.WAREHOUSE_CD = MDM.ISSUE_WAREHOUSE_CD AND MLM.USE_YN = 'Y' AND MLM.USER_NO = #{userId, jdbcType=VARCHAR} )
AND (UPPER(MDM.DO_NO) LIKE '%' || UPPER(#{restoreRequestNo, jdbcType=VARCHAR}) || '%')
AND (UPPER(MDM.REQUEST_WAREHOUSE_CD) LIKE '%' || UPPER(#{restoreWarehouseCdNm, jdbcType=VARCHAR}) || '%' OR UPPER(MWR.WAREHOUSE_NM) LIKE '%' || UPPER(#{restoreWarehouseCdNm, jdbcType=VARCHAR}) || '%')
AND MDM.ISSUE_WAREHOUSE_CD = #{requestWarehouseCd, jdbcType=VARCHAR}
) A WHERE ROW_CNT BETWEEN #{startPage, jdbcType=INTEGER} AND #{endPage, jdbcType=INTEGER}

mapper.delete:
DELETE FROM MOM_PROC_DO_MST


    (       COMPANY_CD  = #{item.companyCd, jdbcType=VARCHAR}
       AND  DIVISION_CD = #{item.divisionCd, jdbcType=VARCHAR}
       AND  PROCESS_CD  = 'MIR'
       AND  CREATE_BY   = #{item.userId, jdbcType=VARCHAR}
    )

insert.foreach:

INTO MOM_PROC_DO_MST( COMPANY_CD
                    , DIVISION_CD
                    , PROCESS_CD
                    , DO_NO
                    , DO_NM
                    , ISSUE_WAREHOUSE_CD
                    , REQUEST_WAREHOUSE_CD
                    , ISSUE_DUE_DATE
                    , REASON_CD
                    , DESCRIPTION
                    , DEL_YN
                    , VAL_MSG
                    , MULTI_MSG
                    , CREATE_BY
                    , CREATE_DATE
                    )
VALUES              ( #{item.companyCd, jdbcType=VARCHAR}
                    , #{item.divisionCd, jdbcType=VARCHAR}
                    , 'MIR'
                    , #{item.restoreRequestNo, jdbcType=VARCHAR}
                    , #{item.restoreRequestNm, jdbcType=VARCHAR}
                    , #{item.requestWarehouseCd, jdbcType=VARCHAR}
                    , #{item.restoreWarehouseCd, jdbcType=VARCHAR}
                    , #{item.restoreDueDate, jdbcType=VARCHAR}
                    , #{item.restoreReasonCd, jdbcType=VARCHAR}
                    , #{item.description, jdbcType=VARCHAR}
                    , #{item.delYn, jdbcType=VARCHAR}
                    , #{item.valMsg, jdbcType=VARCHAR}
                    , #{item.multiMsg, jdbcType=VARCHAR}
                    , #{item.userId, jdbcType=VARCHAR}
                    , MOM_COMMON_PKG.FN_GET_LOCAL_TIME(#{item.companyCd, jdbcType=VARCHAR},#{item.divisionCd, jdbcType=VARCHAR})
                    )

mapper.select:

{ CALL MOM_STOCK_PKG.SP_UPSERT_DO_MST( #{p_err_code,  jdbcType=VARCHAR, mode=OUT}
                                     , #{p_err_msg,   jdbcType=VARCHAR, mode=OUT}
                                     , #{companyCd,   jdbcType=VARCHAR, mode=IN}
                                     , #{divisionCd,  jdbcType=VARCHAR, mode=IN}
                                     , 'MIR'
                                     , #{userId,      jdbcType=VARCHAR, mode=IN} )}

mapper.delete:
