mapper.select:
SELECT A.* FROM (
SELECT MWH.COMPANY_CD , MWH.DIVISION_CD , MWH.PLANT_CD , (SELECT MOM_COMMON_PKG.FN_GET_PLANT_NAME(MWH.COMPANY_CD, MWH.DIVISION_CD, MWH.PLANT_CD) FROM DUAL ) AS PLANT_NM , MWH.WAREHOUSE_CD , MWH.WAREHOUSE_NM , MWH.WAREHOUSE_GROUP_CD , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MWH.COMPANY_CD, MWH.DIVISION_CD, 'MD0017', MWH.WAREHOUSE_GROUP_CD, #{langCd, jdbcType=VARCHAR}) FROM DUAL ) AS WAREHOUSE_GROUP_NM , MWH.WAREHOUSE_TYPE , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MWH.COMPANY_CD, MWH.DIVISION_CD,'MD0016', MWH.WAREHOUSE_TYPE , #{langCd, jdbcType=VARCHAR}) FROM DUAL ) AS WAREHOUSE_TYPE_NM , MWH.PARTNER_CD , (SELECT MOM_COMMON_PKG.FN_GET_PARTNER_NAME(MWH.COMPANY_CD, MWH.DIVISION_CD, MWH.PARTNER_CD) FROM DUAL ) AS PARTNER_NM , MWH.WAREHOUSE_USE_YN , MWH.PURCHASE_WAREHOUSE_YN , MWH.SHIPPING_WAREHOUSE_YN , MWH.PICKING_CREATE_TYPE , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MWH.COMPANY_CD, MWH.DIVISION_CD, 'WM0009', MWH.PICKING_CREATE_TYPE, #{langCd, jdbcType=VARCHAR}) FROM DUAL ) AS PICKING_CREATE_TYPE_NM , MWH.USE_YN , MWH.CREATE_BY AS CREATE_USER_NO , (SELECT MOM_COMMON_PKG.FN_GET_USER_NAME(MWH.COMPANY_CD, MWH.DIVISION_CD, MWH.CREATE_BY) FROM DUAL ) AS CREATE_USER_NM , TO_CHAR(MWH.CREATE_DATE ,'YYYY-MM-DD HH24:MI:SS') AS CREATE_DATE , MWH.UPDATE_BY AS UPDATE_USER_NO , (SELECT MOM_COMMON_PKG.FN_GET_USER_NAME(MWH.COMPANY_CD, MWH.DIVISION_CD, MWH.UPDATE_BY) FROM DUAL ) AS UPDATE_USER_NM , TO_CHAR(MWH.UPDATE_DATE ,'YYYY-MM-DD HH24:MI:SS') AS UPDATE_DATE , MWH.DESCRIPTION , ROWIDTOCHAR(MWH.ROWID) AS KEY_ID , COUNT(*) OVER() AS TOTAL_COUNT , ROW_NUMBER() OVER(PARTITION BY MWH.COMPANY_CD,MWH.DIVISION_CD ORDER BY MWH.WAREHOUSE_CD) AS ROW_CNT FROM MOM_WAREHOUSE MWH WHERE MWH.COMPANY_CD = #{companyCd, jdbcType=VARCHAR} AND MWH.DIVISION_CD = #{divisionCd, jdbcType=VARCHAR}
AND MWH.PLANT_CD IN (${plantCd})
AND (UPPER(MWH.WAREHOUSE_CD) LIKE '%' || UPPER(#{warehouseCd, jdbcType=VARCHAR}) || '%' )
AND (UPPER(MWH.WAREHOUSE_NM) LIKE '%' || UPPER(#{warehouseNm, jdbcType=VARCHAR}) || '%' )
AND MWH.WAREHOUSE_TYPE IN (${warehouseType})
AND MWH.WAREHOUSE_GROUP_CD IN (${warehouseGroup})
AND MWH.USE_YN = #{useYn, jdbcType=VARCHAR}
) A WHERE ROW_CNT BETWEEN #{startPage, jdbcType=INTEGER} AND #{endPage, jdbcType=INTEGER}

mapper.delete:
DELETE FROM MOM_PROC_WAREHOUSE


    (       COMPANY_CD  = #{item.companyCd, jdbcType=VARCHAR}
       AND  DIVISION_CD = #{item.divisionCd, jdbcType=VARCHAR}
       AND  CREATE_BY   = #{item.userId, jdbcType=VARCHAR}
    )

insert.foreach:

        INTO MOM_PROC_WAREHOUSE( COMPANY_CD
			                   , DIVISION_CD
			                   , PLANT_CD
			                   , WAREHOUSE_CD
			                   , WAREHOUSE_NM
			                   , WAREHOUSE_GROUP_CD
			                   , WAREHOUSE_TYPE
			                   , PARTNER_CD
			                   , WAREHOUSE_USE_YN
			                   , PURCHASE_WAREHOUSE_YN
			                   , SHIPPING_WAREHOUSE_YN
			                   , PICKING_CREATE_TYPE
			                   , USE_YN
			                   , DESCRIPTION
			                   , DEL_YN
			                   , VAL_MSG
			                   , MULTI_MSG
			                   , CREATE_BY
			                   , CREATE_DATE )
        VALUES                 ( #{item.companyCd, jdbcType=VARCHAR}
	                           , #{item.divisionCd, jdbcType=VARCHAR}
	                           , #{item.plantCd, jdbcType=VARCHAR}
	                           , #{item.warehouseCd, jdbcType=VARCHAR}
	                           , #{item.warehouseNm, jdbcType=VARCHAR}
	                           , #{item.warehouseGroupCd, jdbcType=VARCHAR}
	                           , #{item.warehouseType, jdbcType=VARCHAR}
	                           , #{item.partnerCd, jdbcType=VARCHAR}
	                           , #{item.warehouseUseYn, jdbcType=VARCHAR}
	                           , #{item.purchaseWarehouseYn, jdbcType=VARCHAR}
	                           , #{item.shippingWarehouseYn, jdbcType=VARCHAR}
	                           , #{item.pickingCreateType, jdbcType=VARCHAR}
	                           , #{item.useYn, jdbcType=VARCHAR}
	                           , #{item.description, jdbcType=VARCHAR}
	                           , #{item.delYn, jdbcType=VARCHAR}
                               , #{item.valMsg, jdbcType=VARCHAR}
                               , #{item.multiMsg, jdbcType=VARCHAR}
                               , #{item.userId, jdbcType=VARCHAR}
                               , MOM_COMMON_PKG.FN_GET_LOCAL_TIME(#{item.companyCd, jdbcType=VARCHAR},#{item.divisionCd, jdbcType=VARCHAR})
                               )

mapper.select:

{ CALL MOM_MASTER_INFO_PKG.SP_UPSERT_WAREHOUSE( #{p_err_code,  jdbcType=VARCHAR, mode=OUT}
                                              , #{p_err_msg,   jdbcType=VARCHAR, mode=OUT}
                                              , #{companyCd,   jdbcType=VARCHAR, mode=IN}
                                              , #{divisionCd,  jdbcType=VARCHAR, mode=IN} 
                                              , #{actionMode,  jdbcType=VARCHAR, mode=IN}        
                                              , #{userId,      jdbcType=VARCHAR, mode=IN} )}

SELECT NVL(VAL_MSG,'PASS') AS VAL_MSG
     , MULTI_MSG      
FROM   MOM_PROC_WAREHOUSE
WHERE  COMPANY_CD  = #{companyCd , jdbcType=VARCHAR}
AND    DIVISION_CD = #{divisionCd , jdbcType=VARCHAR}
AND    CREATE_BY    = #{userId , jdbcType=VARCHAR}

