mapper.select:
SELECT MCD.COMPANY_CD , MCD.COMPANY_NM , MCD.DIVISION_CD , MCD.DIVISION_NM , MO.OPERATION_ID , MO.OPERATION_NM , MO.INSPECTION_YN , MO.USE_YN , MO.DESCRIPTION , MO.CREATE_BY ,(SELECT MOM_COMMON_PKG.FN_GET_USER_NAME(MO.COMPANY_CD, MO.DIVISION_CD, MO.CREATE_BY) FROM DUAL ) AS CREATE_BY_NM , TO_CHAR(MO.CREATE_DATE ,'YYYY-MM-DD HH24:MI:SS') AS CREATE_DATE , MO.UPDATE_BY ,(SELECT MOM_COMMON_PKG.FN_GET_USER_NAME(MO.COMPANY_CD, MO.DIVISION_CD, MO.UPDATE_BY) FROM DUAL ) AS UPDATE_BY_NM , TO_CHAR(MO.UPDATE_DATE ,'YYYY-MM-DD HH24:MI:SS') AS UPDATE_DATE , ROWIDTOCHAR(MO.ROWID) AS GRID_ID FROM MOM_COM_DIV_V MCD , MOM_OPERATION MO WHERE MCD.COMPANY_CD = MO.COMPANY_CD AND MCD.DIVISION_CD = MO.DIVISION_CD AND MCD.COMPANY_CD = #{companyCd, jdbcType=VARCHAR} AND MCD.DIVISION_CD = #{divisionCd, jdbcType=VARCHAR}
AND (MO.OPERATION_ID LIKE '%' || TRIM(UPPER(#{operationId, jdbcType=VARCHAR})) || '%' )
AND (MO.OPERATION_NM LIKE '%' || TRIM(UPPER(#{operationNm, jdbcType=VARCHAR})) || '%' )
AND MO.INSPECTION_YN = #{inspectionYn, jdbcType=VARCHAR}
AND MO.USE_YN = #{useYn, jdbcType=VARCHAR}
ORDER BY MCD.COMPANY_NM, MCD.DIVISION_NM,MO.OPERATION_NM

mapper.delete:
DELETE FROM MOM_PROC_OPERATION


      (       COMPANY_CD  = #{item.companyCd, jdbcType=VARCHAR}
         AND  DIVISION_CD = #{item.divisionCd, jdbcType=VARCHAR}
         AND  CREATE_BY   = #{item.userId, jdbcType=VARCHAR}
      )

insert.foreach:

        INTO MOM_PROC_OPERATION( COMPANY_CD
							   , DIVISION_CD
							   , OPERATION_ID
                               , OPERATION_NM
                               , INSPECTION_YN
							   , USE_YN
							   , DESCRIPTION
							   , DEL_YN
						   	   , VAL_MSG
							   , MULTI_MSG
							   , CREATE_BY
							   , CREATE_DATE
							   )
        VALUES                 ( #{item.companyCd, jdbcType=VARCHAR}
				               , #{item.divisionCd, jdbcType=VARCHAR}
				               , #{item.operationId, jdbcType=VARCHAR}
				               , #{item.operationNm, jdbcType=VARCHAR}
				               , #{item.inspectionYn, jdbcType=VARCHAR}
				               , #{item.useYn, jdbcType=VARCHAR}
			                   , #{item.description, jdbcType=VARCHAR}
			                   , #{item.delYn, jdbcType=VARCHAR}
			                   , #{item.valMsg, jdbcType=VARCHAR}
			                   , #{item.multiMsg, jdbcType=VARCHAR}
			                   , #{item.userId, jdbcType=VARCHAR}
			                   , MOM_COMMON_PKG.FN_GET_LOCAL_TIME(#{item.companyCd, jdbcType=VARCHAR},#{item.divisionCd, jdbcType=VARCHAR})
		                       )

mapper.select:

        { CALL MOM_MASTER_INFO_PKG.SP_UPSERT_OPERATION( #{p_err_code,  jdbcType=VARCHAR, mode=OUT}
	                                                  , #{p_err_msg,   jdbcType=VARCHAR, mode=OUT}
	                                                  , #{companyCd,   jdbcType=VARCHAR, mode=IN}
	                                                  , #{divisionCd,  jdbcType=VARCHAR, mode=IN} 
	                                                  , #{actionMode,  jdbcType=VARCHAR, mode=IN}        
	                                                  , #{userId,      jdbcType=VARCHAR, mode=IN} )}

SELECT NVL(VAL_MSG,'PASS') AS VAL_MSG
     , MULTI_MSG      
FROM   MOM_PROC_OPERATION
WHERE  COMPANY_CD  = #{companyCd, jdbcType=VARCHAR}
AND    DIVISION_CD = #{divisionCd, jdbcType=VARCHAR}
AND    CREATE_BY   = #{userId, jdbcType=VARCHAR}

mapper.delete:
DELETE FROM MOM_PROC_OPERATION


      (       COMPANY_CD  = #{item.companyCd, jdbcType=VARCHAR}
         AND  DIVISION_CD = #{item.divisionCd, jdbcType=VARCHAR}
         AND  CREATE_BY   = #{item.userId, jdbcType=VARCHAR}
      )

insert.foreach:

        INTO MOM_PROC_OPERATION( COMPANY_CD
							   , DIVISION_CD
							   , OPERATION_ID
                               , OPERATION_NM
                               , INSPECTION_YN
							   , USE_YN
							   , DESCRIPTION
							   , DEL_YN
						   	   , VAL_MSG
							   , MULTI_MSG
							   , CREATE_BY
							   , CREATE_DATE
							   )
        VALUES                 ( #{item.companyCd, jdbcType=VARCHAR}
				               , #{item.divisionCd, jdbcType=VARCHAR}
				               , #{item.operationId, jdbcType=VARCHAR}
				               , #{item.operationNm, jdbcType=VARCHAR}
				               , #{item.inspectionYn, jdbcType=VARCHAR}
				               , #{item.useYn, jdbcType=VARCHAR}
			                   , #{item.description, jdbcType=VARCHAR}
			                   , 'N'
			                   , #{item.valMsg, jdbcType=VARCHAR}
			                   , #{item.multiMsg, jdbcType=VARCHAR}
			                   , #{item.userId, jdbcType=VARCHAR}
			                   , MOM_COMMON_PKG.FN_GET_LOCAL_TIME(#{item.companyCd, jdbcType=VARCHAR},#{item.divisionCd, jdbcType=VARCHAR})
		                       )

mapper.select:

        { CALL MOM_MASTER_INFO_PKG.SP_UPSERT_OPERATION( #{p_err_code,  jdbcType=VARCHAR, mode=OUT}
	                                                  , #{p_err_msg,   jdbcType=VARCHAR, mode=OUT}
	                                                  , #{companyCd,   jdbcType=VARCHAR, mode=IN}
	                                                  , #{divisionCd,  jdbcType=VARCHAR, mode=IN} 
	                                                  , #{actionMode,  jdbcType=VARCHAR, mode=IN}        
	                                                  , #{userId,      jdbcType=VARCHAR, mode=IN} )}
