mapper.select:
SELECT A.* FROM (
SELECT MUP.COMPANY_CD , MUP.DIVISION_CD , MUP.PRICE_CATEGORY , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MUP.COMPANY_CD,MUP.DIVISION_CD, 'MD0011', MUP.PRICE_CATEGORY, #{langCd, jdbcType=VARCHAR}) FROM DUAL) AS PRICE_CATEGORY_NM , MUP.PARTNER_CD AS OUTSOURCING_CD , MP.PARTNER_NM AS OUTSOURCING_NM , MUP.ITEM_ID , MI.ITEM_NM , MI.ITEM_SPEC , MI.ITEM_TYPE , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MI.COMPANY_CD,MI.DIVISION_CD, 'MD0002', MI.ITEM_TYPE, #{langCd, jdbcType=VARCHAR}) FROM DUAL) AS ITEM_TYPE_NM , MUP.UOM , (SELECT MOM_COMMON_PKG.FN_GET_UNIT_NAME(MUP.COMPANY_CD,MUP.DIVISION_CD, MUP.UOM) FROM DUAL) AS UOM_NM , MUP.CURRENCY_CD , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MUP.COMPANY_CD,MUP.DIVISION_CD, 'SM0017', MUP.CURRENCY_CD, #{langCd, jdbcType=VARCHAR}) FROM DUAL) AS CURRENCY_NM , MUP.PREV_UNIT_PRICE , MUP.UNIT_PRICE , TO_CHAR(MUP.START_DATE ,'YYYY-MM-DD' ) AS START_DATE , TO_CHAR(MUP.END_DATE ,'YYYY-MM-DD' ) AS END_DATE , MUP.DESCRIPTION , MUP.UPDATE_BY , (SELECT MOM_COMMON_PKG.FN_GET_USER_NAME(MUP.COMPANY_CD, MUP.DIVISION_CD, MUP.UPDATE_BY) FROM DUAL ) AS UPDATEBYNM , TO_CHAR(MUP.UPDATE_DATE ,'YYYY-MM-DD HH24:MI:SS') AS UPDATE_DATE , ROWIDTOCHAR(MUP.ROWID) AS KEY_ID , COUNT(*) OVER() AS TOTAL_COUNT , ROW_NUMBER() OVER(PARTITION BY MUP.COMPANY_CD,MUP.DIVISION_CD ORDER BY MUP.ITEM_ID, MUP.PARTNER_CD, MUP.CURRENCY_CD, TO_CHAR(MUP.START_DATE ,'YYYY-MM-DD') ) AS ROW_COUNT FROM MOM_UNIT_PRICE MUP , MOM_ITEM MI , MOM_PARTNER MP WHERE MUP.COMPANY_CD = MI.COMPANY_CD AND MUP.DIVISION_CD = MI.DIVISION_CD AND MUP.ITEM_ID = MI.ITEM_ID AND MUP.COMPANY_CD = MP.COMPANY_CD AND MUP.DIVISION_CD = MP.DIVISION_CD AND MUP.PARTNER_CD = MP.PARTNER_CD AND MUP.COMPANY_CD = #{companyCd, jdbcType=VARCHAR} AND MUP.DIVISION_CD = #{divisionCd, jdbcType=VARCHAR} AND MUP.PRICE_CATEGORY = 'O'
AND TO_DATE(#{basicDate, jdbcType=VARCHAR},'YYYY-MM-DD') BETWEEN MUP.START_DATE AND MUP.END_DATE
AND (UPPER(MUP.PARTNER_CD) LIKE '%' || UPPER(#{outsourcingCdNm, jdbcType=VARCHAR}) || '%' OR UPPER(MP.PARTNER_NM) LIKE '%' || UPPER(#{outsourcingCdNm, jdbcType=VARCHAR}) || '%')
AND (UPPER(MI.ITEM_ID) LIKE '%' || UPPER(#{itemIdNm, jdbcType=VARCHAR}) || '%' OR UPPER(MI.ITEM_NM) LIKE '%' || UPPER(#{itemIdNm, jdbcType=VARCHAR}) || '%')
) A WHERE ROW_COUNT BETWEEN #{startPage, jdbcType=INTEGER} AND #{endPage, jdbcType=INTEGER}

SELECT NVL(MULTI_MSG,'PASS') AS VAL_MSG
     , VAL_MSG               AS MULTI_MSG      
FROM   MOM_PROC_UNIT_PRICE
WHERE  COMPANY_CD     = #{companyCd , jdbcType=VARCHAR}
AND    DIVISION_CD    = #{divisionCd , jdbcType=VARCHAR}
AND    PRICE_CATEGORY = 'O'
AND    CREATE_BY      = #{userId , jdbcType=VARCHAR}
ORDER BY NO

mapper.delete:
        DELETE FROM MOM_PROC_UNIT_PRICE


	        (       COMPANY_CD     = #{item.companyCd, jdbcType=VARCHAR}
	           AND  DIVISION_CD    = #{item.divisionCd, jdbcType=VARCHAR}
	           AND  PRICE_CATEGORY = 'O'
	           AND  CREATE_BY      = #{item.userId, jdbcType=VARCHAR}
	        )

insert.foreach:

        INTO MOM_PROC_UNIT_PRICE ( COMPANY_CD
								 , DIVISION_CD
								 , PRICE_CATEGORY
								 , PARTNER_CD
								 , ITEM_ID
								 , CURRENCY_CD
								 , UOM
								 , UNIT_PRICE
								 , START_DATE
								 , END_DATE
								 , PRICE_TYPE
								 , MAIN_PARTNER_YN
								 , CREATE_BY
								 , CREATE_DATE
                                 )
        VALUES                   ( #{item.companyCd, jdbcType=VARCHAR}
				                 , #{item.divisionCd, jdbcType=VARCHAR}
				                 , 'O'
				                 , #{item.outsourcingCd, jdbcType=VARCHAR}
				                 , #{item.itemId, jdbcType=VARCHAR}
				                 , #{item.currencyCd, jdbcType=VARCHAR}
				                 , #{item.uom, jdbcType=VARCHAR}
				                 , #{item.unitPrice, jdbcType=NUMERIC}
				                 , TO_DATE(#{item.startDate, jdbcType=VARCHAR},'YYYY-MM-DD')
				                 , TO_DATE(#{item.endDate, jdbcType=VARCHAR},'YYYY-MM-DD')
				                 , 'F'
				                 , 'Y'
				                 , #{item.userId, jdbcType=VARCHAR}
				                 , MOM_COMMON_PKG.FN_GET_LOCAL_TIME(#{item.companyCd, jdbcType=VARCHAR},#{item.divisionCd, jdbcType=VARCHAR})
				                 )

mapper.select:

{ CALL MOM_MASTER_INFO_PKG.SP_UPSERT_PRICE( #{p_err_code, jdbcType=VARCHAR, mode=OUT}
                                          , #{p_err_msg,  jdbcType=VARCHAR, mode=OUT}
                                          , #{companyCd,  javaType=string,  jdbcType=VARCHAR , mode=IN}
                                          , #{divisionCd, jdbcType=VARCHAR, mode=IN}
                                          , 'O'
                                          , #{actionMode, jdbcType=VARCHAR, mode=IN}                          
                                          , #{userId,     jdbcType=VARCHAR, mode=IN} )}

