mapper.select:
SELECT A.* FROM (
SELECT MC.COMPANY_CD , MC.DIVISION_CD , TO_CHAR(MC.DATE_ID,'YYYY-MM-DD') AS DATE_ID , MC.YEAR , MC.QUARTER , MC.MONTH , SUBSTR(MC.WEEK,5) AS WEEK , MC.WEEK_DAY , TO_CHAR(MC.WEEK_START_DATE,'YYYY-MM-DD') AS WEEK_START_DATE , TO_CHAR(MC.WEEK_END_DATE,'YYYY-MM-DD') AS WEEK_END_DATE , MC.DAY_OFF_YN , MC.SHIFT_CD , MS.SHIFT_NM , MC.DESCRIPTION , MC.UPDATE_BY , (SELECT MOM_COMMON_PKG.FN_GET_USER_NAME(MC.COMPANY_CD, MC.DIVISION_CD, MC.UPDATE_BY) FROM DUAL ) AS UPDATE_BY_NM , TO_CHAR(MC.UPDATE_DATE ,'YYYY-MM-DD HH24:MI:SS') AS UPDATE_DATE , ROWIDTOCHAR(MC.ROWID) AS KEY_ID , COUNT(*) OVER() AS TOTAL_COUNT , ROW_NUMBER() OVER(PARTITION BY MC.COMPANY_CD,MC.DIVISION_CD ORDER BY TO_CHAR(MC.DATE_ID,'YYYY-MM-DD') ) AS ROW_COUNT FROM MOM_CALENDAR MC , MOM_SHIFT MS WHERE MC.COMPANY_CD = MS.COMPANY_CD(+) AND MC.DIVISION_CD = MS.DIVISION_CD(+) AND MC.SHIFT_CD = MS.SHIFT_CD(+) AND MC.COMPANY_CD = #{companyCd, jdbcType=VARCHAR} AND MC.DIVISION_CD = #{divisionCd, jdbcType=VARCHAR} AND MC.DATE_ID >= TO_DATE(#{startDate, jdbcType=VARCHAR}, 'YYYY-MM-DD') AND MC.DATE_ID < TO_DATE(#{endDate, jdbcType=VARCHAR}, 'YYYY-MM-DD') + 0.99999 ORDER BY MC.DATE_ID
) A WHERE ROW_COUNT BETWEEN #{startPage, jdbcType=INTEGER} AND #{endPage, jdbcType=INTEGER}

update.foreach:
UPDATE MOM_CALENDAR
SET    DAY_OFF_YN  = #{item.dayOffYn, jdbcType=VARCHAR}
     , SHIFT_CD    = #{item.shiftCd, jdbcType=VARCHAR}
     , DESCRIPTION = #{item.description, jdbcType=VARCHAR}
     , UPDATE_BY   = #{item.userId, jdbcType=VARCHAR}
     , UPDATE_DATE = MOM_COMMON_PKG.FN_GET_LOCAL_TIME(#{item.companyCd, jdbcType=VARCHAR},#{item.divisionCd, jdbcType=VARCHAR})
WHERE  1=1
AND    COMPANY_CD  = #{item.companyCd, jdbcType=VARCHAR}
AND    DIVISION_CD = #{item.divisionCd, jdbcType=VARCHAR}
AND    DATE_ID     = TO_DATE(#{item.dateId, jdbcType=VARCHAR},'YYYY-MM-DD')

mapper.select:

          { CALL MOM_MASTER_INFO_PKG.SP_APPLY_SHIFT_SCHEDULE( #{p_err_code, jdbcType=VARCHAR, mode=OUT}
					                                        , #{p_err_msg,  jdbcType=VARCHAR, mode=OUT}
					                                        , #{companyCd,javaType=string,  jdbcType=VARCHAR , mode=IN}
					                                        , #{divisionCd, jdbcType=VARCHAR, mode=IN}
					                                        , TO_DATE(#{startDate,  jdbcType=VARCHAR, mode=IN},'YYYY-MM-DD')
					                                        , TO_DATE(#{endDate,    jdbcType=VARCHAR, mode=IN},'YYYY-MM-DD')
					                                        , #{userId,     jdbcType=VARCHAR, mode=IN} )}
