mapper.select:
	 SELECT SHIFT_CD
     FROM MOM_SHIFT_SCHEDULE
	 WHERE 1=1
	 AND   COMPANY_CD  =  #{companyCd, jdbcType=VARCHAR}
	 AND   DIVISION_CD =  #{divisionCd, jdbcType=VARCHAR}
	 AND   TO_CHAR(APPLY_DATE,'YYYY-MM-DD')  = #{applyDate, jdbcType=VARCHAR}
	 AND   WORK_CENTER_CD = #{workCenterCd, jdbcType=VARCHAR}

SELECT A.* FROM (
SELECT A.* FROM ( SELECT MCD.COMPANY_CD , MCD.COMPANY_NM , MCD.DIVISION_CD , MCD.DIVISION_NM , MSS.WORK_CENTER_CD , MSS.WORK_CENTER_CD AS FROM_WORK_CENTER_CD , MWG.WORK_GROUP_CD , MWC.WORK_CENTER_NM , TO_CHAR(MSS.APPLY_DATE ,'YYYY-MM-DD') AS APPLY_DATE , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME( MCD.COMPANY_CD , MCD.DIVISION_CD , 'SM0019' , (SELECT MC.WEEK_DAY FROM MOM_CALENDAR MC WHERE MC.COMPANY_CD = MSS.COMPANY_CD AND MC.DIVISION_CD = MSS.DIVISION_CD AND MC.DATE_ID = MSS.APPLY_DATE) , #{langCd, jdbcType=VARCHAR}) FROM DUAL) AS WEEK_DAY_NM , (SELECT MC.WEEK_DAY FROM MOM_CALENDAR MC WHERE MC.COMPANY_CD = MSS.COMPANY_CD AND MC.DIVISION_CD = MSS.DIVISION_CD AND MC.DATE_ID = MSS.APPLY_DATE) AS WEEK_DAY , MSS.SHIFT_CD , MS.SHIFT_NM , CASE WHEN DAY_OFF_YN = 'N' THEN MS.START_TIME WHEN DAY_OFF_YN = 'Y' THEN '00:00' ELSE '00:00' END AS START_TIME , CASE WHEN DAY_OFF_YN = 'N' THEN MS.END_TIME WHEN DAY_OFF_YN = 'Y' THEN '00:00' ELSE '00:00' END AS END_TIME , MSS.DAY_OFF_YN , CASE WHEN DAY_OFF_YN = 'Y' THEN 0 ELSE (SELECT MOM_COMMON_PKG.FN_GET_SHIFT_WORK_TIME(MSS.COMPANY_CD, MSS.DIVISION_CD, MSS.SHIFT_CD) FROM DUAL) END AS WORK_TIME , CASE WHEN DAY_OFF_YN = 'Y' THEN 0 ELSE (SELECT MOM_COMMON_PKG.FN_GET_SHIFT_REST_TIME(MSS.COMPANY_CD, MSS.DIVISION_CD, MSS.SHIFT_CD) FROM DUAL) END AS REST_TIME , CASE WHEN DAY_OFF_YN = 'Y' THEN 0 ELSE NVL(MSS.PALN_SHUTDOWN_TIME,0) END AS PALN_SHUTDOWN_TIME , CASE WHEN DAY_OFF_YN = 'Y' THEN 0 ELSE NVL(MSS.UNMANNED_OPERATION_TIME,0) END AS UNMANNED_OPERATION_TIME , CASE WHEN DAY_OFF_YN = 'Y' THEN 0 ELSE NVL(MS.WORK_TIME,0) - NVL(MT.REST_TIME,0) - NVL(MSS.PALN_SHUTDOWN_TIME,0) + NVL(MSS.UNMANNED_OPERATION_TIME,0) END AS REAL_WORK_TIME , MSS.UPDATE_BY , (SELECT MOM_COMMON_PKG.FN_GET_USER_NAME(MSS.COMPANY_CD, MSS.DIVISION_CD, MSS.UPDATE_BY) FROM DUAL ) AS UPDATE_BY_NM , TO_CHAR(MSS.UPDATE_DATE ,'YYYY-MM-DD HH24:MI:SS') AS UPDATE_DATE , MSS.DESCRIPTION , ROWIDTOCHAR(MSS.ROWID) AS KEY_ID , COUNT(*) OVER() AS TOTAL_COUNT , ROW_NUMBER() OVER(PARTITION BY MSS.COMPANY_CD,MSS.DIVISION_CD ORDER BY MSS.WORK_CENTER_CD, MSS.APPLY_DATE ) AS ROW_CNT FROM MOM_COM_DIV_V MCD , MOM_SHIFT_SCHEDULE MSS , MOM_SHIFT MS , MOM_WORK_GROUP MWG , MOM_WORK_CENTER MWC , MOM_SHIFT_REST_TIME_V MT WHERE MCD.COMPANY_CD = MSS.COMPANY_CD AND MCD.DIVISION_CD = MSS.DIVISION_CD AND MSS.COMPANY_CD = MS.COMPANY_CD AND MSS.DIVISION_CD = MS.DIVISION_CD AND MSS.SHIFT_CD = MS.SHIFT_CD AND MS.COMPANY_CD = MT.COMPANY_CD(+) AND MS.DIVISION_CD = MT.DIVISION_CD(+) AND MS.SHIFT_CD = MT.SHIFT_CD(+) AND MWC.COMPANY_CD = MWG.COMPANY_CD AND MWC.DIVISION_CD = MWG.DIVISION_CD AND MWC.WORK_GROUP_CD = MWG.WORK_GROUP_CD AND MSS.COMPANY_CD = MWC.COMPANY_CD AND MSS.DIVISION_CD = MWC.DIVISION_CD AND MSS.WORK_CENTER_CD = MWC.WORK_CENTER_CD AND MSS.COMPANY_CD = #{companyCd, jdbcType=VARCHAR} AND MSS.DIVISION_CD = #{divisionCd, jdbcType=VARCHAR} AND MSS.APPLY_DATE BETWEEN TO_DATE(#{startDate, jdbcType=VARCHAR}, 'YYYY-MM-DD') AND TO_DATE(#{endDate, jdbcType=VARCHAR}, 'YYYY-MM-DD') + 0.99999
AND MSS.DAY_OFF_YN = #{dayOffYn, jdbcType=VARCHAR}
AND MWG.WORK_GROUP_CD IN (${workGroupCd})
AND MSS.WORK_CENTER_CD IN (${workCenterCd})
) A WHERE 1 = 1
AND A.WEEK_DAY IN (${weekDay})
) A WHERE ROW_CNT BETWEEN #{startPage, jdbcType=INTEGER} AND #{endPage, jdbcType=INTEGER}


          { CALL MOM_MASTER_INFO_PKG.xumd4040_wg_copy_proc(     #{p_err_code, jdbcType=VARCHAR, mode=OUT}
					                                          , #{p_err_msg,  jdbcType=VARCHAR, mode=OUT}
					                                          , #{companyCd,javaType=string,  jdbcType=VARCHAR , mode=IN}
					                                          , #{divisionCd, jdbcType=VARCHAR, mode=IN}
					                                          , TO_DATE(#{startDate,  jdbcType=VARCHAR, mode=IN},'YYYY-MM-DD')
					                                          , TO_DATE(#{endDate,    jdbcType=VARCHAR, mode=IN},'YYYY-MM-DD')
					                                          , #{fromWorkCenterCd, jdbcType=VARCHAR, mode=IN}
					                                          , #{arrayParam1,javaType=Object,jdbcType=ARRAY, jdbcTypeName=VARCHAR_ARRAY_LIST, mode=IN, typeHandler=com.mom.backend.util.ListMapTypeHandler}
					                                          , #{userId,     jdbcType=VARCHAR, mode=IN} )}


          { CALL MOM_MASTER_INFO_PKG.xumd4040_wc_copy_proc(     #{p_err_code, jdbcType=VARCHAR, mode=OUT}
					                                          , #{p_err_msg,  jdbcType=VARCHAR, mode=OUT}
					                                          , #{companyCd,javaType=string,  jdbcType=VARCHAR , mode=IN}
					                                          , #{divisionCd, jdbcType=VARCHAR, mode=IN}
					                                          , TO_DATE(#{startDate,  jdbcType=VARCHAR, mode=IN},'YYYY-MM-DD')
					                                          , TO_DATE(#{endDate,    jdbcType=VARCHAR, mode=IN},'YYYY-MM-DD')
					                                          , #{fromWorkCenterCd, jdbcType=VARCHAR, mode=IN}
					                                          , #{arrayParam1,javaType=Object,jdbcType=ARRAY, jdbcTypeName=VARCHAR_ARRAY_LIST, mode=IN, typeHandler=com.mom.backend.util.ListMapTypeHandler}
					                                          , #{userId,     jdbcType=VARCHAR, mode=IN} )}


          { CALL MOM_MASTER_INFO_PKG.xumd4040_wg_dayoff_proc(   #{p_err_code, jdbcType=VARCHAR, mode=OUT}
					                                          , #{p_err_msg,  jdbcType=VARCHAR, mode=OUT}
					                                          , #{companyCd,javaType=string,  jdbcType=VARCHAR , mode=IN}
					                                          , #{divisionCd, jdbcType=VARCHAR, mode=IN}
					                                          , TO_DATE(#{startDate,  jdbcType=VARCHAR, mode=IN},'YYYY-MM-DD')
					                                          , TO_DATE(#{endDate,    jdbcType=VARCHAR, mode=IN},'YYYY-MM-DD')
                                                              , #{arrayParam1,javaType=Object,jdbcType=ARRAY, jdbcTypeName=VARCHAR_ARRAY_LIST, mode=IN, typeHandler=com.mom.backend.util.ListMapTypeHandler}
					                                          , #{userId,     jdbcType=VARCHAR, mode=IN} )}


          { CALL MOM_MASTER_INFO_PKG.xumd4040_wc_dayoff_proc(   #{p_err_code, jdbcType=VARCHAR, mode=OUT}
					                                          , #{p_err_msg,  jdbcType=VARCHAR, mode=OUT}
					                                          , #{companyCd,javaType=string,  jdbcType=VARCHAR , mode=IN}
					                                          , #{divisionCd, jdbcType=VARCHAR, mode=IN}
					                                          , TO_DATE(#{startDate,  jdbcType=VARCHAR, mode=IN},'YYYY-MM-DD')
					                                          , TO_DATE(#{endDate,    jdbcType=VARCHAR, mode=IN},'YYYY-MM-DD')
                                                              , #{arrayParam1,javaType=Object,jdbcType=ARRAY, jdbcTypeName=VARCHAR_ARRAY_LIST, mode=IN, typeHandler=com.mom.backend.util.ListMapTypeHandler}
					                                          , #{userId,     jdbcType=VARCHAR, mode=IN} )}

insert.foreach:

INTO MOM_SHIFT_SCHEDULE (
             COMPANY_CD
           , DIVISION_CD
           , WORK_CENTER_CD
           , SHIFT_CD
           , APPLY_DATE
           , DAY_OFF_YN
           , PALN_SHUTDOWN_TIME
           , UNMANNED_OPERATION_TIME
           , DESCRIPTION
           , CREATE_BY
           , CREATE_DATE
           , UPDATE_BY
           , UPDATE_DATE )
 VALUES 
         (   #{item.companyCd, jdbcType=VARCHAR}
           , #{item.divisionCd, jdbcType=VARCHAR}
           , #{item.workCenterCd, jdbcType=VARCHAR}
           , #{item.shiftCd, jdbcType=VARCHAR}
           , #{item.applyDate, jdbcType=VARCHAR}
           , #{item.dayOffYn, jdbcType=VARCHAR}
           , #{item.palnShutdownTime, jdbcType=NUMERIC}
           , #{item.unmannedOperationTime, jdbcType=NUMERIC}
           , #{item.description, jdbcType=VARCHAR}
           , #{item.userId, jdbcType=VARCHAR}
           , MOM_COMMON_PKG.FN_GET_LOCAL_TIME(#{item.companyCd, jdbcType=VARCHAR},#{item.divisionCd, jdbcType=VARCHAR})
           , #{item.userId, jdbcType=VARCHAR}
           , MOM_COMMON_PKG.FN_GET_LOCAL_TIME(#{item.companyCd, jdbcType=VARCHAR},#{item.divisionCd, jdbcType=VARCHAR})
         )


INTO MOM_SHIFT_SCHEDULE (
             COMPANY_CD
           , DIVISION_CD
           , WORK_CENTER_CD
           , SHIFT_CD
           , APPLY_DATE
           , DAY_OFF_YN
           , PALN_SHUTDOWN_TIME
           , UNMANNED_OPERATION_TIME
           , DESCRIPTION
           , CREATE_BY
           , CREATE_DATE
           , UPDATE_BY
           , UPDATE_DATE )
 VALUES 
         (   #{item.companyCd, jdbcType=VARCHAR}
           , #{item.divisionCd, jdbcType=VARCHAR}
           , #{item.workCenterCd, jdbcType=VARCHAR}
           , #{item.shiftCd, jdbcType=VARCHAR}
           , #{item.applyDate, jdbcType=VARCHAR}
           , #{item.dayOffYn, jdbcType=VARCHAR}
           , #{item.palnShutdownTime, jdbcType=NUMERIC}
           , #{item.unmannedOperationTime, jdbcType=NUMERIC}
           , #{item.description, jdbcType=VARCHAR}
           , #{item.userId, jdbcType=VARCHAR}
           , MOM_COMMON_PKG.FN_GET_LOCAL_TIME(#{item.companyCd, jdbcType=VARCHAR},#{item.divisionCd, jdbcType=VARCHAR})
           , #{item.userId, jdbcType=VARCHAR}
           , MOM_COMMON_PKG.FN_GET_LOCAL_TIME(#{item.companyCd, jdbcType=VARCHAR},#{item.divisionCd, jdbcType=VARCHAR})
         )

update.foreach:
UPDATE MOM_SHIFT_SCHEDULE
SET    SHIFT_CD = #{item.shiftCd, jdbcType=VARCHAR}         
     , DAY_OFF_YN               = #{item.dayOffYn, jdbcType=VARCHAR}
     , PALN_SHUTDOWN_TIME	      = #{item.palnShutdownTime, jdbcType=NUMERIC}
     , UNMANNED_OPERATION_TIME  = #{item.unmannedOperationTime, jdbcType=NUMERIC}
     , DESCRIPTION = #{item.description, jdbcType=VARCHAR}
     , UPDATE_BY = #{item.userId, jdbcType=VARCHAR}
     , UPDATE_DATE = MOM_COMMON_PKG.FN_GET_LOCAL_TIME(#{item.companyCd, jdbcType=VARCHAR},#{item.divisionCd, jdbcType=VARCHAR})
WHERE  1=1
AND    COMPANY_CD = #{item.companyCd, jdbcType=VARCHAR}
AND    DIVISION_CD = #{item.divisionCd, jdbcType=VARCHAR}
AND    WORK_CENTER_CD = #{item.workCenterCd, jdbcType=VARCHAR}
AND    APPLY_DATE = #{item.applyDate, jdbcType=VARCHAR}

mapper.delete:
DELETE FROM MOM_SHIFT_SCHEDULE


  (       COMPANY_CD = #{item.companyCd, jdbcType=VARCHAR}
     AND  DIVISION_CD = #{item.divisionCd, jdbcType=VARCHAR}
     AND  WORK_CENTER_CD = #{item.workCenterCd, jdbcType=VARCHAR}
     AND  APPLY_DATE = #{item.applyDate, jdbcType=VARCHAR}
  )
