mapper.select:
SELECT A.* FROM (
SELECT MWM.COMPANY_CD , MWM.DIVISION_CD , MWM.USER_NO , MU.USER_NM , MWM.WAREHOUSE_CD , MWH.WAREHOUSE_NM , MWM.USE_YN , MWM.DESCRIPTION , MWM.UPDATE_BY AS UPDATE_USER_NO , (SELECT MOM_COMMON_PKG.FN_GET_USER_NAME(MWM.COMPANY_CD, MWM.DIVISION_CD, MWM.UPDATE_BY) FROM DUAL ) AS UPDATE_USER_NM , TO_CHAR(MWM.UPDATE_DATE ,'YYYY-MM-DD HH24:MI:SS') AS UPDATE_DATE , ROWIDTOCHAR(MWM.ROWID) AS KEY_ID , COUNT(*) OVER() AS TOTAL_COUNT , ROW_NUMBER() OVER(PARTITION BY MWM.COMPANY_CD,MWM.DIVISION_CD ORDER BY MWM.USER_NO, MWM.WAREHOUSE_CD) AS ROW_CNT FROM MOM_WAREHOUSE_MANAGER MWM , MOM_USER MU , MOM_WAREHOUSE MWH WHERE MWM.COMPANY_CD = MU.COMPANY_CD AND MWM.DIVISION_CD = MU.DIVISION_CD AND MWM.USER_NO = MU.USER_NO AND MWM.COMPANY_CD = MWH.COMPANY_CD AND MWM.DIVISION_CD = MWH.DIVISION_CD AND MWM.WAREHOUSE_CD = MWH.WAREHOUSE_CD AND MWM.COMPANY_CD = #{companyCd, jdbcType=VARCHAR} AND MWM.DIVISION_CD = #{divisionCd, jdbcType=VARCHAR}
AND (UPPER(MWM.USER_NO) LIKE '%' || UPPER(#{userNoNm, jdbcType=VARCHAR}) || '%' OR UPPER(MU.USER_NM) LIKE '%' || UPPER(#{userNoNm, jdbcType=VARCHAR}) || '%')
AND (UPPER(MWM.WAREHOUSE_CD) LIKE '%' || UPPER(#{warehouseCdNm, jdbcType=VARCHAR}) || '%' OR UPPER(MWH.WAREHOUSE_NM) LIKE '%' || UPPER(#{warehouseCdNm, jdbcType=VARCHAR}) || '%')
AND MWM.USE_YN = #{useYn, jdbcType=VARCHAR}
) A WHERE ROW_CNT BETWEEN #{startPage, jdbcType=INTEGER} AND #{endPage, jdbcType=INTEGER}

mapper.delete:
DELETE FROM MOM_PROC_WAREHOUSE_MANAGER


      (       COMPANY_CD  = #{item.companyCd, jdbcType=VARCHAR}
         AND  DIVISION_CD = #{item.divisionCd, jdbcType=VARCHAR}
         AND  CREATE_BY   = #{item.userId, jdbcType=VARCHAR}
      )

insert.foreach:

INTO MOM_PROC_WAREHOUSE_MANAGER( COMPANY_CD
                               , DIVISION_CD
                               , USER_NO 
                               , WAREHOUSE_CD 
                               , USE_YN
                               , DESCRIPTION
                               , DEL_YN
                               , VAL_MSG
                               , MULTI_MSG
                               , CREATE_BY
                               , CREATE_DATE
                               )
VALUES                         ( #{item.companyCd, jdbcType=VARCHAR}
                               , #{item.divisionCd, jdbcType=VARCHAR}
                               , #{item.userNo, jdbcType=VARCHAR}
                               , #{item.warehouseCd, jdbcType=VARCHAR}
                               , #{item.useYn, jdbcType=VARCHAR}
                               , #{item.description, jdbcType=VARCHAR}
                               , #{item.delYn, jdbcType=VARCHAR}
                               , #{item.valMsg, jdbcType=VARCHAR}
                               , #{item.multiMsg, jdbcType=VARCHAR}
                               , #{item.userId, jdbcType=VARCHAR}
                               , MOM_COMMON_PKG.FN_GET_LOCAL_TIME(#{item.companyCd, jdbcType=VARCHAR},#{item.divisionCd, jdbcType=VARCHAR})
                               )

mapper.select:

{ CALL MOM_MASTER_INFO_PKG.SP_UPSERT_WAREHOUSE_MANAGER( #{p_err_code,  jdbcType=VARCHAR, mode=OUT}
                                                      , #{p_err_msg,   jdbcType=VARCHAR, mode=OUT}
                                                      , #{companyCd,   jdbcType=VARCHAR, mode=IN}
                                                      , #{divisionCd,  jdbcType=VARCHAR, mode=IN} 
                                                      , #{actionMode,  jdbcType=VARCHAR, mode=IN}        
                                                      , #{userId,      jdbcType=VARCHAR, mode=IN} )}

SELECT NVL(VAL_MSG,'PASS') AS VAL_MSG
     , MULTI_MSG      
FROM   MOM_PROC_WAREHOUSE_MANAGER
WHERE  COMPANY_CD  = #{companyCd , jdbcType=VARCHAR}
AND    DIVISION_CD = #{divisionCd , jdbcType=VARCHAR}
AND    CREATE_BY    = #{userId , jdbcType=VARCHAR}

