mapper.select:
SELECT A.* FROM (
SELECT MWP.COMPANY_CD , MWP.DIVISION_CD , MWP.WAREHOUSE_CD , MWH.WAREHOUSE_NM , MWP.PROCESS_TYPE , (SELECT MOM_COMMON_PKG.FN_GET_PROCESS_TYPE_NAME(MWP.COMPANY_CD, MWP.DIVISION_CD, MWP.PROCESS_TYPE) FROM DUAL) AS PROCESS_TYPE_NM , MWP.USE_YN , MWP.DESCRIPTION , MWP.UPDATE_BY AS UPDATE_USER_NO , (SELECT MOM_COMMON_PKG.FN_GET_USER_NAME(MWP.COMPANY_CD, MWP.DIVISION_CD, MWP.UPDATE_BY) FROM DUAL ) AS UPDATE_USER_NM , TO_CHAR(MWP.UPDATE_DATE ,'YYYY-MM-DD HH24:MI:SS') AS UPDATE_DATE , ROWIDTOCHAR(MWP.ROWID) AS KEY_ID , COUNT(*) OVER() AS TOTAL_COUNT , ROW_NUMBER() OVER(PARTITION BY MWP.COMPANY_CD,MWP.DIVISION_CD ORDER BY MWP.WAREHOUSE_CD, MWP.PROCESS_TYPE) AS ROW_CNT FROM MOM_WAREHOUSE_PROCESS_TYPE MWP , MOM_WAREHOUSE MWH WHERE MWP.COMPANY_CD = MWH.COMPANY_CD AND MWP.DIVISION_CD = MWH.DIVISION_CD AND MWP.WAREHOUSE_CD = MWH.WAREHOUSE_CD AND MWP.COMPANY_CD = #{companyCd, jdbcType=VARCHAR} AND MWP.DIVISION_CD = #{divisionCd, jdbcType=VARCHAR}
AND (UPPER(MWP.WAREHOUSE_CD) LIKE '%' || UPPER(#{warehouseCdNm, jdbcType=VARCHAR}) || '%' OR UPPER(MWH.WAREHOUSE_NM) LIKE '%' || UPPER(#{warehouseCdNm, jdbcType=VARCHAR}) || '%')
AND MWP.PROCESS_TYPE = #{processType, jdbcType=VARCHAR}
AND MWP.USE_YN = #{useYn, jdbcType=VARCHAR}
) A WHERE ROW_CNT BETWEEN #{startPage, jdbcType=INTEGER} AND #{endPage, jdbcType=INTEGER}

mapper.delete:
DELETE FROM MOM_PROC_WAREHOUSE_PROCESS


      (       COMPANY_CD  = #{item.companyCd, jdbcType=VARCHAR}
         AND  DIVISION_CD = #{item.divisionCd, jdbcType=VARCHAR}
         AND  CREATE_BY   = #{item.userId, jdbcType=VARCHAR}
      )

insert.foreach:

INTO MOM_PROC_WAREHOUSE_PROCESS( COMPANY_CD
                               , DIVISION_CD
                               , WAREHOUSE_CD 
                               , PROCESS_TYPE 
                               , USE_YN
                               , DESCRIPTION
                               , DEL_YN
                               , VAL_MSG
                               , MULTI_MSG
                               , CREATE_BY
                               , CREATE_DATE
                               )
VALUES                         ( #{item.companyCd, jdbcType=VARCHAR}
                               , #{item.divisionCd, jdbcType=VARCHAR}
                               , #{item.warehouseCd, jdbcType=VARCHAR}
                               , #{item.processType, jdbcType=VARCHAR}
                               , #{item.useYn, jdbcType=VARCHAR}
                               , #{item.description, jdbcType=VARCHAR}
                               , #{item.delYn, jdbcType=VARCHAR}
                               , #{item.valMsg, jdbcType=VARCHAR}
                               , #{item.multiMsg, jdbcType=VARCHAR}
                               , #{item.userId, jdbcType=VARCHAR}
                               , MOM_COMMON_PKG.FN_GET_LOCAL_TIME(#{item.companyCd, jdbcType=VARCHAR},#{item.divisionCd, jdbcType=VARCHAR})
                               )

mapper.select:

{ CALL MOM_MASTER_INFO_PKG.SP_UPSERT_WAREHOUSE_PROCESS( #{p_err_code,  jdbcType=VARCHAR, mode=OUT}
                                                      , #{p_err_msg,   jdbcType=VARCHAR, mode=OUT}
                                                      , #{companyCd,   jdbcType=VARCHAR, mode=IN}
                                                      , #{divisionCd,  jdbcType=VARCHAR, mode=IN} 
                                                      , #{actionMode,  jdbcType=VARCHAR, mode=IN}        
                                                      , #{userId,      jdbcType=VARCHAR, mode=IN} )}

SELECT NVL(VAL_MSG,'PASS') AS VAL_MSG
     , MULTI_MSG      
FROM   MOM_PROC_WAREHOUSE_PROCESS
WHERE  COMPANY_CD  = #{companyCd , jdbcType=VARCHAR}
AND    DIVISION_CD = #{divisionCd , jdbcType=VARCHAR}
AND    CREATE_BY   = #{userId , jdbcType=VARCHAR}

