mapper.select:
SELECT A.* FROM (
SELECT MPR.PR_NO , MPR.PR_TYPE , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MPR.COMPANY_CD, MPR.DIVISION_CD, 'MM0001', MPR.PR_TYPE, #{langCd, jdbcType=VARCHAR}) FROM DUAL) AS PR_TYPE_NM , MPR.PR_TITLE , MPR.REFERENCE_NO , MPR.REQUEST_DEPT_CD , (SELECT MOM_COMMON_PKG.FN_GET_DEPARTMENT_NAME(MPR.COMPANY_CD, MPR.DIVISION_CD, MPR.REQUEST_DEPT_CD) FROM DUAL) AS REQUEST_DEPT_NM , MPR.REQUEST_REASON , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MPR.COMPANY_CD, MPR.DIVISION_CD, 'MM0003', MPR.REQUEST_REASON, #{langCd, jdbcType=VARCHAR}) FROM DUAL) AS REQUEST_REASON_NM , TO_CHAR(MPR.REQUEST_DATE ,'YYYY-MM-DD') AS REQUEST_DATE , MPR.REQUEST_USER_NO , (SELECT MOM_COMMON_PKG.FN_GET_USER_NAME(MPR.COMPANY_CD, MPR.DIVISION_CD, MPR.REQUEST_USER_NO) FROM DUAL ) AS REQUEST_USER_NM , TO_CHAR(MPR.CONFIRM_DATE ,'YYYY-MM-DD') AS CONFIRM_DATE , MPR.CONFIRM_USER_NO , (SELECT MOM_COMMON_PKG.FN_GET_USER_NAME(MPR.COMPANY_CD, MPR.DIVISION_CD, MPR.CONFIRM_USER_NO) FROM DUAL ) AS CONFIRM_USER_NM , MPR.EXPECTED_AMOUNT , MPR.DESCRIPTION , MPR.COMPANY_CD , MPR.DIVISION_CD , ROWIDTOCHAR(MPR.ROWID) AS KEY_ID , COUNT(*) OVER() AS TOTAL_COUNT , ROW_NUMBER() OVER(PARTITION BY MPR.COMPANY_CD,MPR.DIVISION_CD ORDER BY MPR.PR_NO) AS ROW_CNT FROM MOM_PR_MST MPR WHERE MPR.COMPANY_CD = #{companyCd, jdbcType=VARCHAR} AND MPR.DIVISION_CD = #{divisionCd, jdbcType=VARCHAR} AND MPR.STATE = 'PRG' AND MPR.PR_TYPE = '10' AND MPR.REQUEST_DATE BETWEEN TO_DATE(#{requestDateSD, jdbcType=VARCHAR}, 'YYYY-MM-DD') AND TO_DATE(#{requestDateED, jdbcType=VARCHAR}, 'YYYY-MM-DD') + 0.99999
AND (UPPER(MI.ITEM_ID) LIKE '%' || UPPER(#{itemIdNm, jdbcType=VARCHAR}) || '%' OR UPPER(MI.ITEM_NM) LIKE '%' || UPPER(#{itemIdNm, jdbcType=VARCHAR}) || '%')
AND (UPPER(MPR.PR_NO) LIKE '%' || UPPER(#{prNo, jdbcType=VARCHAR}) || '%')
AND (UPPER(MPR.REFERENCE_NO) LIKE '%' || UPPER(#{referenceNo, jdbcType=VARCHAR}) || '%')
AND MPR.REQUEST_DEPT_CD = #{requestDeptCd, jdbcType=VARCHAR}
AND (UPPER(MPR.PR_TITLE) LIKE '%' || UPPER(#{prTitle, jdbcType=VARCHAR}) || '%')
ORDER BY MPR.REQUEST_DATE , MPR.PR_TYPE
) A WHERE ROW_CNT BETWEEN #{startPage, jdbcType=INTEGER} AND #{endPage, jdbcType=INTEGER}

mapper.delete:
DELETE FROM MOM_PROC_PR_MST


(       COMPANY_CD  = #{item.companyCd, jdbcType=VARCHAR}
   AND  DIVISION_CD = #{item.divisionCd, jdbcType=VARCHAR}
   AND  CREATE_BY   = #{item.userId, jdbcType=VARCHAR}
)

insert.foreach:

	    INTO MOM_PROC_PR_MST( COMPANY_CD
		                    , DIVISION_CD
			                , PR_NO
			                , PR_TYPE
			                , PR_TITLE
			                , REFERENCE_NO
			                , REQUEST_DATE
			                , REQUEST_REASON
			                , DESCRIPTION
			                , DEL_YN
			                , VAL_MSG
			                , MULTI_MSG
			                , CREATE_BY
			                , CREATE_DATE )
        VALUES              ( #{item.companyCd, jdbcType=VARCHAR}
			                , #{item.divisionCd, jdbcType=VARCHAR}
			                , #{item.prNo, jdbcType=VARCHAR}
			                , #{item.prType, jdbcType=VARCHAR}
			                , #{item.prTitle, jdbcType=VARCHAR}
			                , #{item.referenceNo, jdbcType=VARCHAR}
			                , TO_DATE(#{item.requestDate, jdbcType=VARCHAR},'YYYY-MM-DD')
			                , #{item.requestReason, jdbcType=VARCHAR}
			                , #{item.description, jdbcType=VARCHAR}
			                , #{item.delYn, jdbcType=VARCHAR}
			                , #{item.valMsg, jdbcType=VARCHAR}
			                , #{item.multiMsg, jdbcType=VARCHAR}
			                , #{item.userId, jdbcType=VARCHAR}
			                , MOM_COMMON_PKG.FN_GET_LOCAL_TIME(#{item.companyCd, jdbcType=VARCHAR},#{item.divisionCd, jdbcType=VARCHAR})
			                )

mapper.select:

        { CALL MOM_MATERIAL_PKG.SP_UPSERT_PR_MST( #{p_err_code,  jdbcType=VARCHAR, mode=OUT}
	                                            , #{p_err_msg,   jdbcType=VARCHAR, mode=OUT}
	                                            , #{companyCd,   jdbcType=VARCHAR, mode=IN}
	                                            , #{divisionCd,  jdbcType=VARCHAR, mode=IN}											  
	                                            , #{userId,      jdbcType=VARCHAR, mode=IN} )}

