mapper.select:
SELECT A.* FROM (
SELECT MPM.PO_NO , MPD.PO_SEQ , MPD.ITEM_ID , TO_CHAR(MPM.CONFIRM_DATE ,'YYYY-MM-DD') AS CONFIRM_DATE , MPM.CONFIRM_USER_NO , (SELECT MOM_COMMON_PKG.FN_GET_USER_NAME(MPM.COMPANY_CD, MPM.DIVISION_CD, MPM.CONFIRM_USER_NO) FROM DUAL ) AS CONFIRM_USER_NM , TO_CHAR(MPM.PO_DUE_DATE ,'YYYY-MM-DD') AS PO_DUE_DATE , MPD.PO_QTY , MPD.PROGRESS_QTY , MPD.CLOSE_QTY AS PO_CLOSE_QTY , MPD.PO_QTY - NVL(MPD.PROGRESS_QTY,0) - NVL(MPD.CLOSE_QTY,0) AS REMAIN_QTY , ( SELECT SUM(NVL(MPID.RECEIPT_QTY,0)) FROM MOM_PURCHASE_INOUT_DTL MPID WHERE MPID.COMPANY_CD = MPD.COMPANY_CD AND MPID.DIVISION_CD = MPD.DIVISION_CD AND MPID.REFERENCE_DTL_NO = MPD.PURCHASE_ORDER_ID) AS PURCHASE_RECEIPT_QTY , MPM.CURRENCY_CD , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MPM.COMPANY_CD, MPM.DIVISION_CD, 'SM0017', MPM.CURRENCY_CD, #{langCd, jdbcType=VARCHAR}) FROM DUAL) AS CURRENCY_NM , MPM.EXCHANGE_RATE , MPD.UNIT_PRICE , MPD.AMOUNT , MPD.LOCAL_AMOUNT , MPD.WAREHOUSE_CD AS RECEIPT_WAREHOUSE_CD , (SELECT MOM_COMMON_PKG.FN_GET_WAREHOUSE_NAME(MPD.COMPANY_CD,MPD.DIVISION_CD,MPD.WAREHOUSE_CD) FROM DUAL) AS RECEIPT_WAREHOUSE_NM , MPD.IQC_FLAG , MPD.LOT_MANAGEMENT_YN , MPD.PO_OVER_RECEIPT_RATE , MI.MAKER , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MPD.COMPANY_CD, MPD.DIVISION_CD, 'MD0007', MI.MAKER, #{langCd, jdbcType=VARCHAR}) FROM DUAL) AS MAKER_NM , MP.PARTNER_CD , MP.PARTNER_NM , MPM.PO_REFERENCE_TYPE , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MPM.COMPANY_CD, MPM.DIVISION_CD, 'MM0005', MPM.PO_REFERENCE_TYPE, #{langCd, jdbcType=VARCHAR}) FROM DUAL) AS PO_REFERENCE_TYPE_NM , MPM.PO_TYPE , (SELECT MOM_COMMON_PKG.FN_GET_PO_TYPE_NAME(MPM.COMPANY_CD, MPM.DIVISION_CD, MPM.PO_TYPE) FROM DUAL) AS PO_TYPE_NM , MI.PROCUREMENT_TYPE , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MPD.COMPANY_CD, MPD.DIVISION_CD, 'MD0004', MI.PROCUREMENT_TYPE, #{langCd, jdbcType=VARCHAR}) FROM DUAL) AS PROCUREMENT_TYPE_NM , MI.ITEM_NM , MI.ITEM_SPEC , MI.ITEM_TYPE , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MPD.COMPANY_CD, MPD.DIVISION_CD, 'MD0002', MI.ITEM_TYPE, #{langCd, jdbcType=VARCHAR}) FROM DUAL) AS ITEM_TYPE_NM , MPD.PURCHASE_UOM , (SELECT MOM_COMMON_PKG.FN_GET_UNIT_NAME(MPD.COMPANY_CD, MPD.DIVISION_CD, MPD.PURCHASE_UOM) FROM DUAL) AS PURCHASE_UOM_NM , MI.UOM , (SELECT MOM_COMMON_PKG.FN_GET_UNIT_NAME(MI.COMPANY_CD, MI.DIVISION_CD, MI.UOM) FROM DUAL) AS UOM_NM , MPD.CREATE_BY AS CREATE_USER_NO , (SELECT MOM_COMMON_PKG.FN_GET_USER_NAME(MPD.COMPANY_CD, MPD.DIVISION_CD, MPD.CREATE_BY) FROM DUAL ) AS CREATE_USER_NM , MPM.CREATE_DEPT_CD , (SELECT MOM_COMMON_PKG.FN_GET_DEPARTMENT_NAME(MPM.COMPANY_CD, MPM.DIVISION_CD, MPM.CREATE_DEPT_CD) FROM DUAL) AS CREATE_DEPT_NM , TO_CHAR(MPD.CREATE_DATE ,'YYYY-MM-DD') AS CREATE_DATE , MPD.PURCHASE_ORDER_ID , ROWIDTOCHAR(MPD.ROWID) AS KEY_ID , MPM.DO_INVOICE_YN , MPD.STATE , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MPD.COMPANY_CD, MPD.DIVISION_CD, 'MM0011', MPD.STATE, #{langCd, jdbcType=VARCHAR}) FROM DUAL) AS STATE_NM , MPM.TAX_TYPE , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MPM.COMPANY_CD, MPM.DIVISION_CD, 'SM0029', MPM.TAX_TYPE, #{langCd, jdbcType=VARCHAR}) FROM DUAL) AS TAX_TYPE_NM , MPD.STOCK_TYPE , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MPD.COMPANY_CD, MPD.DIVISION_CD, 'MD0048', MPD.STOCK_TYPE, #{langCd, jdbcType=VARCHAR}) FROM DUAL) AS STOCK_TYPE_NM , COUNT(*) OVER() AS TOTAL_COUNT , ROW_NUMBER() OVER(PARTITION BY MPD.COMPANY_CD,MPD.DIVISION_CD ORDER BY MPD.PURCHASE_ORDER_ID ) AS ROW_CNT FROM MOM_PO_MST MPM , MOM_PO_DTL MPD , MOM_PARTNER MP , MOM_ITEM MI WHERE MPM.COMPANY_CD = MPD.COMPANY_CD AND MPM.DIVISION_CD = MPD.DIVISION_CD AND MPM.PO_NO = MPD.PO_NO AND MPM.COMPANY_CD = MP.COMPANY_CD AND MPM.DIVISION_CD = MP.DIVISION_CD AND MPM.VENDOR_CD = MP.PARTNER_CD AND MPD.COMPANY_CD = MI.COMPANY_CD AND MPD.DIVISION_CD = MI.DIVISION_CD AND MPD.ITEM_ID = MI.ITEM_ID AND MPM.COMPANY_CD = #{companyCd, jdbcType=VARCHAR} AND MPM.DIVISION_CD = #{divisionCd, jdbcType=VARCHAR} AND MPM.CONFIRM_DATE BETWEEN TO_DATE(#{confirmDateSD, jdbcType=VARCHAR}, 'YYYY-MM-DD') AND TO_DATE(#{confirmDateED, jdbcType=VARCHAR}, 'YYYY-MM-DD') + 0.99999 AND MPM.PO_REFERENCE_TYPE IN ('10','20')
AND (UPPER(MPM.PO_NO) LIKE '%' || UPPER(#{poNo, jdbcType=VARCHAR}) || '%')
AND MPD.CREATE_BY = #{poUserNo, jdbcType=VARCHAR}
AND (UPPER(MI.ITEM_ID) LIKE '%' || UPPER(#{itemIdNm, jdbcType=VARCHAR}) || '%' OR UPPER(MI.ITEM_NM) LIKE '%' || UPPER(#{itemIdNm, jdbcType=VARCHAR}) || '%')
AND (UPPER(MPM.VENDOR_CD) LIKE '%' || UPPER(#{partnerCdNm, jdbcType=VARCHAR}) || '%' OR UPPER(MP.PARTNER_NM) LIKE '%' || UPPER(#{partnerCdNm, jdbcType=VARCHAR}) || '%')
AND (UPPER(MPM.PO_TITLE) LIKE '%' || UPPER(#{poTitle, jdbcType=VARCHAR}) || '%')
AND MPM.CREATE_DEPT_CD = #{poDeptCd, jdbcType=VARCHAR}
AND MPM.PO_TYPE IN (${poCategory})
AND MPM.PO_REFERENCE_TYPE = #{poReferenceType, jdbcType=VARCHAR}
AND MPD.STATE = #{state, jdbcType=VARCHAR}
) A WHERE ROW_CNT BETWEEN #{startPage, jdbcType=INTEGER} AND #{endPage, jdbcType=INTEGER}

mapper.delete:
DELETE FROM MOM_PROC_PO_CLOSE


(       COMPANY_CD  = #{item.companyCd, jdbcType=VARCHAR}
   AND  DIVISION_CD = #{item.divisionCd, jdbcType=VARCHAR}
   AND  CREATE_BY   = #{item.userId, jdbcType=VARCHAR}
)

insert.foreach:

            INTO   MOM_PROC_PO_CLOSE( COMPANY_CD
			                		, DIVISION_CD
					                , PO_NO
					                , PURCHASE_ORDER_ID
					                , DO_INVOICE_YN
					                , VAL_MSG
					                , MULTI_MSG
					                , DESCRIPTION
					                , CREATE_BY
					                , CREATE_DATE )
			VALUES                  ( #{item.companyCd, jdbcType=VARCHAR}
					                , #{item.divisionCd, jdbcType=VARCHAR}
					                , #{item.poNo, jdbcType=VARCHAR}
					                , #{item.purchaseOrderId, jdbcType=VARCHAR}
					                , #{item.doInvoiceYn, jdbcType=VARCHAR}
					                , #{item.valMsg, jdbcType=VARCHAR}
					                , #{item.multiMsg, jdbcType=VARCHAR}
					                , #{item.description, jdbcType=VARCHAR}
					                , #{item.userId, jdbcType=VARCHAR}
					                , MOM_COMMON_PKG.FN_GET_LOCAL_TIME(#{item.companyCd, jdbcType=VARCHAR},#{item.divisionCd, jdbcType=VARCHAR})
					              )

mapper.select:

        { CALL MOM_MATERIAL_PKG.SP_PO_CLOSE( #{p_err_code,  jdbcType=VARCHAR, mode=OUT}
					                       , #{p_err_msg,   jdbcType=VARCHAR, mode=OUT}
					                       , #{companyCd,   javaType=string,  jdbcType=VARCHAR , mode=IN}
					                       , #{divisionCd,  jdbcType=VARCHAR, mode=IN}											  
					                       , #{userId,      jdbcType=VARCHAR, mode=IN} )}
