mapper.select:
SELECT MPIM.COMPANY_CD , MPIM.DIVISION_CD , MPIM.INOUT_NO AS DEPARTURE_NO , ROWIDTOCHAR(MPIM.ROWID) AS KEY_ID , MPIM.PROCESS_CD , (SELECT MOM_COMMON_PKG.FN_GET_PROCESS_NAME(MPIM.COMPANY_CD, MPIM.DIVISION_CD, MPIM.PROCESS_CD) FROM DUAL) AS PROCESS_NM , MPIM.TITLE AS DEPARTURE_TITLE , MP.PARTNER_CD , MP.PARTNER_NM , MPIM.DEPARTURE_VENDOR_CD , (SELECT MOM_COMMON_PKG.FN_GET_PARTNER_NAME(MPIM.COMPANY_CD,MPIM.DIVISION_CD,MPIM.DEPARTURE_VENDOR_CD) FROM DUAL) AS DEPARTURE_VENDOR_NM , MPIM.WAREHOUSE_CD AS DEPARTURE_WAREHOUSE_CD , (SELECT MOM_COMMON_PKG.FN_GET_WAREHOUSE_NAME(MPIM.COMPANY_CD,MPIM.DIVISION_CD,MPIM.WAREHOUSE_CD) FROM DUAL) AS DEPARTURE_WAREHOUSE_NM , MPIM.IQC_FLAG , TO_CHAR(MPIM.DEPARTURE_DATE,'YYYY-MM-DD') AS DEPARTURE_DATE , MPIM.CURRENCY_CD , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MPIM.COMPANY_CD, MPIM.DIVISION_CD, 'SM0017', MPIM.CURRENCY_CD, #{langCd, jdbcType=VARCHAR}) FROM DUAL) AS CURRENCY_NM , MPIM.EXCHANGE_RATE , MPIM.AMOUNT AS DEPARTURE_AMOUNT , MPIM.VENDOR_INVOICE , MPIM.TAX_TYPE , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MPIM.COMPANY_CD, MPIM.DIVISION_CD, 'SM0029', MPIM.TAX_TYPE, #{langCd, jdbcType=VARCHAR}) FROM DUAL) AS TAX_TYPE_NM , MPIM.DESCRIPTION , MPIM.CREATE_BY AS CREATE_USER_NO , (SELECT MOM_COMMON_PKG.FN_GET_USER_NAME(MPIM.COMPANY_CD, MPIM.DIVISION_CD, MPIM.CREATE_BY) FROM DUAL ) AS CREATE_USER_NM , TO_CHAR(MPIM.CREATE_DATE,'YYYY-MM-DD') AS CREATE_DATE FROM MOM_PURCHASE_INOUT_MST MPIM , MOM_PARTNER MP WHERE MPIM.COMPANY_CD = MP.COMPANY_CD AND MPIM.DIVISION_CD = MP.DIVISION_CD AND MPIM.VENDOR_CD = MP.PARTNER_CD AND MPIM.COMPANY_CD = #{companyCd, jdbcType=VARCHAR} AND MPIM.DIVISION_CD = #{divisionCd, jdbcType=VARCHAR} AND MPIM.CREATE_DATE BETWEEN TO_DATE(#{createDateSD, jdbcType=VARCHAR}, 'YYYY-MM-DD') AND TO_DATE(#{createDateED, jdbcType=VARCHAR}, 'YYYY-MM-DD') + 0.99999 AND MPIM.STATE = 'PIG' -- 등록 AND MPIM.PROCESS_CD = 'OP' -- 외주임가공실적 AND MPIM.VENDOR_CD = #{partnerCd, jdbcType=VARCHAR}
AND UPPER(MPIM.INOUT_NO) LIKE '%' || UPPER(#{departureNo, jdbcType=VARCHAR}) || '%'
AND UPPER(MPIM.TITLE) LIKE '%' || UPPER(#{departureTitle, jdbcType=VARCHAR}) || '%'
AND MPIM.IQC_FLAG = #{iqcFlag, jdbcType=VARCHAR}

mapper.delete:
DELETE FROM MOM_PROC_PI_MST


(       COMPANY_CD  = #{item.companyCd, jdbcType=VARCHAR}
   AND  DIVISION_CD = #{item.divisionCd, jdbcType=VARCHAR}
   AND  PROCESS_CD  = 'OP' 
   AND  CREATE_BY   = #{item.userId, jdbcType=VARCHAR}
)

insert.foreach:

        INTO MOM_PROC_PI_MST( COMPANY_CD
			                , DIVISION_CD
			                , INOUT_NO
			                , PROCESS_CD
			                , TITLE
			                , IQC_FLAG
			                , VENDOR_CD
			                , BL_NO
			                , IP_NO
			                , IP_CURRENCY_CD
			                , IP_EXCHANGE_RATE
			                , CC_DATE
			                , LC_NO
			                , DEPARTURE_DATE
			                , DEPARTURE_VENDOR_CD
			                , WAREHOUSE_CD
			                , CURRENCY_CD
			                , EXCHANGE_RATE
			                , VENDOR_INVOICE
			                , FREE_YN
			                , TAX_TYPE
			                , DESCRIPTION
			                , DEL_YN
			                , VAL_MSG
			                , MULTI_MSG
			                , CREATE_BY
			                , CREATE_DATE )
        VALUES              ( #{item.companyCd, jdbcType=VARCHAR}
			                , #{item.divisionCd, jdbcType=VARCHAR}
			                , #{item.departureNo, jdbcType=VARCHAR}
			                , 'OP' 
			                , #{item.departureTitle, jdbcType=VARCHAR}
			                , #{item.iqcFlag, jdbcType=VARCHAR}
			                , #{item.partnerCd, jdbcType=VARCHAR}
			                , #{item.blNo, jdbcType=VARCHAR}
			                , #{item.ipNo, jdbcType=VARCHAR}
			                , #{item.ipCurrencyCd, jdbcType=VARCHAR}
			                , #{item.ipExchangeRate, jdbcType=NUMERIC}
			                , TO_DATE(#{item.ccDate, jdbcType=VARCHAR},'YYYY-MM-DD HH24:MI:SS')
			                , #{item.lcNo, jdbcType=VARCHAR}
			                , TO_DATE(#{item.departureDate, jdbcType=VARCHAR},'YYYY-MM-DD HH24:MI:SS')
			                , #{item.departureVendorCd, jdbcType=VARCHAR}
			                , #{item.departureWarehouseCd, jdbcType=VARCHAR}
			                , #{item.currencyCd, jdbcType=VARCHAR}
			                , #{item.exchangeRate, jdbcType=NUMERIC}
			                , #{item.vendorInvoice, jdbcType=VARCHAR}
			                , #{item.freeYn, jdbcType=VARCHAR}
			                , #{item.taxType, jdbcType=VARCHAR}
			                , #{item.description, jdbcType=VARCHAR}
			                , #{item.delYn, jdbcType=VARCHAR}
			                , #{item.valMsg, jdbcType=VARCHAR}
			                , #{item.multiMsg, jdbcType=VARCHAR}
			                , #{item.userId, jdbcType=VARCHAR}
			                , MOM_COMMON_PKG.FN_GET_LOCAL_TIME(#{item.companyCd, jdbcType=VARCHAR},#{item.divisionCd, jdbcType=VARCHAR})
			                )

mapper.select:

        { CALL MOM_MATERIAL_PKG.SP_UPSERT_PI_MST( #{p_err_code,  jdbcType=VARCHAR, mode=OUT}
					                            , #{p_err_msg,   jdbcType=VARCHAR, mode=OUT}
					                            , #{companyCd,   jdbcType=VARCHAR, mode=IN}
					                            , #{divisionCd,  jdbcType=VARCHAR, mode=IN}		
					                            , 'OP' 							  
					                            , #{userId,      jdbcType=VARCHAR, mode=IN} )}

mapper.delete:
DELETE FROM MOM_PROC_PI_DTL


(       COMPANY_CD  = #{item.companyCd, jdbcType=VARCHAR}
   AND  DIVISION_CD = #{item.divisionCd, jdbcType=VARCHAR}
   AND  PROCESS_CD  = 'OP' 
   AND  CREATE_BY   = #{item.userId, jdbcType=VARCHAR}
)

insert.foreach:

            INTO MOM_PROC_PI_DTL( COMPANY_CD
				                , DIVISION_CD
				                , PROCESS_CD
				                , INOUT_NO
				                , PURCHASE_INOUT_ID
				                , IO_TYPE
				                , ITEM_ID
		                        , INOUT_UOM
		                        , INOUT_QTY
		                        , GOOD_QTY
			                    , DEFECT_QTY
		                        , CURRENCY_CD
		                        , EXCHANGE_RATE
		                        , UNIT_PRICE
		                        , PO_OVER_RECEIPT_RATE
		                        , LOT_USE_YN
	                            , LOT_RULE
	                            , EFFECTIVE_DATE
				                , REFERENCE_NO
				                , REFERENCE_DTL_NO
				                , DESCRIPTION
				                , DEL_YN
				                , VAL_MSG
				                , MULTI_MSG
				                , CREATE_BY
				                , CREATE_DATE )
            VALUES              ( #{item.companyCd, jdbcType=VARCHAR}
				                , #{item.divisionCd, jdbcType=VARCHAR}
				                , 'OP' 
				                , #{item.departureNo, jdbcType=VARCHAR}
				                , #{item.purchaseInoutId, jdbcType=VARCHAR}
				                , #{item.ioType, jdbcType=VARCHAR}
				                , #{item.itemId, jdbcType=VARCHAR}
			                    , #{item.purchaseUom, jdbcType=VARCHAR}
			                    , #{item.goodQty, jdbcType=NUMERIC}
			                    , #{item.goodQty, jdbcType=NUMERIC}
			                    , #{item.defectQty, jdbcType=NUMERIC}
			                    , #{item.currencyCd, jdbcType=VARCHAR}
			                    , #{item.exchangeRate, jdbcType=NUMERIC}
			                    , #{item.unitPrice, jdbcType=NUMERIC}
			                    , #{item.poOverReceiptRate, jdbcType=NUMERIC}
			                    , #{item.lotManagementYn, jdbcType=VARCHAR}
                                , #{item.lotRule, jdbcType=VARCHAR}
                                , TO_DATE(TO_CHAR(TO_DATE(#{item.effectiveDate, jdbcType=VARCHAR},'YYYY-MM-DD HH24:MI:SS'),'YYYY-MM-DD'),'YYYY-MM-DD')	
				                , #{item.referenceNo, jdbcType=VARCHAR}
				                , #{item.referenceDtlNo, jdbcType=VARCHAR}
				                , #{item.description, jdbcType=VARCHAR}
				                , #{item.delYn, jdbcType=VARCHAR}
				                , #{item.valMsg, jdbcType=VARCHAR}
				                , #{item.multiMsg, jdbcType=VARCHAR}
				                , #{item.userId, jdbcType=VARCHAR}
				                , MOM_COMMON_PKG.FN_GET_LOCAL_TIME(#{item.companyCd, jdbcType=VARCHAR},#{item.divisionCd, jdbcType=VARCHAR})
				                )

mapper.select:

        { CALL MOM_MATERIAL_PKG.SP_UPSERT_PI_DTL( #{p_err_code,  jdbcType=VARCHAR, mode=OUT}
	                                            , #{p_err_msg,   jdbcType=VARCHAR, mode=OUT}
	                                            , #{companyCd,   jdbcType=VARCHAR, mode=IN}
	                                            , #{divisionCd,  jdbcType=VARCHAR, mode=IN}		
	                                            , 'OP'						  
