mapper.select:
SELECT MPIM.COMPANY_CD , MPIM.DIVISION_CD , MPIM.INOUT_NO AS DEPARTURE_NO , ROWIDTOCHAR(MPIM.ROWID) AS KEY_ID , MPIM.PROCESS_CD , (SELECT MOM_COMMON_PKG.FN_GET_PROCESS_NAME(MPIM.COMPANY_CD, MPIM.DIVISION_CD, MPIM.PROCESS_CD) FROM DUAL) AS PROCESS_NM , MPIM.TITLE AS DEPARTURE_TITLE , MP.PARTNER_CD , MP.PARTNER_NM , MPIM.DEPARTURE_VENDOR_CD , (SELECT MOM_COMMON_PKG.FN_GET_PARTNER_NAME(MPIM.COMPANY_CD,MPIM.DIVISION_CD,MPIM.DEPARTURE_VENDOR_CD) FROM DUAL) AS DEPARTURE_VENDOR_NM , MPIM.WAREHOUSE_CD AS DEPARTURE_WAREHOUSE_CD , (SELECT MOM_COMMON_PKG.FN_GET_WAREHOUSE_NAME(MPIM.COMPANY_CD,MPIM.DIVISION_CD,MPIM.WAREHOUSE_CD) FROM DUAL) AS DEPARTURE_WAREHOUSE_NM , MPIM.IQC_FLAG , MPIM.CURRENCY_CD , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MPIM.COMPANY_CD, MPIM.DIVISION_CD, 'SM0017', MPIM.CURRENCY_CD, #{langCd, jdbcType=VARCHAR}) FROM DUAL) AS CURRENCY_NM , MPIM.EXCHANGE_RATE , MPIM.AMOUNT AS DEPARTURE_AMOUNT , TO_CHAR(MPIM.DEPARTURE_DATE ,'YYYY-MM-DD') AS DEPARTURE_DATE , MPIM.VENDOR_INVOICE , MPIM.FREE_YN , MPIM.TAX_TYPE , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MPIM.COMPANY_CD, MPIM.DIVISION_CD, 'SM0029', MPIM.TAX_TYPE, #{langCd, jdbcType=VARCHAR}) FROM DUAL) AS TAX_TYPE_NM , MPIM.DESCRIPTION , MPIM.CONFIRM_USER_NO , (SELECT MOM_COMMON_PKG.FN_GET_USER_NAME(MPIM.COMPANY_CD, MPIM.DIVISION_CD, MPIM.CONFIRM_USER_NO) FROM DUAL ) AS CONFIRM_USER_NM , TO_CHAR(MPIM.CONFIRM_DATE ,'YYYY-MM-DD') AS CONFIRM_DATE FROM MOM_PURCHASE_INOUT_MST MPIM , MOM_PARTNER MP WHERE MPIM.COMPANY_CD = MP.COMPANY_CD AND MPIM.DIVISION_CD = MP.DIVISION_CD AND MPIM.VENDOR_CD = MP.PARTNER_CD AND MPIM.COMPANY_CD = #{companyCd, jdbcType=VARCHAR} AND MPIM.DIVISION_CD = #{divisionCd, jdbcType=VARCHAR} AND MPIM.CONFIRM_DATE BETWEEN TO_DATE(#{confirmDateSD, jdbcType=VARCHAR}, 'YYYY-MM-DD') AND TO_DATE(#{confirmDateED, jdbcType=VARCHAR}, 'YYYY-MM-DD') + 0.99999 AND MPIM.STATE = 'PIF' -- 확정 AND MPIM.PROCESS_CD = 'OP' AND MPIM.VENDOR_CD = #{partnerCd, jdbcType=VARCHAR}
AND UPPER(MPIM.INOUT_NO) LIKE '%' || UPPER(#{departureNo, jdbcType=VARCHAR}) || '%'
AND UPPER(MPIM.TITLE) LIKE '%' || UPPER(#{departureTitle, jdbcType=VARCHAR}) || '%'
AND MPIM.IQC_FLAG = #{iqcFlag, jdbcType=VARCHAR}

mapper.delete:
DELETE FROM MOM_PROC_PI_CONFIRM_CANCEL


      (    COMPANY_CD  = #{item.companyCd, jdbcType=VARCHAR}
      AND  DIVISION_CD = #{item.divisionCd, jdbcType=VARCHAR}
      AND  PROCESS_CD  = 'OP'
      AND  CREATE_BY   = #{item.userId, jdbcType=VARCHAR}
      )

insert.foreach:

        INTO MOM_PROC_PI_CONFIRM_CANCEL( COMPANY_CD
		                               , DIVISION_CD
		                               , PROCESS_CD
		                               , INOUT_NO
		                               , DESCRIPTION
		                               , VAL_MSG
		                               , MULTI_MSG
		                               , CREATE_BY
		                               , CREATE_DATE )
        VALUES                         ( #{item.companyCd, jdbcType=VARCHAR}
                                       , #{item.divisionCd, jdbcType=VARCHAR}
                                       , 'OP'
	                                   , #{item.departureNo, jdbcType=VARCHAR}
	                                   , #{item.description, jdbcType=VARCHAR}
	                                   , #{item.valMsg, jdbcType=VARCHAR}
	                                   , #{item.multiMsg, jdbcType=VARCHAR}
	                                   , #{item.userId, jdbcType=VARCHAR}
	                                   , MOM_COMMON_PKG.FN_GET_LOCAL_TIME(#{item.companyCd, jdbcType=VARCHAR},#{item.divisionCd, jdbcType=VARCHAR})
	                                   )

mapper.select:

    { CALL MOM_MATERIAL_PKG.SP_PI_CONFIRM_CANCEL( #{p_err_code,  jdbcType=VARCHAR, mode=OUT}
	                                            , #{p_err_msg,   jdbcType=VARCHAR, mode=OUT}
	                                            , #{companyCd,   jdbcType=VARCHAR , mode=IN}
	                                            , #{divisionCd,  jdbcType=VARCHAR, mode=IN}    
	                                            , 'OP'                   
	                                            , #{userId,      jdbcType=VARCHAR, mode=IN} )}
