mapper.select:
SELECT A.* FROM (
SELECT MPM.COMPANY_CD , MPM.DIVISION_CD , MPM.INOUT_NO AS RECEIPT_NO , MPD.SEQ , MPD.PURCHASE_INOUT_ID AS RECEIPT_DETAIL_NO , TO_CHAR(MPD.RECEIPT_DATE ,'YYYY-MM-DD') AS RECEIPT_DATE , MPD.IO_TYPE , (SELECT MOM_COMMON_PKG.FN_GET_IO_TYPE_NAME(MPD.COMPANY_CD, MPD.DIVISION_CD, MPD.IO_TYPE) FROM DUAL) AS IO_TYPE_NM , MPM.WAREHOUSE_CD AS RECEIPT_WAREHOUSE_CD , MWH.WAREHOUSE_NM AS RECEIPT_WAREHOUSE_NM , MPD.ITEM_ID , MI.ITEM_NM , MI.ITEM_SPEC , MI.ITEM_TYPE , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MI.COMPANY_CD, MI.DIVISION_CD, 'MD0002', MI.ITEM_TYPE, #{langCd, jdbcType=VARCHAR}) FROM DUAL) AS ITEM_TYPE_NM , MPD.INOUT_QTY , MPD.CONVERSION_QTY , MPD.INOUT_UOM AS , (SELECT MOM_COMMON_PKG.FN_GET_UNIT_NAME(MPD.COMPANY_CD, MPD.DIVISION_CD, MPD.INOUT_UOM) FROM DUAL) AS PURCHASE_UOM_NM , MPD.CONVERSION_UOM , (SELECT MOM_COMMON_PKG.FN_GET_UNIT_NAME(MPD.COMPANY_CD, MPD.DIVISION_CD, MPD.CONVERSION_UOM) FROM DUAL) AS CONVERSION_UOM_NM , MPD.RECEIPT_QTY , MPM.VENDOR_CD AS PARTNER_CD , MP.PARTNER_NM , MPM.DEPARTURE_VENDOR_CD , (SELECT MOM_COMMON_PKG.FN_GET_PARTNER_NAME(MPM.COMPANY_CD,MPM.DIVISION_CD, MPM.DEPARTURE_VENDOR_CD) FROM DUAL) AS DEPARTURE_VENDOR_NM , MPM.CURRENCY_CD , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MPM.COMPANY_CD, MPM.DIVISION_CD, 'SM0017', MPM.CURRENCY_CD, #{langCd, jdbcType=VARCHAR}) FROM DUAL) AS CURRENCY_NM , MPM.EXCHANGE_RATE ,MPD.UNIT_PRICE , MPD.AMOUNT , MPD.LOCAL_AMOUNT , NVL(MPD.LOT_MANAGEMENT_YN, 'N') AS LOT_MANAGEMENT_YN , MPD.LOT_RULE AS LOT_RULE , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MPD.COMPANY_CD, MPD.DIVISION_CD, 'MD0052', MPD.LOT_RULE, #{langCd, jdbcType=VARCHAR}) FROM DUAL) AS LOT_RULE_NM , TO_CHAR(MPD.EFFECTIVE_DATE ,'YYYY-MM-DD') AS EFFECTIVE_DATE , MPD.LOT_ID , MPM.PROCESS_CD , (SELECT MOM_COMMON_PKG.FN_GET_PROCESS_NAME(MPM.COMPANY_CD, MPM.DIVISION_CD, MPM.PROCESS_CD) FROM DUAL) AS PROCESS_NM , MPD.STOCK_TYPE , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MPD.COMPANY_CD, MPD.DIVISION_CD, 'MD0048', MPD.STOCK_TYPE, #{langCd, jdbcType=VARCHAR}) FROM DUAL) AS STOCK_TYPE_NM , ROWIDTOCHAR(MPD.ROWID) AS KEY_ID , COUNT(*) OVER() AS TOTAL_COUNT , ROW_NUMBER() OVER(PARTITION BY MPD.COMPANY_CD,MPD.DIVISION_CD ORDER BY MPD.PURCHASE_INOUT_ID ) AS ROW_COUNT FROM MOM_PURCHASE_INOUT_MST MPM , MOM_PURCHASE_INOUT_DTL MPD , MOM_ITEM MI , MOM_PARTNER MP , MOM_WAREHOUSE MWH WHERE MPM.COMPANY_CD = MPD.COMPANY_CD AND MPM.DIVISION_CD = MPD.DIVISION_CD AND MPM.INOUT_NO = MPD.INOUT_NO AND MPD.COMPANY_CD = MI.COMPANY_CD AND MPD.DIVISION_CD = MI.DIVISION_CD AND MPD.ITEM_ID = MI.ITEM_ID AND MPM.COMPANY_CD = MP.COMPANY_CD AND MPM.DIVISION_CD = MP.DIVISION_CD AND MPM.VENDOR_CD = MP.PARTNER_CD AND MPM.COMPANY_CD = MWH.COMPANY_CD AND MPM.DIVISION_CD = MWH.DIVISION_CD AND MPM.WAREHOUSE_CD = MWH.WAREHOUSE_CD AND MPM.COMPANY_CD = #{companyCd, jdbcType=VARCHAR} AND MPM.DIVISION_CD = #{divisionCd, jdbcType=VARCHAR} AND MPD.RECEIPT_DATE BETWEEN TO_DATE(#{receiptDateSD, jdbcType=VARCHAR}, 'YYYY-MM-DD') AND TO_DATE(#{receiptDateED, jdbcType=VARCHAR}, 'YYYY-MM-DD') + 0.99999 AND MPM.VENDOR_CD = #{partnerCd, jdbcType=VARCHAR}
AND (UPPER(MI.ITEM_ID) LIKE '%' || UPPER(#{itemIdNm, jdbcType=VARCHAR}) || '%' OR UPPER(MI.ITEM_NM) LIKE '%' || UPPER(#{itemIdNm, jdbcType=VARCHAR}) || '%')
AND MI.ITEM_TYPE IN (${itemType})
AND (UPPER(MPM.WAREHOUSE_CD) LIKE '%' || UPPER(#{receiptWarehouseCdNm, jdbcType=VARCHAR}) || '%' OR UPPER(MWH.WAREHOUSE_NM) LIKE '%' || UPPER(#{receiptWarehouseCdNm, jdbcType=VARCHAR}) || '%')
AND MPM.PROCESS_CD = 'OP'
) A WHERE ROW_COUNT BETWEEN #{startPage, jdbcType=INTEGER} AND #{endPage, jdbcType=INTEGER}
