mapper.select:
SELECT A.* FROM (
SELECT * FROM (
SELECT CPM.COMPANY_CD , CPM.DIVISION_CD , CPM.ITEM_ID , MI.ITEM_NM , MI.ITEM_TYPE , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MI.COMPANY_CD,MI.DIVISION_CD, 'MD0002', MI.ITEM_TYPE, #{langCd, jdbcType=VARCHAR}) FROM DUAL) AS ITEM_TYPE_NM , CPM.ONHAND_QTY|| '/' ||MI.UOM AS ONHAND_QTY , CPM.SAFETY_STOCK_QTY , CPM.LEAD_TIME , CPM.MOQ , NVL(CPM.LEAD_TIME, 0) ||'/'|| NVL(CPM.MOQ, 0) AS LT_MOQ , CPM.SEQ , CPM.CATEGORY , CPM.QTY , TO_CHAR(CPM.PLAN_DATE ,'YYYY-MM-DD') AS PLAN_DATE , CPM.ITEM_ID||'_'|| CPM.SEQ AS KEY_ID , COUNT(*) OVER() AS TOTAL_COUNT , DENSE_RANK() OVER(PARTITION BY CPM.COMPANY_CD, CPM.DIVISION_CD ORDER BY CPM.ITEM_ID, CPM.SEQ ) AS ROW_COUNT FROM CAL_EXP_PSI_MOM CPM , MOM_ITEM MI WHERE CPM.COMPANY_CD = MI.COMPANY_CD AND CPM.DIVISION_CD = MI.DIVISION_CD AND CPM.ITEM_ID = MI.ITEM_ID AND CPM.COMPANY_CD = #{companyCd, jdbcType=VARCHAR} AND CPM.DIVISION_CD = #{divisionCd, jdbcType=VARCHAR} AND CPM.PLAN_TYPE = 'PRE' AND CPM.SEQ <> 1 AND CPM.PLAN_DATE BETWEEN TO_DATE(#{planDateSD, jdbcType=VARCHAR}, 'YYYY-MM-DD') AND TO_DATE(#{planDateED, jdbcType=VARCHAR}, 'YYYY-MM-DD')
AND (UPPER(CPM.ITEM_ID) LIKE '%' || UPPER(#{itemIdNm, jdbcType=VARCHAR}) || '%' OR UPPER(MI.ITEM_NM) LIKE '%' || UPPER(#{itemIdNm, jdbcType=VARCHAR}) || '%')
AND CPM.ITEM_ID IN ( SELECT S.ITEM_ID FROM CAL_EXP_PSI_MOM S WHERE S.COMPANY_CD = CPM.COMPANY_CD AND S.DIVISION_CD = CPM.DIVISION_CD AND S.PLAN_TYPE = CPM.PLAN_TYPE AND S.seq = 5 )
) PIVOT(SUM(QTY) FOR PLAN_DATE IN(${pivot}))
) A WHERE ROW_COUNT BETWEEN #{startPage, jdbcType=INTEGER} AND #{endPage, jdbcType=INTEGER}


    { CALL MOM_PP_PKG.SP_MRP( #{p_err_code, jdbcType=VARCHAR, mode=OUT}
	                        , #{p_err_msg,  jdbcType=VARCHAR, mode=OUT}
	                        , #{companyCd,  jdbcType=VARCHAR, mode=IN}
	                        , #{divisionCd, jdbcType=VARCHAR, mode=IN}		
	                        , 'PRE'									  
	                        , #{userId,     jdbcType=VARCHAR, mode=IN} )}
