mapper.select:
SELECT A.* FROM (
SELECT MSM.COMPANY_CD , MSM.DIVISION_CD , MSM.SHIPPING_NO AS SHIPPING_RETRUN_NO , MSM.SHIPPING_NM AS SHIPPING_RETRUN_NM , MSM.PROCESS_CD , (SELECT MOM_COMMON_PKG.FN_GET_PROCESS_NAME(MSM.COMPANY_CD, MSM.DIVISION_CD, MSM.PROCESS_CD) FROM DUAL) AS PROCESS_NM , MSM.CUSTOMER_CD , MP.PARTNER_NM AS CUSTOMER_NM , MSM.SHIPPING_WAREHOUSE_CD AS RETURN_WAREHOUSE_CD , MWH.WAREHOUSE_NM AS RETURN_WAREHOUSE_NM , TO_CHAR(MSM.SHIPPING_DUE_DATE ,'YYYY-MM-DD') AS RETURN_DUE_DATE , MSM.CURRENCY_CD , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MSM.COMPANY_CD, MSM.DIVISION_CD, 'SM0017', MSM.CURRENCY_CD, #{langCd, jdbcType=VARCHAR}) FROM DUAL) AS CURRENCY_NM , MSM.EXCHANGE_RATE , MSM.TAX_TYPE , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MSM.COMPANY_CD, MSM.DIVISION_CD, 'SM0029', MSM.TAX_TYPE, #{langCd, jdbcType=VARCHAR}) FROM DUAL) AS TAX_TYPE_NM , MSM.DESCRIPTION , MSM.UPDATE_BY AS UPDATE_USER_NO , (SELECT MOM_COMMON_PKG.FN_GET_USER_NAME(MSM.COMPANY_CD, MSM.DIVISION_CD, MSM.UPDATE_BY) FROM DUAL ) AS UPDATE_USER_NM , TO_CHAR(MSM.UPDATE_DATE ,'YYYY-MM-DD HH24:MI:SS') AS UPDATE_DATE , TO_CHAR(MSM.CREATE_DATE ,'YYYY-MM-DD') AS CREATE_DATE , ROWIDTOCHAR(MSM.ROWID) AS KEY_ID , COUNT(*) OVER() AS TOTAL_COUNT , ROW_NUMBER() OVER(PARTITION BY MSM.COMPANY_CD, MSM.DIVISION_CD ORDER BY MSM.SHIPPING_NO) AS ROW_CNT FROM MOM_SHIPPING_ORDER_MST MSM , MOM_PARTNER MP , MOM_WAREHOUSE MWH WHERE MSM.COMPANY_CD = MP.COMPANY_CD AND MSM.DIVISION_CD = MP.DIVISION_CD AND MSM.CUSTOMER_CD = MP.PARTNER_CD AND MSM.COMPANY_CD = MWH.COMPANY_CD AND MSM.DIVISION_CD = MWH.DIVISION_CD AND MSM.SHIPPING_WAREHOUSE_CD = MWH.WAREHOUSE_CD AND MSM.COMPANY_CD = #{companyCd, jdbcType=VARCHAR} AND MSM.DIVISION_CD = #{divisionCd, jdbcType=VARCHAR} AND MSM.PROCESS_CD = 'RCR' AND MSM.STATE = 'SHG' AND MSM.CREATE_DATE BETWEEN TO_DATE(#{createDateSD, jdbcType=VARCHAR}, 'YYYY-MM-DD') AND TO_DATE(#{createDateED, jdbcType=VARCHAR}, 'YYYY-MM-DD') + 0.99999
AND (UPPER(MSM.SHIPPING_NO) LIKE '%' || UPPER(#{shippingRetrunNo, jdbcType=VARCHAR}) || '%')
AND (UPPER(MSM.CUSTOMER_CD) LIKE '%' || UPPER(#{customerCdNm, jdbcType=VARCHAR}) || '%' OR UPPER(MP.PARTNER_NM) LIKE '%' || UPPER(#{customerCdNm, jdbcType=VARCHAR}) || '%')
AND (UPPER(MSM.SHIPPING_WAREHOUSE_CD) LIKE '%' || UPPER(#{returnWarehouseCdNm, jdbcType=VARCHAR}) || '%' OR UPPER(MWH.WAREHOUSE_NM) LIKE '%' || UPPER(#{returnWarehouseCdNm, jdbcType=VARCHAR}) || '%')
) A WHERE ROW_CNT BETWEEN #{startPage, jdbcType=INTEGER} AND #{endPage, jdbcType=INTEGER}

mapper.delete:
DELETE FROM MOM_PROC_SHIP_ORDER_MST


    (       COMPANY_CD  = #{item.companyCd, jdbcType=VARCHAR}
       AND  DIVISION_CD = #{item.divisionCd, jdbcType=VARCHAR}
       AND  PROCESS_CD  = 'RCR'
       AND  CREATE_BY   = #{item.userId, jdbcType=VARCHAR}
    )

insert.foreach:

        INTO MOM_PROC_SHIP_ORDER_MST( COMPANY_CD
									, DIVISION_CD
									, PROCESS_CD
									, SHIPPING_NO
									, SHIPPING_NM
									, SHIPPING_WAREHOUSE_CD
									, CUSTOMER_CD
									, DESTINATION_CD
									, SHIPPING_DUE_DATE
									, CURRENCY_CD
									, EXCHANGE_RATE
									, TAX_TYPE
									, DESCRIPTION
									, DEL_YN
									, VAL_MSG
									, MULTI_MSG
									, CREATE_BY
									, CREATE_DATE
                                    )
        VALUES                      ( #{item.companyCd, jdbcType=VARCHAR}
		                            , #{item.divisionCd, jdbcType=VARCHAR}
		                            , 'RCR'
		                            , #{item.shippingRetrunNo, jdbcType=VARCHAR}
		                            , #{item.shippingRetrunNm, jdbcType=VARCHAR}
		                            , #{item.returnWarehouseCd, jdbcType=VARCHAR}
		                            , #{item.customerCd, jdbcType=VARCHAR}
		                            , #{item.destinationCd, jdbcType=VARCHAR}
		                            , #{item.returnDueDate, jdbcType=VARCHAR}
		                            , #{item.currencyCd, jdbcType=VARCHAR}
		                            , #{item.exchangeRate, jdbcType=NUMERIC}
		                            , #{item.taxType, jdbcType=VARCHAR}
		                            , #{item.description, jdbcType=VARCHAR}
		                            , #{item.delYn, jdbcType=VARCHAR}
		                            , #{item.valMsg, jdbcType=VARCHAR}
		                            , #{item.multiMsg, jdbcType=VARCHAR}
		                            , #{item.userId, jdbcType=VARCHAR}
		                            , MOM_COMMON_PKG.FN_GET_LOCAL_TIME(#{item.companyCd, jdbcType=VARCHAR},#{item.divisionCd, jdbcType=VARCHAR})
		                            )

mapper.select:

        { CALL MOM_SO_PKG.SP_UPSERT_SHIP_ORDER_MST( #{p_err_code,  jdbcType=VARCHAR, mode=OUT}
		                                          , #{p_err_msg,   jdbcType=VARCHAR, mode=OUT}
		                                          , #{companyCd,   jdbcType=VARCHAR, mode=IN}
		                                          , #{divisionCd,  jdbcType=VARCHAR, mode=IN}
		                                          , 'RCR' 
		                                          , #{userId,      jdbcType=VARCHAR, mode=IN} )}

mapper.delete:
