mapper.select:
SELECT MP.COMPANY_CD , MP.DIVISION_CD , MP.PROCESS_CD , MP.PROCESS_NM , MP.PICKING_YN , MP.AUTO_RECEIPT_YN , MP.USE_YN , MP.UPDATE_BY AS UPDATE_USER_NO ,(SELECT MOM_COMMON_PKG.FN_GET_USER_NAME(MP.COMPANY_CD, MP.DIVISION_CD, MP.UPDATE_BY) FROM DUAL ) AS UPDATE_USER_NM , TO_CHAR(MP.UPDATE_DATE ,'YYYY-MM-DD HH24:MI:SS') AS UPDATE_DATE , MP.DESCRIPTION , ROWIDTOCHAR(MP.ROWID) AS KEY_ID FROM MOM_PROCESS MP WHERE MP.COMPANY_CD = #{companyCd, jdbcType=VARCHAR} AND MP.DIVISION_CD = #{divisionCd, jdbcType=VARCHAR}
AND UPPER(MP.PROCESS_NM) LIKE '%' || UPPER(#{processNm, jdbcType=VARCHAR}) || '%'
AND MP.USE_YN = #{useYn, jdbcType=VARCHAR}

mapper.delete:
DELETE FROM MOM_PROC_PROCESS


(       COMPANY_CD  = #{item.companyCd, jdbcType=VARCHAR}
   AND  DIVISION_CD = #{item.divisionCd, jdbcType=VARCHAR}
   AND  CREATE_BY   = #{item.userId, jdbcType=VARCHAR}
)

insert.foreach:

        INTO MOM_PROC_PROCESS( COMPANY_CD
                             , DIVISION_CD
                             , PROCESS_CD
                             , PROCESS_NM
                             , PICKING_YN
		                     , AUTO_RECEIPT_YN
                             , USE_YN
                             , DESCRIPTION
                             , DEL_YN
                             , VAL_MSG
                             , MULTI_MSG
                             , CREATE_BY
                             , CREATE_DATE)
        VALUES               ( #{item.companyCd, jdbcType=VARCHAR}
                             , #{item.divisionCd, jdbcType=VARCHAR}
                             , #{item.processCd, jdbcType=VARCHAR}
                             , #{item.processNm, jdbcType=VARCHAR}
                             , #{item.pickingYn, jdbcType=VARCHAR}
                             , #{item.autoReceiptYn, jdbcType=VARCHAR}
                             , #{item.useYn, jdbcType=VARCHAR}
                             , #{item.description, jdbcType=VARCHAR}
                             , #{item.delYn, jdbcType=VARCHAR}
                             , #{item.valMsg, jdbcType=VARCHAR}
                             , #{item.multiMsg, jdbcType=VARCHAR}
                             , #{item.userId, jdbcType=VARCHAR}
                             , MOM_COMMON_PKG.FN_GET_LOCAL_TIME(#{item.companyCd, jdbcType=VARCHAR},#{item.divisionCd, jdbcType=VARCHAR})
                             )

mapper.select:

	    { CALL MOM_MASTER_INFO_PKG.SP_UPSERT_PROCESS( #{p_err_code,  jdbcType=VARCHAR, mode=OUT}
	                                                , #{p_err_msg,   jdbcType=VARCHAR, mode=OUT}
                                                    , #{companyCd,   jdbcType=VARCHAR , mode=IN}
                                                    , #{divisionCd,  jdbcType=VARCHAR, mode=IN}   
                                                    , 'M'                     
                                                    , #{userId,      jdbcType=VARCHAR, mode=IN} )}

mapper.delete:
DELETE FROM MOM_PROC_PROCESS_IO_MAPPING


        (    COMPANY_CD  = #{item.companyCd, jdbcType=VARCHAR}
        AND  DIVISION_CD = #{item.divisionCd, jdbcType=VARCHAR}
        AND  CREATE_BY   = #{item.userId, jdbcType=VARCHAR}
        )

insert.foreach:

INTO MOM_PROC_PROCESS_IO_MAPPING( COMPANY_CD
                                , DIVISION_CD
                                , PROCESS_CD
                                , IO_TYPE
                                , SORT_NO
                                , USE_YN
                                , DESCRIPTION
                                , DEL_YN
                                , VAL_MSG
                                , MULTI_MSG
                                , CREATE_BY
                                , CREATE_DATE )
VALUES                          ( #{item.companyCd, jdbcType=VARCHAR}
                                , #{item.divisionCd, jdbcType=VARCHAR}
                                , #{item.processCd, jdbcType=VARCHAR}
                                , #{item.ioType, jdbcType=VARCHAR}
                                , #{item.sortNo, jdbcType=NUMERIC}
                                , #{item.useYn, jdbcType=VARCHAR}
                                , #{item.description, jdbcType=VARCHAR}
                                , #{item.delYn, jdbcType=VARCHAR}
                                , #{item.valMsg, jdbcType=VARCHAR}
                                , #{item.multiMsg, jdbcType=VARCHAR}
                                , #{item.userId, jdbcType=VARCHAR}
                                , MOM_COMMON_PKG.FN_GET_LOCAL_TIME(#{item.companyCd, jdbcType=VARCHAR},#{item.divisionCd, jdbcType=VARCHAR})
                                )

mapper.select:

	    { CALL MOM_MASTER_INFO_PKG.SP_UPSERT_PROCESS( #{p_err_code,  jdbcType=VARCHAR, mode=OUT}
                                                    , #{p_err_msg,   jdbcType=VARCHAR, mode=OUT}
                                                    , #{companyCd,   jdbcType=VARCHAR , mode=IN}
                                                    , #{divisionCd,  jdbcType=VARCHAR, mode=IN}   
                                                    , 'D'                     
                                                    , #{userId,      jdbcType=VARCHAR, mode=IN} )}
