mapper.select:
SELECT MPT.COMPANY_CD , MPT.DIVISION_CD , MPT.PROCESS_TYPE , MPT.PROCESS_TYPE_NM , MPT.BOUNDARY_TYPE_CD , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MPT.COMPANY_CD, MPT.DIVISION_CD, 'MD0026', MPT.BOUNDARY_TYPE_CD, #{langCd, jdbcType=VARCHAR}) FROM DUAL) AS BOUNDARY_TYPE_NM , MPT.ACTION_TYPE_CD , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MPT.COMPANY_CD, MPT.DIVISION_CD, 'MD0027', MPT.ACTION_TYPE_CD, #{langCd, jdbcType=VARCHAR}) FROM DUAL) AS ACTION_TYPE_NM , MPT.USE_YN , MPT.UPDATE_BY AS UPDATE_USER_NO ,(SELECT MOM_COMMON_PKG.FN_GET_USER_NAME(MPT.COMPANY_CD, MPT.DIVISION_CD, MPT.UPDATE_BY) FROM DUAL ) AS UPDATE_USER_NM , TO_CHAR(MPT.UPDATE_DATE ,'YYYY-MM-DD HH24:MI:SS') AS UPDATE_DATE , MPT.DESCRIPTION , ROWIDTOCHAR(MPT.ROWID) AS KEY_ID FROM MOM_PROCESS_TYPE MPT WHERE MPT.COMPANY_CD = #{companyCd, jdbcType=VARCHAR} AND MPT.DIVISION_CD = #{divisionCd, jdbcType=VARCHAR}
AND UPPER(MPT.PROCESS_TYPE_NM) LIKE '%' || UPPER(#{processTypeNm, jdbcType=VARCHAR}) || '%'
AND MPT.BOUNDARY_TYPE_CD IN (${boundaryTypeCd})
AND MPT.ACTION_TYPE_CD IN (${actionTypeCd})
AND MPT.USE_YN = #{useYn, jdbcType=VARCHAR}

mapper.delete:
DELETE FROM MOM_PROC_PROCESS_TYPE


(       COMPANY_CD  = #{item.companyCd, jdbcType=VARCHAR}
   AND  DIVISION_CD = #{item.divisionCd, jdbcType=VARCHAR}
   AND  CREATE_BY   = #{item.userId, jdbcType=VARCHAR}
)

insert.foreach:

        INTO MOM_PROC_PROCESS_TYPE( COMPANY_CD
								  , DIVISION_CD
								  , PROCESS_TYPE
								  , PROCESS_TYPE_NM
								  , BOUNDARY_TYPE_CD
								  , ACTION_TYPE_CD
								  , USE_YN
								  , DESCRIPTION
								  , DEL_YN
								  , VAL_MSG
								  , MULTI_MSG
								  , CREATE_BY
								  , CREATE_DATE )
        VALUES                    ( #{item.companyCd, jdbcType=VARCHAR}
	                              , #{item.divisionCd, jdbcType=VARCHAR}
	                              , #{item.processType, jdbcType=VARCHAR}
	                              , #{item.processTypeNm, jdbcType=VARCHAR}
	                              , #{item.boundaryTypeCd, jdbcType=VARCHAR}
	                              , #{item.actionTypeCd, jdbcType=VARCHAR}
	                              , #{item.useYn, jdbcType=VARCHAR}
	                              , #{item.description, jdbcType=VARCHAR}
	                              , #{item.delYn, jdbcType=VARCHAR}
	                              , #{item.valMsg, jdbcType=VARCHAR}
	                              , #{item.multiMsg, jdbcType=VARCHAR}
	                              , #{item.userId, jdbcType=VARCHAR}
	                              , MOM_COMMON_PKG.FN_GET_LOCAL_TIME(#{item.companyCd, jdbcType=VARCHAR},#{item.divisionCd, jdbcType=VARCHAR})
	                              )

mapper.select:

	    { CALL MOM_MASTER_INFO_PKG.SP_UPSERT_PROCESS_TYPE( #{p_err_code,  jdbcType=VARCHAR, mode=OUT}
	                                                	 , #{p_err_msg,   jdbcType=VARCHAR, mode=OUT}
                                                         , #{companyCd,   jdbcType=VARCHAR , mode=IN}
                                                         , #{divisionCd,  jdbcType=VARCHAR, mode=IN}   
                                                         , #{userId,      jdbcType=VARCHAR, mode=IN} )}
