mapper.select:
SELECT MWT.COMPANY_CD , MWT.DIVISION_CD , MWT.IO_RECEIPT_TYPE AS WH_RECEIPT_TYPE , MWT.IO_RECEIPT_TYPE_NM AS WH_RECEIPT_TYPE_NM , MWT.USE_YN , MWT.UPDATE_BY AS UPDATE_USER_NO ,(SELECT MOM_COMMON_PKG.FN_GET_USER_NAME(MWT.COMPANY_CD, MWT.DIVISION_CD, MWT.UPDATE_BY) FROM DUAL ) AS UPDATE_USER_NM , TO_CHAR(MWT.UPDATE_DATE ,'YYYY-MM-DD HH24:MI:SS') AS UPDATE_DATE , ROWIDTOCHAR(MWT.ROWID) AS KEY_ID , MWT.DESCRIPTION FROM MOM_WH_IO_RECEIPT_TYPE MWT WHERE MWT.COMPANY_CD = #{companyCd, jdbcType=VARCHAR} AND MWT.DIVISION_CD = #{divisionCd, jdbcType=VARCHAR}
AND MWT.IO_RECEIPT_TYPE IN (${whReceiptType})
AND UPPER(MWT.IO_RECEIPT_TYPE_NM) LIKE '%' || UPPER(#{whReceiptTypeNm, jdbcType=VARCHAR}) || '%'
AND MWT.USE_YN = #{useYn, jdbcType=VARCHAR}

mapper.delete:
DELETE FROM MOM_PROC_WH_RECEIPT_TYPE


(       COMPANY_CD  = #{item.companyCd, jdbcType=VARCHAR}
   AND  DIVISION_CD = #{item.divisionCd, jdbcType=VARCHAR}
   AND  CREATE_BY   = #{item.userId, jdbcType=VARCHAR}
)

insert.foreach:

        INTO MOM_PROC_WH_RECEIPT_TYPE( COMPANY_CD
                             		 , DIVISION_CD
		                             , IO_RECEIPT_TYPE
		                             , IO_RECEIPT_TYPE_NM
		                             , USE_YN
		                             , DESCRIPTION
		                             , DEL_YN
		                             , VAL_MSG
		                             , MULTI_MSG
		                             , CREATE_BY
		                             , CREATE_DATE)
        VALUES                       ( #{item.companyCd, jdbcType=VARCHAR}
		                             , #{item.divisionCd, jdbcType=VARCHAR}
		                             , #{item.whReceiptType, jdbcType=VARCHAR}
		                             , #{item.whReceiptTypeNm, jdbcType=VARCHAR}
		                             , #{item.useYn, jdbcType=VARCHAR}
		                             , #{item.description, jdbcType=VARCHAR}
		                             , #{item.delYn, jdbcType=VARCHAR}
		                             , #{item.valMsg, jdbcType=VARCHAR}
		                             , #{item.multiMsg, jdbcType=VARCHAR}
		                             , #{item.userId, jdbcType=VARCHAR}
		                             , MOM_COMMON_PKG.FN_GET_LOCAL_TIME(#{item.companyCd, jdbcType=VARCHAR},#{item.divisionCd, jdbcType=VARCHAR})
		                             )

mapper.select:

	    { CALL MOM_MASTER_INFO_PKG.SP_UPSERT_WH_RECEIPT_TYPE( #{p_err_code,  jdbcType=VARCHAR, mode=OUT}
	                                                        , #{p_err_msg,   jdbcType=VARCHAR, mode=OUT}
                                                            , #{companyCd,   jdbcType=VARCHAR , mode=IN}
                                                            , #{divisionCd,  jdbcType=VARCHAR, mode=IN}   
                                                            , #{userId,      jdbcType=VARCHAR, mode=IN} )}

mapper.delete:
DELETE FROM MOM_PROC_WH_REC_PROCESS_MAP


        (    COMPANY_CD  = #{item.companyCd, jdbcType=VARCHAR}
        AND  DIVISION_CD = #{item.divisionCd, jdbcType=VARCHAR}
        AND  CREATE_BY   = #{item.userId, jdbcType=VARCHAR}
        )

insert.foreach:

INTO MOM_PROC_WH_REC_PROCESS_MAP( COMPANY_CD
                                , DIVISION_CD
                                , IO_RECEIPT_TYPE
                                , PROCESS_CD
                                , USE_YN
                                , DESCRIPTION
                                , DEL_YN
                                , VAL_MSG
                                , MULTI_MSG
                                , CREATE_BY
                                , CREATE_DATE )
VALUES                          ( #{item.companyCd, jdbcType=VARCHAR}
                                , #{item.divisionCd, jdbcType=VARCHAR}
                                , #{item.whReceiptType, jdbcType=VARCHAR}
                                , #{item.processCd, jdbcType=VARCHAR}
                                , #{item.useYn, jdbcType=VARCHAR}
                                , #{item.description, jdbcType=VARCHAR}
                                , #{item.delYn, jdbcType=VARCHAR}
                                , #{item.valMsg, jdbcType=VARCHAR}
                                , #{item.multiMsg, jdbcType=VARCHAR}
                                , #{item.userId, jdbcType=VARCHAR}
                                , MOM_COMMON_PKG.FN_GET_LOCAL_TIME(#{item.companyCd, jdbcType=VARCHAR},#{item.divisionCd, jdbcType=VARCHAR})
                                )

mapper.select:

    { CALL MOM_MASTER_INFO_PKG.SP_UPSERT_WH_REC_PROCESS_MAP( #{p_err_code,  jdbcType=VARCHAR, mode=OUT}
		                                                   , #{p_err_msg,   jdbcType=VARCHAR, mode=OUT}
		                                                   , #{companyCd,   jdbcType=VARCHAR , mode=IN}
		                                                   , #{divisionCd,  jdbcType=VARCHAR, mode=IN}   
		                                                   , #{userId,      jdbcType=VARCHAR, mode=IN} )}
