mapper.select:
SELECT A.* FROM (
SELECT MWM.COMPANY_CD , MWM.DIVISION_CD , MWM.RECEIPT_ID , MWM.RECEIPT_NM , MWM.WAREHOUSE_CD AS RECEIPT_WAREHOUSE_CD , MW.WAREHOUSE_NM AS RECEIPT_WAREHOUSE_NM , MWM.TO_WAREHOUSE_CD AS REQUEST_WAREHOUSE_CD , MWT.WAREHOUSE_NM AS REQUEST_WAREHOUSE_NM , MWM.WAREHOUSE_USER_NO AS RECEIPT_USER_NO , (SELECT MOM_COMMON_PKG.FN_GET_USER_NAME(MWM.COMPANY_CD, MWM.DIVISION_CD, MWM.WAREHOUSE_USER_NO) FROM DUAL ) AS RECEIPT_USER_NM , MWM.IO_RECEIPT_TYPE AS INPUT_RECEIPT_TYPE , (SELECT MOM_COMMON_PKG.FN_GET_WH_IO_RECEIPT_TYPE_NAME(MWM.COMPANY_CD, MWM.DIVISION_CD, MWM.IO_RECEIPT_TYPE) FROM DUAL) AS INPUT_RECEIPT_TYPE_NM , MWM.STATE , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MWM.COMPANY_CD, MWM.DIVISION_CD, 'WM0002', MWM.STATE, #{langCd, jdbcType=VARCHAR}) FROM DUAL) AS STATE_NM , MWM.DESCRIPTION , MWM.UPDATE_BY AS UPDATE_USER_NO , (SELECT MOM_COMMON_PKG.FN_GET_USER_NAME(MWM.COMPANY_CD, MWM.DIVISION_CD, MWM.UPDATE_BY) FROM DUAL ) AS UPDATE_USER_NM , TO_CHAR(MWM.UPDATE_DATE ,'YYYY-MM-DD HH24:MI:SS') AS UPDATE_DATE , TO_CHAR(MWM.CREATE_DATE ,'YYYY-MM-DD') AS CREATE_DATE , ROWIDTOCHAR(MWM.ROWID) AS KEY_ID , COUNT(*) OVER() AS TOTAL_COUNT , ROW_NUMBER() OVER(PARTITION BY MWM.COMPANY_CD,MWM.DIVISION_CD ORDER BY MWM.RECEIPT_ID) AS ROW_CNT FROM MOM_WAREHOUSE_RECEIPT_MST MWM , MOM_WAREHOUSE MW , MOM_WAREHOUSE MWT WHERE MWM.COMPANY_CD = MW.COMPANY_CD AND MWM.DIVISION_CD = MW.DIVISION_CD AND MWM.WAREHOUSE_CD = MW.WAREHOUSE_CD AND MWM.COMPANY_CD = MWT.COMPANY_CD AND MWM.DIVISION_CD = MWT.DIVISION_CD AND MWM.TO_WAREHOUSE_CD = MWT.WAREHOUSE_CD AND MWM.COMPANY_CD = #{companyCd, jdbcType=VARCHAR} AND MWM.DIVISION_CD = #{divisionCd, jdbcType=VARCHAR} AND MWM.CONFIRM_DATE BETWEEN TO_DATE(#{confirmDateSD, jdbcType=VARCHAR}, 'YYYY-MM-DD') AND TO_DATE(#{confirmDateED, jdbcType=VARCHAR}, 'YYYY-MM-DD') + 0.99999 AND MWM.STATE = 'WRF' AND MWM.IO_RECEIPT_TYPE = 'WMO' AND EXISTS ( SELECT '*' FROM MOM_WAREHOUSE_RECEIPT_DTL MWD WHERE MWD.COMPANY_CD = MWM.COMPANY_CD AND MWD.DIVISION_CD = MWM.DIVISION_CD AND MWD.RECEIPT_ID = MWM.RECEIPT_ID )
AND MWM.WAREHOUSE_CD = #{receiptWarehouseCd, jdbcType=VARCHAR}
AND MWM.WAREHOUSE_CD IN (SELECT MUM.WAREHOUSE_CD FROM MOM_USER_WH_IO_RECEIPT_MAP MUM WHERE MUM.COMPANY_CD = #{companyCd, jdbcType=VARCHAR} AND MUM.DIVISION_CD = #{divisionCd, jdbcType=VARCHAR} AND MUM.USER_NO = #{userId, jdbcType=VARCHAR} AND MUM.IO_RECEIPT_TYPE = 'WMO' AND MUM.USE_YN = 'Y')
) A WHERE ROW_CNT BETWEEN #{startPage, jdbcType=INTEGER} AND #{endPage, jdbcType=INTEGER}

mapper.delete:
DELETE FROM MOM_PROC_WR_CONFIRM_CANCEL


    (       COMPANY_CD      = #{item.companyCd, jdbcType=VARCHAR}
       AND  DIVISION_CD     = #{item.divisionCd, jdbcType=VARCHAR}
       AND  IO_RECEIPT_TYPE = 'WMO' 
       AND  CREATE_BY       = #{item.userId, jdbcType=VARCHAR}
    )

insert.foreach:

        INTO MOM_PROC_WR_CONFIRM_CANCEL( COMPANY_CD
		                               , DIVISION_CD
		                               , IO_RECEIPT_TYPE
		                               , RECEIPT_ID
		                               , DESCRIPTION
		                               , VAL_MSG
		                               , MULTI_MSG
		                               , CREATE_BY
		                               , CREATE_DATE
		                               )
		VALUES                         ( #{item.companyCd, jdbcType=VARCHAR}
		                               , #{item.divisionCd, jdbcType=VARCHAR}
		                               , 'WMO' 
		                               , #{item.receiptId, jdbcType=VARCHAR}
		                               , #{item.description, jdbcType=VARCHAR}
		                               , #{item.valMsg, jdbcType=VARCHAR}
		                               , #{item.multiMsg, jdbcType=VARCHAR}
		                               , #{item.userId, jdbcType=VARCHAR}
		                               , MOM_COMMON_PKG.FN_GET_LOCAL_TIME(#{item.companyCd, jdbcType=VARCHAR},#{item.divisionCd, jdbcType=VARCHAR})
		                               )

mapper.select:

        { CALL MOM_WMS_PKG.SP_WR_CONFIRM_CANCEL( #{p_err_code,  jdbcType=VARCHAR, mode=OUT}
				                               , #{p_err_msg,   jdbcType=VARCHAR, mode=OUT}
				                               , #{companyCd,   jdbcType=VARCHAR, mode=IN}
				                               , #{divisionCd,  jdbcType=VARCHAR, mode=IN} 
				                               , 'WMO' 
				                               , #{userId,      jdbcType=VARCHAR, mode=IN} )}
