mapper.select:
SELECT A.* FROM (
SELECT MLM.COMPANY_CD , MLM.DIVISION_CD , MLM.LOCATION_DO_NO , MLM.LOCATION_DO_NM , MLM.WAREHOUSE_CD , MWH.WAREHOUSE_NM , MLM.FROM_LOCATION_CD , (SELECT MOM_COMMON_PKG.FN_GET_LOCATION_NAME(MLM.COMPANY_CD, MLM.DIVISION_CD, MLM.FROM_LOCATION_CD) FROM DUAL) AS FROM_LOCATION_NM , MLM.TO_LOCATION_CD , (SELECT MOM_COMMON_PKG.FN_GET_LOCATION_NAME(MLM.COMPANY_CD, MLM.DIVISION_CD, MLM.TO_LOCATION_CD) FROM DUAL) AS TO_LOCATION_NM , MLM.DESCRIPTION , MLM.UPDATE_BY AS UPDATE_USER_NO , (SELECT MOM_COMMON_PKG.FN_GET_USER_NAME(MLM.COMPANY_CD, MLM.DIVISION_CD, MLM.UPDATE_BY) FROM DUAL ) AS UPDATE_USER_NM , TO_CHAR(MLM.UPDATE_DATE ,'YYYY-MM-DD HH24:MI:SS') AS UPDATE_DATE , TO_CHAR(MLM.CREATE_DATE ,'YYYY-MM-DD') AS CREATE_DATE , TO_CHAR(MLM.CONFIRM_DATE ,'YYYY-MM-DD') AS CONFIRM_DATE , (SELECT MOM_COMMON_PKG.FN_GET_USER_NAME(MLM.COMPANY_CD, MLM.DIVISION_CD, MLM.CONFIRM_USER_NO) FROM DUAL ) AS CONFIRM_USER_NM , MLM.CONFIRM_USER_NO , ROWIDTOCHAR(MLM.ROWID) AS KEY_ID , COUNT(*) OVER() AS TOTAL_COUNT , ROW_NUMBER() OVER(PARTITION BY MLM.COMPANY_CD,MLM.DIVISION_CD ORDER BY MLM.LOCATION_DO_NO) AS ROW_CNT FROM MOM_LOCATION_DO_MST MLM , MOM_WAREHOUSE MWH WHERE MLM.COMPANY_CD = MWH.COMPANY_CD AND MLM.DIVISION_CD = MWH.DIVISION_CD AND MLM.WAREHOUSE_CD = MWH.WAREHOUSE_CD AND MLM.COMPANY_CD = #{companyCd, jdbcType=VARCHAR} AND MLM.DIVISION_CD = #{divisionCd, jdbcType=VARCHAR}
AND MWM.CONFIRM_DATE BETWEEN TO_DATE(#{confirmDateSD, jdbcType=VARCHAR}, 'YYYY-MM-DD') AND TO_DATE(#{confirmDateED, jdbcType=VARCHAR}, 'YYYY-MM-DD') + 0.99999
AND (UPPER(MWH.WAREHOUSE_CD) LIKE '%' || UPPER(#{warehouseCdNm, jdbcType=VARCHAR}) || '%' OR UPPER(MWH.WAREHOUSE_NM) LIKE '%' || UPPER(#{warehouseCdNm, jdbcType=VARCHAR}) || '%')
AND MLM.STATE = 'LDF' AND EXISTS ( SELECT '*' FROM MOM_WAREHOUSE_MANAGER MWM WHERE MWM.COMPANY_CD = MLM.COMPANY_CD AND MWM.DIVISION_CD = MLM.DIVISION_CD AND MWM.WAREHOUSE_CD = MLM.WAREHOUSE_CD AND MWM.USER_NO = #{userId, jdbcType=VARCHAR} AND MWM.USE_YN = 'Y' )
) A WHERE ROW_CNT BETWEEN #{startPage, jdbcType=INTEGER} AND #{endPage, jdbcType=INTEGER}

mapper.delete:
DELETE FROM MOM_PROC_LDO_CONFIRM_CANCEL


    (       COMPANY_CD   = #{item.companyCd, jdbcType=VARCHAR}
       AND  DIVISION_CD  = #{item.divisionCd, jdbcType=VARCHAR}
       AND  CREATE_BY    = #{item.userId, jdbcType=VARCHAR}
    )

insert.foreach:

        INTO MOM_PROC_LDO_CONFIRM_CANCEL( COMPANY_CD
		                                 , DIVISION_CD
		                                 , LOCATION_DO_NO
		                                 , DESCRIPTION
		                                 , VAL_MSG
		                                 , MULTI_MSG
		                                 , CREATE_BY
		                                 , CREATE_DATE
		                                 )
		VALUES                           ( #{item.companyCd, jdbcType=VARCHAR}
		                                 , #{item.divisionCd, jdbcType=VARCHAR}
		                                 , #{item.locationDoNo, jdbcType=VARCHAR}
		                                 , #{item.description, jdbcType=VARCHAR}
		                                 , #{item.valMsg, jdbcType=VARCHAR}
		                                 , #{item.multiMsg, jdbcType=VARCHAR}
		                                 , #{item.userId, jdbcType=VARCHAR}
		                                 , MOM_COMMON_PKG.FN_GET_LOCAL_TIME(#{item.companyCd, jdbcType=VARCHAR},#{item.divisionCd, jdbcType=VARCHAR})
		                                 )

mapper.select:

        { CALL MOM_WMS_PKG.SP_LOCATION_DO_CONFIRM_CANCEL( #{p_err_code,  jdbcType=VARCHAR, mode=OUT}
				                                        , #{p_err_msg,   jdbcType=VARCHAR, mode=OUT}
				                                        , #{companyCd,   jdbcType=VARCHAR, mode=IN}
				                                        , #{divisionCd,  jdbcType=VARCHAR, mode=IN} 
				                                        , #{userId,      jdbcType=VARCHAR, mode=IN} )}
