mapper.select:
SELECT A.* FROM (
SELECT MLM.COMPANY_CD , MLM.DIVISION_CD , MLM.LOCATION_DO_NO , MLM.LOCATION_DO_NM , MLM.WAREHOUSE_CD , MWH.WAREHOUSE_NM , MLM.FROM_LOCATION_CD , (SELECT MOM_COMMON_PKG.FN_GET_LOCATION_NAME(MLM.COMPANY_CD, MLM.DIVISION_CD, MLM.FROM_LOCATION_CD) FROM DUAL) AS FROM_LOCATION_NM , MLM.TO_LOCATION_CD , (SELECT MOM_COMMON_PKG.FN_GET_LOCATION_NAME(MLM.COMPANY_CD, MLM.DIVISION_CD, MLM.TO_LOCATION_CD) FROM DUAL) AS TO_LOCATION_NM , MLD.LOGISTICS_UNIT_CD , MLD.LOT_ID , MLD.ITEM_ID , MI.ITEM_NM , MI.ITEM_SPEC , MI.ITEM_TYPE , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MI.COMPANY_CD, MI.DIVISION_CD, 'MD0002', MI.ITEM_TYPE, #{langCd, jdbcType=VARCHAR}) FROM DUAL) AS ITEM_TYPE_NM , MLD.QTY AS DELIVERY_REQUEST_QTY , DECODE(MLM.STATE, 'LDE', MLD.QTY, 0) AS DO_QTY , MLD.UOM , (SELECT MOM_COMMON_PKG.FN_GET_UNIT_NAME(MLD.COMPANY_CD, MLD.DIVISION_CD, MLD.UOM) FROM DUAL) AS UOM_NM , MLM.STATE , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MLM.COMPANY_CD, MLM.DIVISION_CD, 'IC0007', MLM.STATE, #{langCd, jdbcType=VARCHAR}) FROM DUAL) AS STATE_NM , MLM.DESCRIPTION , MLD.UPDATE_BY AS UPDATE_USER_NO , (SELECT MOM_COMMON_PKG.FN_GET_USER_NAME(MLD.COMPANY_CD, MLD.DIVISION_CD, MLD.UPDATE_BY) FROM DUAL ) AS UPDATE_USER_NM , TO_CHAR(MLD.UPDATE_DATE ,'YYYY-MM-DD HH24:MI:SS') AS UPDATE_DATE , TO_CHAR(MLD.CREATE_DATE ,'YYYY-MM-DD') AS CREATE_DATE , ROWIDTOCHAR(MLD.ROWID) AS KEY_ID , COUNT(*) OVER() AS TOTAL_COUNT , ROW_NUMBER() OVER(PARTITION BY MLD.COMPANY_CD, MLD.DIVISION_CD ORDER BY MLD.LOCATION_DO_NO, MLD.ITEM_ID) AS ROW_CNT FROM MOM_LOCATION_DO_MST MLM , MOM_LOCATION_DO_DTL MLD , MOM_WAREHOUSE MWH , MOM_ITEM MI WHERE MLM.COMPANY_CD = MLD.COMPANY_CD AND MLM.DIVISION_CD = MLD.DIVISION_CD AND MLM.LOCATION_DO_NO = MLD.LOCATION_DO_NO AND MLM.COMPANY_CD = MWH.COMPANY_CD AND MLM.DIVISION_CD = MWH.DIVISION_CD AND MLM.WAREHOUSE_CD = MWH.WAREHOUSE_CD AND MLD.COMPANY_CD = MI.COMPANY_CD AND MLD.DIVISION_CD = MI.DIVISION_CD AND MLD.ITEM_ID = MI.ITEM_ID AND MLM.COMPANY_CD = #{companyCd, jdbcType=VARCHAR} AND MLM.DIVISION_CD = #{divisionCd, jdbcType=VARCHAR} AND MLM.CREATE_DATE BETWEEN TO_DATE(#{createDateSD, jdbcType=VARCHAR}, 'YYYY-MM-DD') AND TO_DATE(#{createDateED, jdbcType=VARCHAR}, 'YYYY-MM-DD') + 0.99999
AND (UPPER(MLM.LOCATION_DO_NO) LIKE '%' || UPPER(#{locationDoNo, jdbcType=VARCHAR}) || '%')
AND (UPPER(MWH.WAREHOUSE_CD) LIKE '%' || UPPER(#{warehouseCdNm, jdbcType=VARCHAR}) || '%' OR UPPER(MWH.WAREHOUSE_NM) LIKE '%' || UPPER(#{warehouseCdNm, jdbcType=VARCHAR}) || '%')
AND EXISTS ( SELECT '*' FROM MOM_WAREHOUSE_MANAGER MWM WHERE MWM.COMPANY_CD = MLM.COMPANY_CD AND MWM.DIVISION_CD = MLM.DIVISION_CD AND MWM.WAREHOUSE_CD = MLM.WAREHOUSE_CD AND MWM.USER_NO = #{userId, jdbcType=VARCHAR} AND MWM.USE_YN = 'Y' )
) A WHERE ROW_CNT BETWEEN #{startPage, jdbcType=INTEGER} AND #{endPage, jdbcType=INTEGER}
