mapper.select:
SELECT A.* FROM (
SELECT MPM.COMPANY_CD , MPM.DIVISION_CD , MPM.PACKING_ID , MPM.PACKING_NM , MPM.CUSTOMER_CD , MPC.PARTNER_NM AS CUSTOMER_NM , MPM.DESTINATION_CD , MPD.PARTNER_NM AS DESTINATION_NM , MPM.FORWARDER_CD , (SELECT MOM_COMMON_PKG.FN_GET_PARTNER_NAME(MPM.COMPANY_CD, MPM.DIVISION_CD, MPM.FORWARDER_CD) FROM DUAL) AS FORWARDER_NM , MPM.NOTIFY_PARTY_CD , (SELECT MOM_COMMON_PKG.FN_GET_PARTNER_NAME(MPM.COMPANY_CD, MPM.DIVISION_CD, MPM.NOTIFY_PARTY_CD) FROM DUAL) AS NOTIFY_PARTY_NM , MPM.LOADING_PLACE_CD , (SELECT MOM_COMMON_PKG.FN_GET_PARTNER_NAME(MPM.COMPANY_CD, MPM.DIVISION_CD, MPM.LOADING_PLACE_CD) FROM DUAL) AS LOADING_PLACE_NM , MPM.DESTINATION_PORT_CD , (SELECT MOM_COMMON_PKG.FN_GET_PARTNER_NAME(MPM.COMPANY_CD, MPM.DIVISION_CD, MPM.DESTINATION_PORT_CD) FROM DUAL) AS DESTINATION_PORT_NM , MPM.PACKING_CNT , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MPM.COMPANY_CD, MPM.DIVISION_CD, 'SM0033', MPM.PACKING_CNT, #{langCd, jdbcType=VARCHAR}) FROM DUAL) AS PACKING_CNT_NM , MPM.DESCRIPTION , MPM.UPDATE_BY AS UPDATE_USER_NO , (SELECT MOM_COMMON_PKG.FN_GET_USER_NAME(MPM.COMPANY_CD, MPM.DIVISION_CD, MPM.UPDATE_BY) FROM DUAL ) AS UPDATE_USER_NM , TO_CHAR(MPM.UPDATE_DATE ,'YYYY-MM-DD HH24:MI:SS') AS UPDATE_DATE , TO_CHAR(MPM.CREATE_DATE ,'YYYY-MM-DD') AS CREATE_DATE , ROWIDTOCHAR(MPM.ROWID) AS KEY_ID , COUNT(*) OVER() AS TOTAL_COUNT , ROW_NUMBER() OVER(PARTITION BY MPM.COMPANY_CD,MPM.DIVISION_CD ORDER BY MPM.PACKING_ID) AS ROW_CNT FROM MOM_PACKING_MST MPM , MOM_PARTNER MPC , MOM_PARTNER MPD WHERE MPM.COMPANY_CD = MPC.COMPANY_CD AND MPM.DIVISION_CD = MPC.DIVISION_CD AND MPM.CUSTOMER_CD = MPC.PARTNER_CD AND MPM.COMPANY_CD = MPD.COMPANY_CD AND MPM.DIVISION_CD = MPD.DIVISION_CD AND MPM.DESTINATION_CD = MPD.PARTNER_CD AND MPM.COMPANY_CD = #{companyCd, jdbcType=VARCHAR} AND MPM.DIVISION_CD = #{divisionCd, jdbcType=VARCHAR} AND MPM.CONFIRM_DATE BETWEEN TO_DATE(#{confirmDateSD, jdbcType=VARCHAR}, 'YYYY-MM-DD') AND TO_DATE(#{confirmDateED, jdbcType=VARCHAR}, 'YYYY-MM-DD') + 0.99999 AND MPM.STATE = 'PKE'
AND (UPPER(MSM.CUSTOMER_CD) LIKE '%' || UPPER(#{customerCdNm, jdbcType=VARCHAR}) || '%' OR UPPER(MPC.PARTNER_NM) LIKE '%' || UPPER(#{customerCdNm, jdbcType=VARCHAR}) || '%')
AND (UPPER(MSM.DESTINATION_CD) LIKE '%' || UPPER(#{destinationCdNm, jdbcType=VARCHAR}) || '%' OR UPPER(MPD.PARTNER_NM) LIKE '%' || UPPER(#{destinationCdNm, jdbcType=VARCHAR}) || '%')
) A WHERE ROW_CNT BETWEEN #{startPage, jdbcType=INTEGER} AND #{endPage, jdbcType=INTEGER}
