mapper.select:
SELECT A.* FROM (
SELECT MDM.COMPANY_CD , MDM.DIVISION_CD , MDM.DR_NO , MDM.DR_NM , MDM.PROCESS_CD , (SELECT MOM_COMMON_PKG.FN_GET_PROCESS_NAME(MDM.COMPANY_CD, MDM.DIVISION_CD, MDM.PROCESS_CD) FROM DUAL) AS PROCESS_NM , MDM.REQUEST_LOCATION_CD , ML.LOCATION_NM AS REQUEST_LOCATION_NM , MDM.CUSTOMER_CD , TO_CHAR(MDM.SHIPPING_DUE_DATE ,'YYYY-MM-DD') AS SHIPPING_DUE_DATE , MDM.STATE , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MDM.COMPANY_CD, MDM.DIVISION_CD, 'WM0001', MDM.STATE, #{langCd, jdbcType=VARCHAR}) FROM DUAL) AS STATE_NM , MDM.LOCATION_USE_YN , MDM.DESCRIPTION , MDM.UPDATE_BY AS UPDATE_USER_NO , (SELECT MOM_COMMON_PKG.FN_GET_USER_NAME(MDM.COMPANY_CD, MDM.DIVISION_CD, MDM.UPDATE_BY) FROM DUAL ) AS UPDATE_USER_NM , TO_CHAR(MDM.UPDATE_DATE ,'YYYY-MM-DD HH24:MI:SS') AS UPDATE_DATE , TO_CHAR(MDM.CREATE_DATE ,'YYYY-MM-DD') AS CREATE_DATE , ROWIDTOCHAR(MDM.ROWID) AS KEY_ID , COUNT(*) OVER() AS TOTAL_COUNT , ROW_NUMBER() OVER(PARTITION BY MDM.COMPANY_CD,MDM.DIVISION_CD ORDER BY MDM.DR_NO) AS ROW_CNT FROM MOM_DR_MST MDM , MOM_LOCATION ML WHERE MDM.COMPANY_CD = ML.COMPANY_CD AND MDM.DIVISION_CD = ML.DIVISION_CD AND MDM.REQUEST_LOCATION_CD = ML.LOCATION_CD AND MDM.COMPANY_CD = #{companyCd, jdbcType=VARCHAR} AND MDM.DIVISION_CD = #{divisionCd, jdbcType=VARCHAR} AND MDM.STATE = 'DRG' AND MDM.PROCESS_CD = 'RR' AND MDM.CREATE_DATE BETWEEN TO_DATE(#{createDateSD, jdbcType=VARCHAR}, 'YYYY-MM-DD') AND TO_DATE(#{createDateED, jdbcType=VARCHAR}, 'YYYY-MM-DD') + 0.99999
AND (UPPER(MDM.DR_NO) LIKE '%' || UPPER(#{drNo, jdbcType=VARCHAR}) || '%')
AND (UPPER(MDM.REQUEST_LOCATION_CD) LIKE '%' || UPPER(#{requestLocationCdNm, jdbcType=VARCHAR}) || '%' OR UPPER(ML.LOCATION_NM) LIKE '%' || UPPER(#{requestLocationCdNm, jdbcType=VARCHAR}) || '%')
) A WHERE ROW_CNT BETWEEN #{startPage, jdbcType=INTEGER} AND #{endPage, jdbcType=INTEGER}

mapper.delete:
        DELETE FROM MOM_PROC_DR_MST


	          (       COMPANY_CD  = #{item.companyCd, jdbcType=VARCHAR}
	             AND  DIVISION_CD = #{item.divisionCd, jdbcType=VARCHAR}
	             AND  PROCESS_CD  = 'RR'
	             AND  CREATE_BY   = #{item.userId, jdbcType=VARCHAR}
	          )

insert.foreach:

INTO MOM_PROC_DR_MST( COMPANY_CD
                    , DIVISION_CD
                    , PROCESS_CD
                    , DR_NO
                    , DR_NM
                    , REQUEST_LOCATION_CD
                    , CUSTOMER_CD
                    , SHIPPING_DUE_DATE
                    , DESCRIPTION
                    , DEL_YN
                    , VAL_MSG
                    , MULTI_MSG
                    , CREATE_BY
                    , CREATE_DATE
                    )
VALUES              ( #{item.companyCd, jdbcType=VARCHAR}
                    , #{item.divisionCd, jdbcType=VARCHAR}
                    , 'RR'
                    , #{item.drNo, jdbcType=VARCHAR}
                    , #{item.drNm, jdbcType=VARCHAR}
                    , #{item.requestLocationCd, jdbcType=VARCHAR}
                    , #{item.customerCd, jdbcType=VARCHAR}
                    , #{item.shippingDueDate, jdbcType=VARCHAR}
                    , #{item.description, jdbcType=VARCHAR}
                    , #{item.delYn, jdbcType=VARCHAR}
                    , #{item.valMsg, jdbcType=VARCHAR}
                    , #{item.multiMsg, jdbcType=VARCHAR}
                    , #{item.userId, jdbcType=VARCHAR}
                    , MOM_COMMON_PKG.FN_GET_LOCAL_TIME(#{item.companyCd, jdbcType=VARCHAR},#{item.divisionCd, jdbcType=VARCHAR})
                    )

mapper.select:

{ CALL MOM_STOCK_PKG.SP_UPSERT_DR_MST( #{p_err_code,  jdbcType=VARCHAR, mode=OUT}
                                     , #{p_err_msg,   jdbcType=VARCHAR, mode=OUT}
                                     , #{companyCd,   jdbcType=VARCHAR, mode=IN}
                                     , #{divisionCd,  jdbcType=VARCHAR, mode=IN} 
                                     , 'RR'
                                     , #{userId,      jdbcType=VARCHAR, mode=IN} )}

mapper.delete:
