mapper.select:
SELECT A.* FROM (
SELECT MDM.COMPANY_CD , MDM.DIVISION_CD , MDM.DR_NO , MDM.DR_NM , MDM.PROCESS_CD , (SELECT MOM_COMMON_PKG.FN_GET_PROCESS_NAME(MDM.COMPANY_CD, MDM.DIVISION_CD, MDM.PROCESS_CD) FROM DUAL) AS PROCESS_NM , MDM.ISSUE_WAREHOUSE_CD , MHI.WAREHOUSE_NM AS ISSUE_WAREHOUSE_NM , MDM.REQUEST_WAREHOUSE_CD , MHR.WAREHOUSE_NM AS REQUEST_WAREHOUSE_NM , MDM.CUSTOMER_CD , MP.PARTNER_NM AS CUSTOMER_NM , MDM.FORWARDER_CD , (SELECT MOM_COMMON_PKG.FN_GET_PARTNER_NAME(MDM.COMPANY_CD, MDM.DIVISION_CD, MDM.FORWARDER_CD) FROM DUAL) AS FORWARDER_NM , MDM.DERIVERY_TO_ADRESS , MDM.LOGISTICS_RESOURCE_CD , (SELECT MOM_COMMON_PKG.FN_GET_LOGISTICS_RESOURCE_NAME(MDM.COMPANY_CD, MDM.DIVISION_CD, MDM.LOGISTICS_RESOURCE_CD) FROM DUAL) AS LOGISTICS_RESOURCE_NM , MDM.PARTIAL_SHIPMENT_YN , MDM.SHIPPING_MARK , MDM.NAME_PLATE , MDM.INSPECTION_REPORT_YN , MDM.TRANSIT_INSURANCE_YN , TO_CHAR(MDM.SHIPPING_DUE_DATE ,'YYYY-MM-DD') AS SHIPPING_DUE_DATE , MDM.STATE , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MDM.COMPANY_CD, MDM.DIVISION_CD, 'WM0001', MDM.STATE, #{langCd, jdbcType=VARCHAR}) FROM DUAL) AS STATE_NM , MDM.DESCRIPTION , MDM.UPDATE_BY AS UPDATE_USER_NO , (SELECT MOM_COMMON_PKG.FN_GET_USER_NAME(MDM.COMPANY_CD, MDM.DIVISION_CD, MDM.UPDATE_BY) FROM DUAL ) AS UPDATE_USER_NM , TO_CHAR(MDM.UPDATE_DATE ,'YYYY-MM-DD HH24:MI:SS') AS UPDATE_DATE , TO_CHAR(MDM.CREATE_DATE ,'YYYY-MM-DD') AS CREATE_DATE , TO_CHAR(MDM.CONFIRM_DATE ,'YYYY-MM-DD') AS CONFIRM_DATE , MDM.CONFIRM_USER_NO , (SELECT MOM_COMMON_PKG.FN_GET_USER_NAME(MDM.COMPANY_CD, MDM.DIVISION_CD,MDM.CONFIRM_USER_NO) FROM DUAL ) AS CONFIRM_USER_NM , ROWIDTOCHAR(MDM.ROWID) AS KEY_ID , COUNT(*) OVER() AS TOTAL_COUNT , ROW_NUMBER() OVER(PARTITION BY MDM.COMPANY_CD,MDM.DIVISION_CD ORDER BY MDM.DR_NO) AS ROW_CNT FROM MOM_DR_MST MDM , MOM_WAREHOUSE MHI , MOM_WAREHOUSE MHR , MOM_PARTNER MP , MOM_PROCESS MS WHERE MDM.COMPANY_CD = MHI.COMPANY_CD(+) AND MDM.DIVISION_CD = MHI.DIVISION_CD(+) AND MDM.ISSUE_WAREHOUSE_CD = MHI.WAREHOUSE_CD(+) AND MDM.COMPANY_CD = MHR.COMPANY_CD(+) AND MDM.DIVISION_CD = MHR.DIVISION_CD(+) AND MDM.REQUEST_WAREHOUSE_CD = MHR.WAREHOUSE_CD(+) AND MDM.COMPANY_CD = MP.COMPANY_CD(+) AND MDM.DIVISION_CD = MP.DIVISION_CD(+) AND MDM.CUSTOMER_CD = MP.PARTNER_CD(+) AND MDM.COMPANY_CD = MS.COMPANY_CD(+) AND MDM.DIVISION_CD = MS.DIVISION_CD(+) AND MDM.PROCESS_CD = MS.PROCESS_CD(+) AND MDM.COMPANY_CD = #{companyCd, jdbcType=VARCHAR} AND MDM.DIVISION_CD = #{divisionCd, jdbcType=VARCHAR} AND MDM.STATE = 'DRF' AND MDM.CONFIRM_DATE BETWEEN TO_DATE(#{confirmDateSD, jdbcType=VARCHAR}, 'YYYY-MM-DD') AND TO_DATE(#{confirmDateED, jdbcType=VARCHAR}, 'YYYY-MM-DD') + 0.99999
AND (UPPER(MDM.DR_NO) LIKE '%' || UPPER(#{drNo, jdbcType=VARCHAR}) || '%')
AND (UPPER(MDM.CUSTOMER_CD) LIKE '%' || UPPER(#{customerCdNm, jdbcType=VARCHAR}) || '%' OR UPPER(MP.PARTNER_NM) LIKE '%' || UPPER(#{customerCdNm, jdbcType=VARCHAR}) || '%')
AND (UPPER(MDM.ISSUE_WAREHOUSE_CD) LIKE '%' || UPPER(#{issueWarehouseCdCdNm, jdbcType=VARCHAR}) || '%' OR UPPER(MHI.WAREHOUSE_NM) LIKE '%' || UPPER(#{issueWarehouseCdCdNm, jdbcType=VARCHAR}) || '%')
AND (UPPER(MDM.REQUEST_WAREHOUSE_CD) LIKE '%' || UPPER(#{requestWarehouseCdNm, jdbcType=VARCHAR}) || '%' OR UPPER(MHR.WAREHOUSE_NM) LIKE '%' || UPPER(#{requestWarehouseCdNm, jdbcType=VARCHAR}) || '%')
AND MDM.PROCESS_CD = #{processCd, jdbcType=VARCHAR}
AND EXISTS ( SELECT '*' FROM MOM_PROCESS_TYPE MPT WHERE MPT.COMPANY_CD = MS.COMPANY_CD AND MPT.DIVISION_CD = MS.DIVISION_CD AND MPT.PROCESS_TYPE = MS.PROCESS_TYPE AND MPT.ACTION_TYPE_CD = 'S' AND MPT.USE_YN = 'Y' ) AND NOT EXISTS ( SELECT '*' FROM MOM_DR_DTL MDD WHERE MDD.COMPANY_CD = MDM.COMPANY_CD AND MDD.DIVISION_CD = MDM.DIVISION_CD AND MDD.DR_NO = MDM.DR_NO AND MDD.STATE != 'DRF' )
) A WHERE ROW_CNT BETWEEN #{startPage, jdbcType=INTEGER} AND #{endPage, jdbcType=INTEGER}

mapper.delete:
        DELETE FROM MOM_PROC_DR_CONFIRM_CANCEL


	          (       COMPANY_CD  = #{item.companyCd, jdbcType=VARCHAR}
	             AND  DIVISION_CD = #{item.divisionCd, jdbcType=VARCHAR}
	             AND  CREATE_BY   = #{item.userId, jdbcType=VARCHAR}
	          )

insert.foreach:

        INTO MOM_PROC_DR_CONFIRM_CANCEL( COMPANY_CD
									   , DIVISION_CD
									   , DR_NO
									   , DESCRIPTION
									   , VAL_MSG
									   , MULTI_MSG
									   , CREATE_BY
									   , CREATE_DATE
		                               )
        VALUES                         ( #{item.companyCd, jdbcType=VARCHAR}
		                               , #{item.divisionCd, jdbcType=VARCHAR}
		                               , #{item.drNo, jdbcType=VARCHAR}
		                               , #{item.description, jdbcType=VARCHAR}
		                               , #{item.valMsg, jdbcType=VARCHAR}
		                               , #{item.multiMsg, jdbcType=VARCHAR}
		                               , #{item.userId, jdbcType=VARCHAR}
		                               , MOM_COMMON_PKG.FN_GET_LOCAL_TIME(#{item.companyCd, jdbcType=VARCHAR},#{item.divisionCd, jdbcType=VARCHAR})
		                               )

mapper.select:

        { CALL MOM_STOCK_PKG.MOM_PROC_DR_CONFIRM_CANCEL( #{p_err_code,  jdbcType=VARCHAR, mode=OUT}
			                                           , #{p_err_msg,   jdbcType=VARCHAR, mode=OUT}
			                                           , #{companyCd,   jdbcType=VARCHAR, mode=IN}
			                                           , #{divisionCd,  jdbcType=VARCHAR, mode=IN} 
			                                           , #{userId,      jdbcType=VARCHAR, mode=IN} )}
