mapper.select:
SELECT A.* FROM (
SELECT MDO.* , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MDO.COMPANY_CD, MDO.DIVISION_CD, 'SM0027', MDO.DO_TYPE, #{langCd, jdbcType=VARCHAR}) FROM DUAL) AS DO_TYPE_NM , MLR.LOCATION_NM AS REQUEST_LOCATION_NM , MLI.LOCATION_NM AS ISSUE_LOCATION_NM , MP.PARTNER_NM AS CUSTOMER_NM , MI.ITEM_NM , MI.ITEM_SPEC , MI.ITEM_TYPE , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MI.COMPANY_CD, MI.DIVISION_CD, 'MD0002', MI.ITEM_TYPE, #{langCd, jdbcType=VARCHAR}) FROM DUAL) AS ITEM_TYPE_NM , (SELECT MOM_COMMON_PKG.FN_GET_UNIT_NAME(MDO.COMPANY_CD, MDO.DIVISION_CD, MDO.UNIT ) FROM DUAL) AS UNIT_NM , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MDO.COMPANY_CD, MDO.DIVISION_CD, 'IC0004', MDO.MAIN_STATE, #{langCd, jdbcType=VARCHAR}) FROM DUAL) AS MAIN_STATE_NM , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MDO.COMPANY_CD, MDO.DIVISION_CD, 'IC0005', MDO.DTL_STATE, #{langCd, jdbcType=VARCHAR}) FROM DUAL) AS DTL_STATE_NM , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MDO.COMPANY_CD, MDO.DIVISION_CD, 'SM0017', MDO.CURRENCY_CD, #{langCd, jdbcType=VARCHAR}) FROM DUAL) AS CURRENCY_NM , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MDO.COMPANY_CD, MDO.DIVISION_CD, 'MM0012', MDO.INOUT_TYPE, #{langCd, jdbcType=VARCHAR}) FROM DUAL) AS INOUT_TYPE_NM , (SELECT MOM_COMMON_PKG.FN_GET_IO_TYPE_NAME(MDO.COMPANY_CD, MDO.DIVISION_CD, MDO.IO_TYPE) FROM DUAL) AS IO_TYPE_NM , MDO.DELIVERY_ORDER_ID AS KEY_ID , COUNT(*) OVER() AS TOTAL_COUNT , ROW_NUMBER() OVER(PARTITION BY MDO.COMPANY_CD,MDO.DIVISION_CD ORDER BY MDO.DELIVERY_ORDER_ID) AS ROW_CNT FROM MOM_DELIVERY_ORDER_V MDO , MOM_LOCATION MLR , MOM_LOCATION MLI , MOM_PARTNER MP , MOM_ITEM MI WHERE MDO.COMPANY_CD = MLR.COMPANY_CD(+) AND MDO.DIVISION_CD = MLR.DIVISION_CD(+) AND MDO.REQUEST_LOCATION_CD = MLR.LOCATION_CD(+) AND MDO.COMPANY_CD = MLI.COMPANY_CD AND MDO.DIVISION_CD = MLI.DIVISION_CD AND MDO.ISSUE_LOCATION_CD = MLI.LOCATION_CD AND MDO.COMPANY_CD = MP.COMPANY_CD AND MDO.DIVISION_CD = MP.DIVISION_CD AND MDO.CUSTOMER_CD = MP.PARTNER_CD AND MDO.COMPANY_CD = MI.COMPANY_CD AND MDO.DIVISION_CD = MI.DIVISION_CD AND MDO.ITEM_ID = MI.ITEM_ID AND MDO.COMPANY_CD = #{companyCd, jdbcType=VARCHAR} AND MDO.DIVISION_CD = #{divisionCd, jdbcType=VARCHAR} AND MDO.CREATE_DATE BETWEEN TO_DATE(#{createDateSD, jdbcType=VARCHAR}, 'YYYY-MM-DD') AND TO_DATE(#{createDateED, jdbcType=VARCHAR}, 'YYYY-MM-DD') + 0.99999 AND MDO.DTL_STATE = 'DOF' AND MDO.REMAIN_QTY > 0
AND MDO.DO_TYPE = #{doType, jdbcType=VARCHAR}
AND (UPPER(MDO.DO_NO) LIKE '%' || UPPER(#{doNo, jdbcType=VARCHAR}) || '%')
AND (UPPER(MP.PARTNER_NM) LIKE '%' || UPPER(#{customerCd, jdbcType=VARCHAR}) || '%')
AND (UPPER(MLI.LOCATION_NM) LIKE '%' || UPPER(#{issueLocationCd, jdbcType=VARCHAR}) || '%')
AND (UPPER(MLR.LOCATION_NM) LIKE '%' || UPPER(#{requestLocationCd, jdbcType=VARCHAR}) || '%')
) A WHERE ROW_CNT BETWEEN #{startPage, jdbcType=INTEGER} AND #{endPage, jdbcType=INTEGER}

mapper.delete:
DELETE FROM MOM_PROC_DO_CLOSE


    (       COMPANY_CD  = #{item.companyCd, jdbcType=VARCHAR}
       AND  DIVISION_CD = #{item.divisionCd, jdbcType=VARCHAR}
       AND  CREATE_BY   = #{item.userId, jdbcType=VARCHAR}
    )

insert.foreach:

INTO MOM_PROC_DO_CLOSE( COMPANY_CD
                      , DIVISION_CD
                      , DO_NO
                      , DELIVERY_ORDER_ID 
                      , DESCRIPTION
                      , VAL_MSG
                      , MULTI_MSG
                      , CREATE_BY
                      , CREATE_DATE
                      )
VALUES                ( #{item.companyCd, jdbcType=VARCHAR}
                      , #{item.divisionCd, jdbcType=VARCHAR}
                      , #{item.doNo, jdbcType=VARCHAR}
                      , #{item.deliveryOrderId, jdbcType=VARCHAR}
                      , #{item.description, jdbcType=VARCHAR}
                      , #{item.valMsg, jdbcType=VARCHAR}
                      , #{item.multiMsg, jdbcType=VARCHAR}
                      , #{item.userId, jdbcType=VARCHAR}
                      , MOM_COMMON_PKG.FN_GET_LOCAL_TIME(#{item.companyCd, jdbcType=VARCHAR},#{item.divisionCd, jdbcType=VARCHAR})
                      )

mapper.select:

{ CALL MOM_STOCK_PKG.SP_DO_CLOSE( #{p_err_code,  jdbcType=VARCHAR, mode=OUT}
                                , #{p_err_msg,   jdbcType=VARCHAR, mode=OUT}
                                , #{companyCd,   jdbcType=VARCHAR, mode=IN}
                                , #{divisionCd,  jdbcType=VARCHAR, mode=IN} 
                                , #{userId,      jdbcType=VARCHAR, mode=IN} )}
