mapper.select:
SELECT A.* FROM (
SELECT MDM.COMPANY_CD , MDM.DIVISION_CD , MDM.PROCESS_TYPE , (SELECT MOM_COMMON_PKG.FN_GET_PROCESS_TYPE_NAME(MDM.COMPANY_CD, MDM.DIVISION_CD, MDM.PROCESS_TYPE) FROM DUAL) AS PROCESS_TYPE_NM , MDM.DO_NO , MDM.DO_NM , MDM.ISSUE_WAREHOUSE_CD , MWH.WAREHOUSE_NM AS ISSUE_WAREHOUSE_NM , MDM.PARTNER_CD AS CUSTOMER_CD , MP.PARTNER_NM AS CUSTOMER_NM , TO_CHAR(MDM.ISSUE_DUE_DATE ,'YYYY-MM-DD') AS ISSUE_DUE_DATE , MDM.DESCRIPTION , MDM.UPDATE_BY AS UPDATE_USER_NO , (SELECT MOM_COMMON_PKG.FN_GET_USER_NAME(MDM.COMPANY_CD, MDM.DIVISION_CD, MDM.UPDATE_BY) FROM DUAL ) AS UPDATE_USER_NM , TO_CHAR(MDM.UPDATE_DATE ,'YYYY-MM-DD HH24:MI:SS') AS UPDATE_DATE , TO_CHAR(MDM.CREATE_DATE ,'YYYY-MM-DD') AS CREATE_DATE , ROWIDTOCHAR(MDM.ROWID) AS KEY_ID , COUNT(*) OVER() AS TOTAL_COUNT , ROW_NUMBER() OVER(PARTITION BY MDM.COMPANY_CD,MDM.DIVISION_CD ORDER BY MDM.DO_NO) AS ROW_CNT FROM MOM_DELIVERY_ORDER_MST MDM , MOM_WAREHOUSE MWH , MOM_PARTNER MP WHERE MDM.COMPANY_CD = MWH.COMPANY_CD AND MDM.DIVISION_CD = MWH.DIVISION_CD AND MDM.ISSUE_WAREHOUSE_CD = MWH.WAREHOUSE_CD AND MDM.COMPANY_CD = MP.COMPANY_CD AND MDM.DIVISION_CD = MP.DIVISION_CD AND MDM.PARTNER_CD = MP.PARTNER_CD AND MDM.COMPANY_CD = #{companyCd, jdbcType=VARCHAR} AND MDM.DIVISION_CD = #{divisionCd, jdbcType=VARCHAR} AND MDM.STATE = 'DOG' AND MDM.PROCESS_TYPE = 'OBS' AND MDM.CREATE_DATE BETWEEN TO_DATE(#{createDateSD, jdbcType=VARCHAR}, 'YYYY-MM-DD') AND TO_DATE(#{createDateED, jdbcType=VARCHAR}, 'YYYY-MM-DD') + 0.99999
AND (UPPER(MDM.DO_NO) LIKE '%' || UPPER(#{doNo, jdbcType=VARCHAR}) || '%')
AND (UPPER(MP.PARTNER_NM) LIKE '%' || UPPER(#{customerCd, jdbcType=VARCHAR}) || '%')
AND (UPPER(MWH.WAREHOUSE_NM) LIKE '%' || UPPER(#{issueWarehouseCd, jdbcType=VARCHAR}) || '%')
) A WHERE ROW_CNT BETWEEN #{startPage, jdbcType=INTEGER} AND #{endPage, jdbcType=INTEGER}

mapper.delete:
        DELETE FROM MOM_PROC_DO_MST


	          (       COMPANY_CD  = #{item.companyCd, jdbcType=VARCHAR}
	             AND  DIVISION_CD = #{item.divisionCd, jdbcType=VARCHAR}
	             AND  CREATE_BY   = #{item.userId, jdbcType=VARCHAR}
	             AND  PROCESS_TYPE = 'OBS'
	          )

insert.foreach:

INTO MOM_PROC_DO_MST( COMPANY_CD
                    , DIVISION_CD
                    , PROCESS_TYPE
                    , DO_NO
                    , DO_NM
                    , ISSUE_WAREHOUSE_CD
                    , REQUEST_WAREHOUSE_CD
                    , PARTNER_CD
                    , ISSUE_DUE_DATE
                    , DESCRIPTION
                    , DEL_YN
                    , VAL_MSG
                    , MULTI_MSG
                    , CREATE_BY
                    , CREATE_DATE
                    )
VALUES              ( #{item.companyCd, jdbcType=VARCHAR}
                    , #{item.divisionCd, jdbcType=VARCHAR}
                    , 'OBS'
                    , #{item.doNo, jdbcType=VARCHAR}
                    , #{item.doNm, jdbcType=VARCHAR}
                    , #{item.issueWarehouseCd, jdbcType=VARCHAR}
                    , #{item.requestWarehouseCd, jdbcType=VARCHAR}
                    , #{item.customerCd, jdbcType=VARCHAR}
                    , #{item.issueDueDate, jdbcType=VARCHAR}
                    , #{item.description, jdbcType=VARCHAR}
                    , #{item.delYn, jdbcType=VARCHAR}
                    , #{item.valMsg, jdbcType=VARCHAR}
                    , #{item.multiMsg, jdbcType=VARCHAR}
                    , #{item.userId, jdbcType=VARCHAR}
                    , MOM_COMMON_PKG.FN_GET_LOCAL_TIME(#{item.companyCd, jdbcType=VARCHAR},#{item.divisionCd, jdbcType=VARCHAR})
                    )

mapper.select:

{ CALL MOM_STOCK_PKG.SP_UPSERT_DO_MST( #{p_err_code,  jdbcType=VARCHAR, mode=OUT}
                                     , #{p_err_msg,   jdbcType=VARCHAR, mode=OUT}
                                     , #{companyCd,   jdbcType=VARCHAR, mode=IN}
                                     , #{divisionCd,  jdbcType=VARCHAR, mode=IN} 
                                     , 'OBS'
                                     , #{userId,      jdbcType=VARCHAR, mode=IN} )}

mapper.delete:
