mapper.select:
SELECT A.* FROM (
SELECT MDO.* , MHR.WAREHOUSE_NM AS REQUEST_WAREHOUSE_NM , MHI.WAREHOUSE_NM AS ISSUE_WAREHOUSE_NM , MP.PARTNER_NM AS CUSTOMER_NM , MI.ITEM_NM , MI.ITEM_SPEC , MI.ITEM_TYPE , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MI.COMPANY_CD, MI.DIVISION_CD, 'MD0002', MI.ITEM_TYPE, #{langCd, jdbcType=VARCHAR}) FROM DUAL) AS ITEM_TYPE_NM , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MDO.COMPANY_CD, MDO.DIVISION_CD, 'IC0004', MDO.MAIN_STATE, #{langCd, jdbcType=VARCHAR}) FROM DUAL) AS MAIN_STATE_NM , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MDO.COMPANY_CD, MDO.DIVISION_CD, 'IC0005', MDO.DTL_STATE, #{langCd, jdbcType=VARCHAR}) FROM DUAL) AS DTL_STATE_NM , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MDO.COMPANY_CD, MDO.DIVISION_CD, 'SM0017', MDO.CURRENCY_CD, #{langCd, jdbcType=VARCHAR}) FROM DUAL) AS CURRENCY_NM , MDO.DELIVERY_ORDER_ID AS KEY_ID , COUNT(*) OVER() AS TOTAL_COUNT , ROW_NUMBER() OVER(PARTITION BY MDO.COMPANY_CD,MDO.DIVISION_CD ORDER BY MDO.DELIVERY_ORDER_ID) AS ROW_CNT FROM MOM_DELIVERY_ORDER_V MDO , MOM_WAREHOUSE MHI , MOM_WAREHOUSE MHR , MOM_PARTNER MP , MOM_ITEM MI WHERE MDO.COMPANY_CD = MHI.COMPANY_CD(+) AND MDO.DIVISION_CD = MHI.DIVISION_CD(+) AND MDO.ISSUE_WAREHOUSE_CD = MHI.WAREHOUSE_CD(+) AND MDO.COMPANY_CD = MHR.COMPANY_CD(+) AND MDO.DIVISION_CD = MHR.DIVISION_CD(+) AND MDO.REQUEST_WAREHOUSE_CD = MHR.WAREHOUSE_CD(+) AND MDO.COMPANY_CD = MP.COMPANY_CD(+) AND MDO.DIVISION_CD = MP.DIVISION_CD(+) AND MDO.CUSTOMER_CD = MP.PARTNER_CD(+) AND MDO.COMPANY_CD = MI.COMPANY_CD AND MDO.DIVISION_CD = MI.DIVISION_CD AND MDO.ITEM_ID = MI.ITEM_ID AND MDO.COMPANY_CD = #{companyCd, jdbcType=VARCHAR} AND MDO.DIVISION_CD = #{divisionCd, jdbcType=VARCHAR} AND MDO.CREATE_DATE BETWEEN TO_DATE(#{createDateSD, jdbcType=VARCHAR}, 'YYYY-MM-DD') AND TO_DATE(#{createDateED, jdbcType=VARCHAR}, 'YYYY-MM-DD') + 0.99999 AND MDO.DTL_STATE = 'DOF' AND MDO.REMAIN_QTY > 0
AND MDO.PROCESS_TYPE = #{processType, jdbcType=VARCHAR}
AND (UPPER(MDO.DO_NO) LIKE '%' || UPPER(#{doNo, jdbcType=VARCHAR}) || '%')
AND (UPPER(MP.PARTNER_NM) LIKE '%' || UPPER(#{customerCd, jdbcType=VARCHAR}) || '%')
AND (UPPER(MDO.ISSUE_WAREHOUSE_CD) LIKE '%' || UPPER(#{issueWarehouseCdNm, jdbcType=VARCHAR}) || '%' OR UPPER(MHI.WAREHOUSE_NM) LIKE '%' || UPPER(#{issueWarehouseCdNm, jdbcType=VARCHAR}) || '%')
AND (UPPER(MDO.REQUEST_WAREHOUSE_CD) LIKE '%' || UPPER(#{requestWarehouseCdNm, jdbcType=VARCHAR}) || '%' OR UPPER(MHR.WAREHOUSE_NM) LIKE '%' || UPPER(#{requestWarehouseCdNm, jdbcType=VARCHAR}) || '%')
) A WHERE ROW_CNT BETWEEN #{startPage, jdbcType=INTEGER} AND #{endPage, jdbcType=INTEGER}

mapper.delete:
DELETE FROM MOM_PROC_DO_CLOSE


    (       COMPANY_CD  = #{item.companyCd, jdbcType=VARCHAR}
       AND  DIVISION_CD = #{item.divisionCd, jdbcType=VARCHAR}
       AND  CREATE_BY   = #{item.userId, jdbcType=VARCHAR}
    )

insert.foreach:

INTO MOM_PROC_DO_CLOSE( COMPANY_CD
                      , DIVISION_CD
                      , PROCESS_TYPE
                      , DO_NO
                      , DELIVERY_ORDER_ID 
                      , DESCRIPTION
                      , VAL_MSG
                      , MULTI_MSG
                      , CREATE_BY
                      , CREATE_DATE
                      )
VALUES                ( #{item.companyCd, jdbcType=VARCHAR}
                      , #{item.divisionCd, jdbcType=VARCHAR}
                      , #{item.processType, jdbcType=VARCHAR}
                      , #{item.doNo, jdbcType=VARCHAR}
                      , #{item.deliveryOrderId, jdbcType=VARCHAR}
                      , #{item.description, jdbcType=VARCHAR}
                      , #{item.valMsg, jdbcType=VARCHAR}
                      , #{item.multiMsg, jdbcType=VARCHAR}
                      , #{item.userId, jdbcType=VARCHAR}
                      , MOM_COMMON_PKG.FN_GET_LOCAL_TIME(#{item.companyCd, jdbcType=VARCHAR},#{item.divisionCd, jdbcType=VARCHAR})
                      )

mapper.select:

{ CALL MOM_STOCK_PKG.SP_DO_CLOSE( #{p_err_code,  jdbcType=VARCHAR, mode=OUT}
                                , #{p_err_msg,   jdbcType=VARCHAR, mode=OUT}
                                , #{companyCd,   jdbcType=VARCHAR, mode=IN}
                                , #{divisionCd,  jdbcType=VARCHAR, mode=IN} 
                                , #{userId,      jdbcType=VARCHAR, mode=IN} )}
