mapper.select:
SELECT A.* FROM (
SELECT MEM.COMPANY_CD , MEM.DIVISION_CD , MEM.EXEC_TASK_ID AS PICKING_ID , MEM.STATE , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MEM.COMPANY_CD, MEM.DIVISION_CD, 'WM0007', MEM.STATE, #{langCd, jdbcType=VARCHAR}) FROM DUAL) AS STATE_NM , MEM.LOCATION_CD AS PICKING_LOCATION_CD , ML.LOCATION_NM AS PICKING_LOCATION_NM , MEM.AREA_CD , (SELECT MOM_COMMON_PKG.FN_GET_AREA_NAME(MEM.COMPANY_CD, MEM.DIVISION_CD, MEM.AREA_CD) FROM DUAL) AS AREA_NM , MEM.LOGISTICS_UNIT_CD , MEM.RECEIPT_TYPE_CD , MRT.RECEIPT_TYPE_NM , MRT.RECEIPT_STEP_ID , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MRT.COMPANY_CD, MRT.DIVISION_CD, 'MD0038', MRT.RECEIPT_STEP_ID, #{langCd, jdbcType=VARCHAR}) FROM DUAL) AS RECEIPT_STEP_NM , MEM.DESCRIPTION , MEM.UPDATE_BY AS UPDATE_USER_NO , (SELECT MOM_COMMON_PKG.FN_GET_USER_NAME(MEM.COMPANY_CD, MEM.DIVISION_CD, MEM.UPDATE_BY) FROM DUAL ) AS UPDATE_USER_NM , TO_CHAR(MEM.UPDATE_DATE ,'YYYY-MM-DD HH24:MI:SS') AS UPDATE_DATE , ROWIDTOCHAR(MEM.ROWID) AS KEY_ID , MEM.PICKING_GROUP_UNIT , MEM.PICKING_GROUP_NO AS RECEIPT_ID , COUNT(*) OVER() AS TOTAL_COUNT , ROW_NUMBER() OVER(PARTITION BY MEM.COMPANY_CD,MEM.DIVISION_CD ORDER BY MEM.EXEC_TASK_ID) AS ROW_CNT FROM MOM_LOCATION_EXEC_TASK_MST MEM , MOM_LOCATION ML , MOM_LOCATION_RECEIPT_TYPE MRT WHERE MEM.COMPANY_CD = ML.COMPANY_CD AND MEM.DIVISION_CD = ML.DIVISION_CD AND MEM.LOCATION_CD = ML.LOCATION_CD AND MEM.COMPANY_CD = MRT.COMPANY_CD AND MEM.DIVISION_CD = MRT.DIVISION_CD AND MEM.RECEIPT_TYPE_CD = MRT.RECEIPT_TYPE_CD AND MEM.COMPANY_CD = #{companyCd, jdbcType=VARCHAR} AND MEM.DIVISION_CD = #{divisionCd, jdbcType=VARCHAR} AND MEM.CREATE_DATE BETWEEN TO_DATE(#{createDateSD, jdbcType=VARCHAR}, 'YYYY-MM-DD') AND TO_DATE(#{createDateED, jdbcType=VARCHAR}, 'YYYY-MM-DD') + 0.99999 AND MEM.STATE = 'LEG' AND MEM.EXEC_TASK_TYPE = 'P' AND MEM.PICKING_GROUP_UNIT = 'R'
AND (UPPER(MEM.EXEC_TASK_ID) LIKE '%' || UPPER(#{pickingId, jdbcType=VARCHAR}) || '%')
AND MEM.RECEIPT_TYPE_CD = #{receiptTypeCd, jdbcType=VARCHAR}
AND (UPPER(MEM.LOCATION_CD) LIKE '%' || UPPER(#{pickingLocationCdNm, jdbcType=VARCHAR}) || '%' OR UPPER(ML.LOCATION_NM) LIKE '%' || UPPER(#{pickingLocationCdNm, jdbcType=VARCHAR}) || '%')
AND (UPPER(MEM.PICKING_GROUP_NO) LIKE '%' || UPPER(#{receiptId, jdbcType=VARCHAR}) || '%')
) A WHERE ROW_CNT BETWEEN #{startPage, jdbcType=INTEGER} AND #{endPage, jdbcType=INTEGER}

mapper.delete:
DELETE FROM MOM_PROC_LOCATION_EXEC_MST


    (       COMPANY_CD         = #{item.companyCd, jdbcType=VARCHAR}
       AND  DIVISION_CD        = #{item.divisionCd, jdbcType=VARCHAR}
       AND  EXEC_TASK_TYPE     = 'P'
       AND  PICKING_GROUP_UNIT = 'R'
       AND  CREATE_BY          = #{item.userId, jdbcType=VARCHAR}
    )

insert.foreach:

INTO MOM_PROC_LOCATION_EXEC_MST( COMPANY_CD
                               , DIVISION_CD
                               , EXEC_TASK_TYPE   
                               , EXEC_TASK_ID
                               , LOCATION_CD
                               , AREA_CD
                               , LOGISTICS_UNIT_CD
                               , RECEIPT_TYPE_CD
                               , PICKING_GROUP_UNIT
                               , PICKING_GROUP_NO
                               , DESCRIPTION
                               , DEL_YN
                               , VAL_MSG
                               , MULTI_MSG
                               , CREATE_BY
                               , CREATE_DATE
                               )
VALUES                         ( #{item.companyCd, jdbcType=VARCHAR}
                               , #{item.divisionCd, jdbcType=VARCHAR}
                               , 'P'
                               , #{item.pickingId, jdbcType=VARCHAR}
                               , #{item.pickingLocationCd, jdbcType=VARCHAR}
                               , #{item.areaCd, jdbcType=VARCHAR}
                               , #{item.logisticsUnitCd, jdbcType=VARCHAR}
                               , #{item.receiptTypeCd, jdbcType=VARCHAR}
                               , 'R'
                               , #{item.receiptId, jdbcType=VARCHAR}
                               , #{item.description, jdbcType=VARCHAR}
                               , #{item.delYn, jdbcType=VARCHAR}
                               , #{item.valMsg, jdbcType=VARCHAR}
                               , #{item.multiMsg, jdbcType=VARCHAR}
                               , #{item.userId, jdbcType=VARCHAR}
                               , MOM_COMMON_PKG.FN_GET_LOCAL_TIME(#{item.companyCd, jdbcType=VARCHAR},#{item.divisionCd, jdbcType=VARCHAR})
                               )

mapper.select:

{ CALL MOM_WMS_PKG.SP_UPSERT_LOCATION_EXEC_TASK_MST( #{p_err_code,  jdbcType=VARCHAR, mode=OUT}
                                                   , #{p_err_msg,   jdbcType=VARCHAR, mode=OUT}
                                                   , #{companyCd,   jdbcType=VARCHAR, mode=IN}
                                                   , #{divisionCd,  jdbcType=VARCHAR, mode=IN} 
                                                   , 'P'
                                                   , 'R'  
                                                   , #{userId,      jdbcType=VARCHAR, mode=IN} )}

